#define SS 3.0  //super sampling

#define iR iResolution

const float PI = 355./113.;

vec2 rotate(vec2 p, float a) {return vec2 (cos(a)*p.x-sin(a)*p.y,sin(a)*p.x+cos(a)*p.y);}

vec4 obj2(vec2 uv)
{
    float time = pow(3.0,iTime);
    uv*=1.5*(iR.x/iR.y);                                      //scale to screen
    uv.xy = rotate(uv.xy,.05*sin(iTime+atan(uv.x,uv.y)*4.));  //wavy motion 
    float s = sign(uv.x);
    uv.x=abs(uv.x);                                           //mirror x-axis

    float a = length(uv)+.5;                        //pixel distance from center
    if(a>2.0)return vec4(0,0,0,0);                  //mask values beyond infiniti
    float my = iMouse.y/iR.y;                       //mouse control y-axis
    my=(iMouse.z>0.0)?1.0-pow(1.0-(my*.3),10.0):.6; 
    float rad = log(2.0-a)/log(my)+1.0;             //number of rings grows logarithmically
    float am = floor(rad);                          //quantize radius into discrete rings

    float am2 = pow(2.0,am);                  //number of divisions grows exponentially as rings get larger
    float mx = iMouse.x/iR.x;                 //mouse control x-axis
    am2*=(iMouse.z>0.0)?pow(mx,10.0)*10.0:.7;                   
    float circ = atan(uv.x,uv.y)*am2;         //pixel angle
    float b0 = round(circ);                   //quantize circumference into discrete slices

    float u = circ-b0;
    float v = 1.0-fract(rad);
    float g = 1.0-2.0*mod(floor(b0),2.0); //two groups (1 and -1)
    return vec4(u*s+.5,v,1.0,g);
}

vec4 get_color(vec2 p)
{
    vec4 F;
    vec4 obj = obj2(p);
    vec2 uv = obj.xy;
    uv.y=-uv.y;
    F=texture(iChannel0,fract(uv));

    F=pow(F,vec4(2.2));
    F*=obj.z;
    if(obj.w>0.0)F=vec4(.03,0,0,1.0);
    return F;
}

void mainImage( out vec4 F, in vec2 C )
{  
    F=vec4(0);
    vec2 uv = (C-vec2(iR.x*.5,0))/iR.xy; //bottom center of screen = (0,0)
    uv.y*=iR.y/iR.x;                     //square pixels

    for(float y = 0.0; y < SS; y++) //super sampling
    {
        for(float x = 0.0; x < SS; x++)
        {
            F+=get_color(uv+vec2((x/SS)/iR.x,(y/SS)/iR.x));
        }
    }

    F/=SS*SS;
    F=pow(F,vec4(1./2.2));


}

#define MAXSTEPS 100
#define MAXDISTANCE 32.0
#define HITDISTANCE 0.005
#define SURFACEOFFSET 0.1
#define GRIDWIDTH 8
#define GRIDDEPTH 8

vec3 boxes[GRIDWIDTH * GRIDDEPTH];


////////////////
// From IÃ±igo Quilez's Value Noise shader -> https://www.shadertoy.com/view/lsf3WH
float hash(vec2 p)  
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

////////////////

void UpdateBoxGrid(vec4 timeParams)
{
    vec2 noiseAspectRatio = vec2(iResolution.x/iResolution.y,1.0);

    int halfwidth = GRIDWIDTH / 2;
    for (int x = 0; x < GRIDWIDTH; ++x)
    {
        for (int y = 0; y < GRIDDEPTH; ++y)
        {
            //Using noise texture 

            //vec2 noiseUV1 = vec2(y,x) * timeParams.z;
            //vec2 noiseUV2 = vec2(x,y) * timeParams.w;
            //vec4 noiseTex1 = texture(iChannel3, noiseUV1);
            //vec4 noiseTex2 = texture(iChannel3, noiseUV2);
            //float noiseA = noiseTex1.x * noiseTex1.x * 0.4;
            //float noiseB = noiseTex2.x * noiseTex1.x * 0.4;
            //vec3 box = vec3((x - halfwidth) * 2, (((noiseA + noiseB)) * float(y)), y * 2);

            //Using IQ's runtime noise

            vec2 noiseUV = vec2(x,y) * noiseAspectRatio;
            vec3 box = vec3((x - halfwidth) * 2, 1.0 - (timeParams.x * noise(noiseUV * vec2(4.0)) + timeParams.y * noise(noiseUV * vec2(2.0))) * 0.2 * float(y), y * 2);

            boxes[(x * GRIDWIDTH) + y] = box;
        }
    }     
}

float GetDistanceToBox(vec3 point, vec3 center, float side)
{
    vec3 pointTransformed = point - center;
    float distance = length(max(abs(pointTransformed) - side, .0));

    return distance;
}

float GetDistanceToSphere(vec3 point, vec3 center, float radius)
{
    float distance = length(point - center.xyz) - radius;
    return distance;
}

float GetDistance(vec3 point)
{
    vec4 sphere = vec4(0.0, 2.4, 2.0, 0.3);

    float sphereDistance = GetDistanceToSphere(point, sphere.xyz, sphere.w);

    float planeDistance = point.y; //Plane is axis-aligned with y = 0
    float distance = min(planeDistance, sphereDistance);


    //Animated Box grid
    for (int x = 0; x < GRIDWIDTH; ++x)
    {
        for (int z = 0; z < GRIDDEPTH; ++z)
        {
            float boxDistance = GetDistanceToBox(point, boxes[(x * GRIDWIDTH) + z], 0.95); 

            distance = min(boxDistance, distance);
        }
    }    

    return distance;
}

float RayMarch(vec3 rOrigin, vec3 rDirection)
{
    float distanceFromOrigin = 0.0;

    for(int i = 0; i < MAXSTEPS; ++i)
    {
        vec3 p = rOrigin + rDirection * distanceFromOrigin;
        float distanceToScene = GetDistance(p);
        distanceFromOrigin += distanceToScene;

        if (distanceFromOrigin > MAXDISTANCE || distanceToScene < HITDISTANCE) break;
    }

    return distanceFromOrigin;
}

vec3 GetNormal(vec3 point)
{
    float distance = GetDistance(point);
    vec2 offset = vec2(0.01, 0.0);

    vec3 normal = distance - vec3(GetDistance(point - offset.xyy), GetDistance(point - offset.yxy),  GetDistance(point - offset.yyx));

    return  normalize(normal);
}

float GetLight(vec3 point)
{
    vec3 lightPosition = vec3(5.0 * sin(iTime), 5.0, 4.0 + cos(iTime) * 6.0);
    vec3 lightDirection = normalize(lightPosition - point);
    vec3 surfaceNormal = GetNormal(point);

    float diffuse = clamp(dot(surfaceNormal, lightDirection), 0.0, 1.0);

    float shadow = RayMarch(point + (surfaceNormal * SURFACEOFFSET), lightDirection);

    diffuse -= step(shadow, length(lightPosition - point)) * 0.9;

    return diffuse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 rOrigin = vec3(0.0, 3.0, 0.0);
    vec3 rDirection = normalize(vec3(uv.x, uv.y - 0.5, 1.0));

    //Time Params    
    float timeSin = sin(iTime);
    float timeCos = cos(iTime);
    float timeSinUV = sin(iTime * 0.15) * 0.003;
    float timeCosUV = cos(iTime * 0.15) * 0.002;
    vec4 timeParams = vec4(timeSin, timeCos, timeSinUV, timeCosUV);

    //Animate Box Grid
    UpdateBoxGrid(timeParams);

    //Raymarching
    float d = RayMarch(rOrigin, rDirection);

    vec3 point = rOrigin + rDirection * d;

    float diffuseLight = clamp(GetLight(point), 0.1, 0.9);

    vec3 col = vec3(diffuseLight, 0.0, 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float GetRandom(float extent, vec2 vertex)
{
    float a =  iTime + vertex.x * 1.2 + vertex.y * 17.21;
    float multiplier = fract(a);
    float b = (a * 3.0 + 1.0) * multiplier + (1.0 - multiplier) * a * 0.5f;
    multiplier = fract(b);
    a = (b * 3.0 + 1.0) * multiplier + (1.0 - multiplier) * b * 0.5f;
    return mod(a, extent);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float random = GetRandom(256.0, fragCoord) * 0.00390625/*(1/256)*/;
    fragColor = vec4(random,random,random,1.0);
}

