void mainImage( out vec4 c, in vec2 u )
{
    u /= iResolution.xy;
    u = abs(u * 2. - 1.);
    u.x *= max(iResolution.x,iResolution.y)/min(iResolution.x,iResolution.y);
    u *= cos(iTime*.33)*6.+7.;
    vec2 g = floor(u);
    u = fract(u);
    u.x = fract(g.x*.5)==0.?u.x:1.-u.x;
    u.y = fract(g.y*.5)==0.?u.y:1.-u.y;
    u *= 1.13;

    //rounded corner radius:
    float r = sin(iTime)*.5+.5;

    float oor  = 1./r;
    float omr  = 1.-r;
    float cir  = length(u*oor-oor+1.);
    float rec1 = max(u.x/omr,u.y);
    float rec2 = max(u.y/omr,u.x);
    float rec  = min(rec1,rec2);
    float less = min(cir,rec);
    float rs   = less > 1.? 0.:1.;

    c = vec4(1.-rs,rec,cir,1.);
}

#define ITR 64
