mat2 rot(float angle){
	float s=sin(angle);
	float c=cos(angle);
	return mat2(s,c,-c,s);
}

float hash(vec2 p){
	return fract(sin(dot(p,vec2(43.232,75.876)))*4526.3257);
}

float sdSphere(vec3 p){
	return length(p)-clamp(sin(iTime),0.2,1.0);
}

float sdTorus(vec3 p){
	vec2 t=vec2(1.0,0.5);
	vec2 q=vec2(length(p.xz)-t.x,p.y);
	return length(q)-t.y;
}

float sdPlane(vec3 p){
	return p.y;
}

float displacement(vec3 p){
	p*=hash(vec2(p.x,p.y))*5.0;
	return sin(p.x)*sin(p.y)*sin(p.z);
}

float mainDist(vec3 p){
	p.xy*=rot(iTime*0.5);
	p.yz*=rot(iTime*0.5);
	float dis=displacement(p);
	float d1=sdSphere(p);
	//float d2=sdPlane(p);
	float d3=sdTorus(p);
	//return d2;
	return d1+dis;
}

