//@iq
float octa( vec3 p, float s) {
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

mat2 r45,r22;
vec3 hit,hitPoint;
vec2 gid,sid;
float time,srnd,grnd;
vec2 sc = vec2(.0),ssc,gsc;

vec2 map(vec3 p) {
    p.y+=5.;
    p.x+=time;
    vec2 res = vec2(1e5,0);

    sc = vec2(.25);
    vec2 bsize = vec2(1.);

    vec2 id = floor(p.xz*sc) + .5;    
    vec2 r = p.xz - id/(sc);
    vec3 q = vec3(r.x,p.y,r.y);

    float rnd = hash21(id);
    float tn=-1.;
    if(rnd>.5){
       sc*=2.;
       id = floor(q.xz*(sc)) + .5;    
       r = q.xz - id/(sc);
       q = vec3(r.x,p.y,r.y);
       rnd = hash21(id+rnd);
       tn=0.;
       if(rnd>.5){
           sc*=2.;
           id = floor(q.xz*(sc)) + .5;    
           r = q.xz - id/(sc);
           q = vec3(r.x,p.y,r.y);
           rnd = hash21(id+rnd);
           if(rnd>.5){
               sc*=2.;
               id = floor(q.xz*(sc)) + .5;    
               r = q.xz - id/(sc);
               q = vec3(r.x,p.y,r.y);
               rnd = hash21(id+rnd);
               if(rnd>.5){
                   sc*=2.;
                   id = floor(q.xz*(sc)) + .5;    
                   r = q.xz - id/(sc);
                   q = vec3(r.x,p.y,r.y);
                   rnd = hash21(id+rnd);
                   tn=1.;
               }
           }
       }
    }
    vec3 qs=q;
    q.xz*=rot(rnd*T*1.5);

    bsize = .375/sc.xx;
    float b1 = octa(q-vec3(0,bsize.y,0),bsize.x);

    if(tn==0.){
        q.xz*=r45;
        b1 = min(octa(q-vec3(0,bsize.y,0),bsize.x),b1);
    }
    if(tn==-1.){
        q.xz*=r22;
        b1 = min(octa(q-vec3(0,bsize.y,0),bsize.x),b1);
        q.xz*=r22;
        b1 = min(octa(q-vec3(0,bsize.y,0),bsize.x),b1);
    }

    if(b1<res.x) {
        res = vec2(b1,2.);
    	hit=q;
        gid=id;
        grnd=rnd;
        gsc=sc;
    }

    float d9 = p.y;
    if(d9<res.x) {
        res = vec2(d9,1.);
    	hit=qs;
        gid=id;
        grnd=rnd;
    }
    return res;
}

vec3 normal(vec3 p, float t, float mindist)
{
    float e = mindist*t;
    vec2 h = vec2(1.0,-1.0);
    return normalize( h.xyy*map( p + h.xyy*e).x + 
					  h.yyx*map( p + h.yyx*e).x + 
					  h.yxy*map( p + h.yxy*e).x + 
					  h.xxx*map( p + h.xxx*e).x );
}

vec3 hue(float t)
{ 
    vec3 d = vec3(0.220,0.961,0.875);
    return .375 + .375*cos(PI2*t*(vec3(.985,.98,.95)+d)); 
}

vec3 render(vec3 p, vec3 rd, vec3 ro, float d, float m, inout vec3 n, inout float fresnel)
{
    n = normal(p,d,1.01);
    vec3 lpos =  vec3(5,4,1);
    vec3 l = normalize(lpos-p);

    float diff = clamp(dot(n,l),0.,1.);

    fresnel = pow(clamp(1.+dot(rd, n), 0., 1.), 3.);
    fresnel = mix(.01, .7, fresnel);

    float shdw = 1.;
    float t=.0;
    for(float i=.0;i<24.;i++)
    {
        float h = map(p + l*t).x;
        if( h<1e-4 ) { shdw = 0.; break; }
        shdw = min(shdw, 18.*h/t);
        t += h;
        if( shdw<.01 || t>64. ) break;
    }
    diff = mix(diff,diff*shdw,.4);

    vec3 h = vec3(0.914,0.984,0.973);
    if(m==1.) {
        float cir = length(hitPoint.xz)-(.15/ssc.x);
        float of =(.045/ssc.x);
        float ox =(.015/ssc.x);
        cir=abs(abs(abs(cir)-of)-of)-ox;
        cir = smoothstep(.001,.0011,cir);
        h=mix(vec3(0.561,0.639,0.627),h,1.-cir);
    }

    if(m==2.) {
        h=hue(((srnd+fresnel)+hitPoint.z*.05)*3.);
    }
    return diff*h;//mix(h,h*shdw,.6);
}

void mainImage( out vec4 O, in vec2 F )
{
    // precal
    time = T*.35;
    r45=rot(45.*PI/180.);
    r22=rot(30.*PI/180.);
    //

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);

    float zoom = 3.;
    //orthographic camera
    vec3 ro = vec3(uv*zoom,-zoom);
    vec3 rd = vec3(0,0,1.);

    mat2 rx = rot(-0.58539816339);
    mat2 ry = rot(-0.78539816339);
    ro.yz *= rx;
    rd.yz *= rx;
    ro.xz *= ry;
    rd.xz *= ry;

    vec3 C = vec3(0);
    vec3  p = ro + rd;
    float atten = 1.;
    float k = 1.;
    float d = 0.;
    for(int i=0;i<125;i++)
    {
        vec2 ray = map(p);
        vec3 n=vec3(0);
        float m = ray.y;

        d = ray.x*.5;
        p += rd * d *k;

        if (d*d < 1e-6) {
            hitPoint = hit;
            sid = gid;
            ssc = gsc;
            srnd=grnd;
            float fresnel=0.;
            C+=render(p,rd,ro,d,ray.y,n,fresnel)*atten;
            if(m==1.)break;

            atten *= .65;
            p += rd*.025;
            k = sign(map(p).x);

            vec3 rr = vec3(0);
            if(int(F.x)%3 != int(F.y)%3) {
                rr = refract(rd,n,.45);
                rd=mix(rr,rd,.9-fresnel);
            }else{
                vec3 rr=reflect(-rd,n);
                rd=mix(rr,rd, vec3(.8)-fresnel);
                p+=n*.05;
            }

        } 

        if(distance(p,rd)>75.) { break; }
    }

    C = pow(C, vec3(.4545));
    O = vec4(C,1.0);
}

//Constants
#define TAU 6.2831853071795865

//Parameters
#define TUNNEL_LAYERS 32
#define RING_POINTS 48
#define POINT_SIZE 12.0
#define POINT_COLOR_A vec3(1.0)
#define POINT_COLOR_B vec3(0.2)
#define SPEED 0.1

//Square of x
float sq(float x)
{
	return x*x;   
}

//Angular repeat
vec2 AngRep(vec2 uv, float angle)
{
    vec2 polar = vec2(atan(uv.y, uv.x), length(uv));
    polar.x = mod(polar.x + angle / 2.0, angle) - angle / 2.0; 

    return polar.y * vec2(cos(polar.x), sin(polar.x));
}

//Signed distance to circle
float sdCircle(vec2 uv, float r)
{
    return length(uv) - r;
}

//Mix a shape defined by a distance field 'sd' with a 'target' color using the 'fill' color.
vec3 MixShape(float sd, vec3 fill, vec3 target)
{
    float blend = smoothstep(0.0,1.0/iResolution.y, sd);
    return mix(fill, target, blend);
}

//Tunnel/Camera path
vec2 TunnelPath(float x)
{
    vec2 offs = vec2(0, 0);

    offs.x = 0.2 * sin(TAU * x * 0.5) + 0.4 * sin(TAU * x * 0.2 + 0.3);
    offs.y = 0.3 * cos(TAU * x * 0.3) + 0.2 * cos(TAU * x * 0.1);

    offs *= smoothstep(1.0,4.0, x);

    return offs;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y;

    uv -= res/2.0;

    vec3 color = vec3(0);

    float repAngle = TAU / float(RING_POINTS);
    float pointSize = POINT_SIZE/2.0/iResolution.y;

    float camZ = iTime * SPEED;
    vec2 camOffs = TunnelPath(camZ);

    for(int i = 1;i <= TUNNEL_LAYERS;i++)
    {
        float pz = 1.0 - (float(i) / float(TUNNEL_LAYERS));

        //Scroll the points towards the screen
        pz -= mod(camZ, 4.0 / float(TUNNEL_LAYERS));

        //Layer x/y offset
        vec2 offs = TunnelPath(camZ + pz) - camOffs;

        //Radius of the current ring
        float ringRad = 0.1 * (1.0 / sq(pz * 0.7 + 0.3));

        //Only draw points when uv is close to the ring.
        float d = abs(length(uv + offs) - ringRad);

        //Angular repeated uv coords
        vec2 aruv = AngRep(uv + offs, repAngle);

        //Distance to the nearest point
        float pdist = sdCircle(aruv - vec2(ringRad, 0), pointSize);

        //Distance fade
        float shade = 1.0 - pz;

        //0 or 1
        if (mod(float(i+1 / 2), 2.0) == 0.0) 
        {
            if(d > pointSize * 1.7 || d < pointSize * 0.3) // 1
            {

                color = MixShape(pdist + (pz * 0.04), POINT_COLOR_A * shade, color);
            } 
        } else {
            if (pdist > pointSize - 0.036)  // 0
            {

                color = MixShape(pdist + (pz * 0.015), POINT_COLOR_A * shade, color);
            }

        }

    }

	fragColor = vec4(color, 1.0);
}

struct Ray {
    vec3 pos;
    vec3 dir;
};

struct Hit {
    vec3 normal;
    float t;
};

float focal = 1.0;
vec3 origin = vec3(0, 0.0, 2);


Ray cammapray(vec2 uv) {
    vec2 siz = vec2((iResolution.x/iResolution.y)*2.0, 2.0);

    vec3 blc = origin-vec3(siz/2., focal);
    return Ray(
        origin,
        vec3(blc.xy+uv*siz, blc.z)-origin
    );
}

vec3 camraycolor(Ray ray) {
    vec3 ndir = normalize(ray.dir);
    float t = 0.5 * (ndir.y + 1.0);
    return t*vec3(0.5, 0.7, 1.0) + (1.0-t)*vec3(1.0, 1.0, 1.0);
}

Hit rayvssphere(Ray ray, vec3 center, float radius) {
    vec3 asubc = ray.pos-center;

    float a = dot(ray.dir, ray.dir),
          b = 2.0 * dot(asubc, ray.dir),
          c = dot(asubc, asubc)-(radius*radius);

    float disc = b*b-4.0*a*c;
    float t = disc < 0.0 ? -1.0 : (-b - sqrt(disc))/(2.0*a);
    return Hit((ray.dir*t+ray.pos-center)/radius, t);
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 scatter(vec2 seed) {
    vec3 v = vec3(rand(vec2(seed.x, 0)), rand(vec2(0, seed.y)), rand(seed));
    v *= 2.0;
    v -= vec3(1, 1, 1);
    return normalize(v);
}

vec3 scene(Ray r) {
    Hit hit;
    float mul = 1.0;
    for (int hits = 0; hits < 3; hits += 1) {
        Hit hit_list[3] = Hit[3](
            rayvssphere(r, vec3(cos(iTime), 0, sin(iTime)), 0.5),
            rayvssphere(r, vec3(-cos(iTime), 0, -sin(iTime)), 0.5),
            rayvssphere(r, vec3(0, -100.5, -1.0), 100.0)
        );
        bool hit_anything = false;

        for (int i = 0; i < 3; i += 1) {
            if (hit_list[i].t > 0.0) {
                if (hit_anything && hit_list[i].t > hit.t) continue;
                hit_anything = true;
                hit = hit_list[i];
            }
        }

        if (hit_anything) {
            vec3 at = hit.t*r.dir+r.pos;
            r = Ray(at, scatter(r.dir.xy)+hit.normal);
            mul *= 0.5;
            continue;
        }
        return camraycolor(r)*mul;
    }
    return camraycolor(r)*mul;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    origin.y += sin(iTime/2.0)/2.0+0.3;
    vec3 col = vec3(0, 0, 0);
    for (int i = 0; i < 30; i += 1) {
        Ray r = cammapray(uv+vec2(rand(uv+vec2(float(i),uv.x)), rand(uv+vec2(uv.y,float(i))))/iResolution.xy);
        col += scene(r);
    }
    fragColor = vec4(col/30.0,1.0);
}

#define PI 3.1415926535897
#define DD 0.001

vec4 circle(vec2 p, float radius, vec3 color) {
    return vec4(length(p) - radius, color);
}

vec4 pentagon( in vec2 p, in float r, vec3 color )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528);
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return vec4(length(p)*sign(p.y), color);
}

vec4 subtract(vec4 d1, vec4 d2) {
    return d1.x > -d2.x ? d1 : vec4(-d2.x, d2.yzw);
}

vec4 smooth_union(float k, vec4 a, vec4 b)
{
    float diff = abs(a.x - b.x);
    float h = max(k - diff, 0.0) / k;
    if (a.x < b.x) {
        return vec4(a.x - h*h*h*k/6.0, b.yzw * (h/2.0) + a.yzw * (1.0-h/2.0));
    } else {
        return vec4(b.x - h*h*h*k/6.0, b.yzw * (1.0-h/2.0) + a.yzw * (h/2.0));
    }
}

vec4 map(vec2 p) {
    vec4 sdf = vec4(1e5, 0, 0, 0);

    for (float th = 0.0; th < 6.28; th += 2. * PI / 3.) {
        float r = 0.5;
        float rc =  0.5 + 0.07 * cos(3.0 * th + 3.0 * iTime);
        sdf = smooth_union(clamp(0.5 + 1.0 * pow(sin(iTime), 3.0), 0.001, 1.0), sdf, 
            pentagon(
                p - r * vec2(cos(th), sin(th)),
                rc,
                vec3(
                    0.4 + 0.3 * pow(sin(th + 2.), 2.),
                    0.2 + 0.2 * sin(iTime + 5.0),
                    0.2 + 0.7 * pow(sin(iTime), 2.)
                )
            )
        );
    }

    sdf = smooth_union(0.1, sdf, 
        circle(p + vec2(cos(iTime), sin(iTime)), 0.3, vec3(0.2, 0.1, 0.3)));

    return sdf;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5) * 4.0;

    float p = max(0.0, (52.0 - pow(max(0.0, mod(iTime, 10.0) + 0.0), 2.0)) / 5.778);
    float a = floor(p);
    float bounce = a * (1.0 - 4.0 * pow(p - a - 0.5, 2.0));
    bounce *= 0.1;
    bounce += 0.005;

    vec4 sdf = map(uv - vec2(0.0, bounce));

    vec3 color;

    if (sdf.x > 0.0) {
        vec4 shadow = map(uv);
        color = clamp(0.1 + shadow.x / (0.1 + bounce * 1.5), 0.02 + 0.10 * bounce, 0.25) 
            * vec3(0.32, 0.35, 0.41) 
            * (3.0 / (3.0 + dot(uv,uv)))
            * (3.0 + pow(sin(iTime + uv.x * 3.0 + sin(iTime + uv.y * 2.3 + uv.x * -2.7)), 5.0));
    } else if (sdf.x > -0.02) {
        color = vec3(0);  
    } else {
        color = sdf.yzw;
    }

    fragColor = vec4(pow(color, vec3(0.4545)), 1.0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


//increase this constant to increase the amount of detail
#define OCTAVES 2

float fbm(in vec2 uv)
{
    //this function generates the terrain height
    float value = 0.;
    float factor = -3.;
    uv *= factor;
    for (int i = 0; i < OCTAVES; i++)
    {
        uv += max(sin(uv*factor)/factor,cos(uv/factor)*factor).yx;
        value = -min(value+sin(uv.x)/factor,value+cos((uv.y)/factor)/factor);

        uv = uv/1.5/factor;
        //factor /= 1.5;

    }

    return value/2.+1.5;
}


struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 2.;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    #endif
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 1.;

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int xor = (int(fragCoord.x) ^ int(fragCoord.y));
    float r = float((xor * 2) & 0xff) / 255.0;
    float g = float((xor * 4) & 0xff) / 255.0;
	float b = float((xor * 8) & 0xff) / 255.0;

    vec3 col = vec3(r, g, b);
	fragColor = vec4(col, 1.0);
}	


