// The main noise function that determines the noise field. Feel free to play around with it!
float noiseFn(vec3 origin, vec3 dir, float len) {
    return snoise(vec4(vec3((origin + len * dir) / 4.), 1.)) - 0.5;
}

// Performance settings, you may have to tweak these based on how complicated your code is
const float stepSize = .2;
const float viewDistance = 40.;

float intersectFn(in vec3 rayOrigin, in vec3 rayDir) {
    float len = 0.;
    float val = noiseFn(rayOrigin, rayDir, len);

    // If we are already inside the noise field, step out of it. 
    // This solves an issue where the entire screen would become completely black if the camera was inside the field
    for (float i = 0.; i < viewDistance / stepSize; i++) {
        if (val <= 0.) break;
        len += 1.;
        val = noiseFn(rayOrigin, rayDir, len);
    }

    // Step according to stepSize until we hit the noise field.
    for (float i = 0.; i < viewDistance / stepSize; i++) {
        if (val >= 0.) break;
        len += stepSize;
        val = noiseFn(rayOrigin, rayDir, len);
    }

    if (len <= 0. || len >= viewDistance) {
        return viewDistance;
    }

    // Use the secant method to linearly smooth the result
    float x0 = len - stepSize;
    float x1 = len;
    float valX0 = noiseFn(rayOrigin, rayDir, x0);
    float result = x1 - val * (x1 - x0) / (val - valX0);

    return result;
}

float intersect(in vec3 rayOrigin, in vec3 rayDir) {
    float len = intersectFn(rayOrigin, rayDir);

    return len;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    // Stretch the canvas to make up for aspect ratios other than 1:1
    vec2 stretch = vec2(1., iResolution.y / iResolution.x);

    vec3 rayOrigin = vec3(iMouse.xy / iResolution.xy * 20., -iTime);
    vec3 rayDir = normalize(vec3((2. * uv - 1.) * stretch, -1.));

    // Intersect the ray with the scene
    float id = intersect(rayOrigin, rayDir);

    vec3 col = vec3(0.);

    if (id > 0.) {
        // Given a hit, draw a shade of gray relative to how far away the hit was
        col = vec3(1./id);
    }

    fragColor = vec4(col, 1);
}

