void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(sin(max(fragCoord.x, fragCoord.y)), cos(min(fragCoord.x, fragCoord.y)))/1.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float layer1 = noise(vec2(uv.x * (iTime / uv.y),uv.y * (iTime / uv.x)));

    float layer2 = noise(vec2(uv.y * (iTime / uv.x),uv.x * (iTime / uv.y)));

    vec3 c = vec3(
        layer1 * layer2
    );


    // Output to screen
    fragColor = vec4(c, 1.0);
}

