#define PI 3.141592

vec3 drawWave(in vec2 uv)
{
    //resize uv
    float downsize_x = 2.5;
    uv.x = (uv.x * downsize_x) - (downsize_x-1.0)*0.5;
    float downsize_y = 7.5;
    uv.y = (uv.y * downsize_y) - (downsize_y-1.0)*0.5;

    // the sound texture is 512x2
    int tx = int(uv.x*512.0);

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;


    // add wave form	
    float waveThickness = pow(abs(wave - 0.5), 0.6) * 3.0;
	vec3 waveCol = vec3(1.0 - smoothstep(0.0, waveThickness, abs(wave - uv.y)));
    // colorize wave
    float x_color_speed = 1.2;
    float x_color1 = cos(iTime * x_color_speed + PI * uv.x) * 0.5 + 0.5;
    float x_color2 = cos(iTime * x_color_speed + PI * uv.x + PI*0.25) * 0.5 + 0.5;
    float x_color3 = 1.0 - x_color2;
    waveCol *= vec3(waveCol.x * x_color3,
                    waveCol.x * x_color2,
                    waveCol.x * x_color1);
    // enhance color
    waveCol *= 1.2;

    // draw middle line
    float line_thickness = 0.02;
    float lineIsInXRange = float(uv.x > 0.0 && uv.x < 1.0);
    float lineIsInYRange = float(uv.y > (0.5 - line_thickness) && uv.y < (0.5 + line_thickness));
    float line_col = 1.0 * lineIsInXRange * lineIsInYRange;

    // combine
    vec3 col = line_col > 0.0 ? vec3(1.0) : waveCol;

    // Fade left/right borders of wave
    float fade = 0.2;
    col *= smoothstep(0.0, fade, uv.x) * (1.0 - smoothstep(1.0-fade, 1.0, uv.x));

    return col;
}

vec3 drawFreq(const vec2 uv, vec2 fragCoord)
{
    // cartesian to polar
    vec2 centered_uv = (fragCoord.xy - iResolution.xy*0.5) / max(iResolution.x, iResolution.y);
    float r = sqrt(centered_uv.x*centered_uv.x + centered_uv.y*centered_uv.y);
    float theta = atan(centered_uv.y, centered_uv.x);
    // re-range to [0,1]
    theta = theta / (2.0*PI) + 0.5;

    // Define bands
    float nBands = 128.0;
    float intBand = floor(theta*nBands);
    float fractBand = fract(theta*nBands);

    // the sound texture is 512x2
    int tx = int(intBand / nBands * 512.0);

    // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // draw bands circle
    float circleMinRadius = 0.24;
    float circleMaxRadius = 0.30;
    float bandWidth = 0.1;
    // add band width depending smoothly on time and theta
    bandWidth += 0.3 * abs(cos(iTime * 0.4 + PI * theta));
    // remap fft (freq amount) between [circleMinRadius, circleMaxRadius]
    float freqAmount = circleMinRadius + (circleMaxRadius - circleMinRadius) * fft;
    float isInBandRange = float(
                fractBand < bandWidth // band width
                && r > circleMinRadius // min radius
                && r < freqAmount); // freq radius
    vec3 freqColor = vec3(1.0) * isInBandRange;

    // colorize freq bands
    float x_color_speed = 1.2;
    float x_color1 = cos(iTime * x_color_speed + PI * theta) * 0.5 + 0.5;
    float x_color2 = cos(iTime * x_color_speed + PI * theta + PI*0.25) * 0.5 + 0.5;
    float x_color3 = 1.0 - x_color2;
    freqColor *= vec3(x_color1, x_color2, x_color3);

    // draw line
    float lineThickness = 0.003;
    vec3 lineColor = vec3(float(r > circleMinRadius && r < circleMinRadius + lineThickness));


    vec3 col = freqColor + lineColor;

    return col;
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 drawCircles(in vec2 uv)
{
    // the sound texture is 512x2
    int tx = int(uv.x * 512.0);

    // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    vec3 col = vec3(0.0);
    if (fft > 0.9)
    {
        col += rand(vec2(uv.y));
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // create pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0.0);
    col += drawFreq(uv, fragCoord);    
    col += drawWave(uv);
    //col += drawCircles(uv);

	// output final color
	fragColor = vec4(col,1.0);
}

