const float PI = 3.141592;

vec3 RED = vec3(206., 17., 39.) / 255.;
vec3 BLUE = vec3(0., 62., 135.) / 255.;

const float PadX = .39;
const float PadY = .26;

const float LW = .3;
const float CenterScale = .625;
const float WingScale = .43;

mat2 rotationMatrix(float angle)
{
	angle *= PI / 180.0;
    float s = sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}

float Band(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1 * step2;
}

float Rect(vec2 uv, float l, float r, float b, float t, float blur) 
{
    float band1 = Band(uv.x, l, r, blur);
    float band2 = Band(uv.y, b, t, blur);    

    return band1 * band2;
}

vec4 LeftTop(vec2 uv, float angle, float scale)  
{
    vec4 col = vec4(0);

    uv *= rotationMatrix(angle) / scale;

    col += Rect(uv, -LW, LW, .1, .2, .005);   
    col += Rect(uv, -LW, LW, -.05, .05, .005);   
    col += Rect(uv, -LW, LW, -.2, -.1, .005);   
    col.rgb *= 0.;

    return col;
}

vec4 RightTop(vec2 uv, float angle, float scale)
{
    vec4 col = vec4(0);   

    uv *= rotationMatrix(angle) / scale;

    float w = .45;
    float l = LW - LW * w;
    float r = LW - LW * w;

    col += Rect(uv, -LW * w - l, LW * w - l, .1, .2, .005);   
    col += Rect(uv, -LW * w + r, LW * w + r, .1, .2, .005);   
    col += Rect(uv, -LW, LW, -.05, .05, .005);    
    col += Rect(uv, -LW * w - l, LW * w - l, -.2, -.1, .005);   
    col += Rect(uv, -LW * w + r, LW * w + r, -.2, -.1, .005);   

    col.rgb *= 0.;
    return col;
}

vec4 LeftBottom(vec2 uv, float angle, float scale)
{
    vec4 col = vec4(0);   

    uv *= rotationMatrix(angle) / scale;

    float w = .45;
    float l = LW - LW * w;
    float r = LW - LW * w;

    col += Rect(uv, -LW, LW, .1, .2, .005);   
    col += Rect(uv, -LW * w - l, LW * w - l, -.05, .05, .005);   
    col += Rect(uv, -LW * w + r, LW * w + r, -.05, .05, .005);
    col += Rect(uv, -LW, LW, -.2, -.1, .005); 

    col.rgb *= 0.;
    return col;
}

vec4 RightBottom(vec2 uv, float angle, float scale)
{
    vec4 col = vec4(0);   

    uv *= rotationMatrix(angle) / scale;

    float w = .45;
    float l = LW - LW * w;
    float r = LW - LW * w;

    col += Rect(uv, -LW * w - l, LW * w - l, .1, .2, .005);   
    col += Rect(uv, -LW * w + r, LW * w + r, .1, .2, .005);   
    col += Rect(uv, -LW * w - l, LW * w - l, -.05, .05, .005);   
    col += Rect(uv, -LW * w + r, LW * w + r, -.05, .05, .005);
    col += Rect(uv, -LW * w - l, LW * w - l, -.2, -.1, .005);   
    col += Rect(uv, -LW * w + r, LW * w + r, -.2, -.1, .005);     

    col.rgb *= 0.;
    return col;
}

vec4 CenterBody(vec2 uv, float scale)
{
    vec2 rotatedUV = uv * rotationMatrix(30.) / scale;

    float body = smoothstep(0.01, 0., rotatedUV.y);    
    vec4 col = vec4(mix(RED, BLUE, body), 1.);

    float d = length(rotatedUV);
    col.a = smoothstep(0.4, 0.39, d);    

    float sd = length(rotatedUV - vec2(-0.2, 0.0));
    float sc = smoothstep(0.205, 0.195, sd);
    col.rgb = mix(col.rgb, RED, sc);

    sd = length(rotatedUV - vec2(0.2, 0.0));
    sc = smoothstep(0.205, 0.195, sd);
    col.rgb = mix(col.rgb, BLUE, sc);

    return col;
}

vec4 Flag(vec2 uv)
{
    vec4 col = vec4(0); 

    vec4 body = CenterBody(uv, CenterScale);    
    col = mix(col, body, body.a);

    vec4 lt = LeftTop(uv - vec2(-PadX,PadY), -50., WingScale);
    col = mix(col, lt, lt.a);

    vec4 rt = RightTop(uv - vec2(PadX,PadY), 50., WingScale);
    col = mix(col, rt, rt.a);

    vec4 lb = LeftBottom(uv - vec2(-PadX,-PadY), 50., WingScale);
    col = mix(col, lb, lb.a);

    vec4 rb = RightBottom(uv - vec2(PadX,-PadY), -50., WingScale);
    col = mix(col, rb, rb.a);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    float t = uv.x * 5. - iTime * 1.4 + uv.y * 3.5;
    uv.y += sin(t)*.035;

    vec4 col = vec4(1);    

    vec4 flag = Flag(uv);
    col = mix(col, flag, flag.a);
    float shadow = .95 + cos(t) *.156;    
    col *= shadow;
    col *= smoothstep(0.5, 0.495, abs(uv.y)); 

    fragColor = col;
}

#define M_PI 3.1415926535897932384626433832795

vec3 rotateX( in vec3 p, float t )
{
    float co = cos(t);
    float si = sin(t);
    p.yz = mat2(co,-si,si,co)*p.yz;
    return p;
}
vec3 rotateY( in vec3 p, float t )
{
    float co = cos(t);
    float si = sin(t);
    p.xz = mat2(co,-si,si,co)*p.xz;
    return p;
}
vec3 rotateZ( in vec3 p, float t )
{
    float co = cos(t);
    float si = sin(t);
    p.xy = mat2(co,-si,si,co)*p.xy;
    return p;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec4 opU( vec4 d1, vec4 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec4 mapSide(vec3 p, float displace) {
    vec2 k = p.xz;
    vec2 por = vec2(sqrt(k.x*k.x+k.y*k.y), atan(k.y,k.x));
    float a = por.x/0.2;
    float h2 = displace*0.4*a;
    float h = displace*0.4*floor(a);
    float b = smoothstep(0.1, 0.2, mod(por.x,0.2));
    vec3 q = p;
    float hh = displace*5.0*0.4;
    q.y = 1.0*q.y-h+hh;
    return vec4(sdBox(q, vec3(1.0,0.2, 1.0)),vec3(b, h2/hh, 0.0));
}

vec4 map(vec3 p) {
    vec4 res = vec4(1e10, 0, 0,0);

    float d = 0.7;
    float s = 1.0;
    if (iTime < 1.0) {
        s = (1.0+sin(iTime*M_PI - M_PI*0.5))/2.0;
    }
    float dd = d*0.5*(1.0+sin(iTime - M_PI*0.5))+0.01;
    {
    vec3 q = p - vec3(0,0.0, -1.0);
    q /= s;
    res = opU(res, mapSide(q, dd)*s);
    }
    {
    vec3 q = rotateX(p, M_PI*1.5) - vec3(0,0.0, 1.0);
    q /= s;
    res = opU(res, mapSide(q, dd)*s);
    }
    {
    vec3 q = rotateX(p, M_PI*0.5) - vec3(0,-2.0, -1.0);
    q /= s;
    res = opU(res, mapSide(q, dd)*s);
    }
    {
    vec3 q = rotateZ(p, M_PI*0.5) - vec3(1.0,-1.0, -1.0);
    q /= s;
    res = opU(res, mapSide(q, dd)*s);
    }
    {
    vec3 q = rotateZ(p, M_PI*1.5) - vec3(-1.0,-1.0, -1.0);
    q /= s;
    res = opU(res, mapSide(q, dd)*s);
    }
    {
    vec3 q = rotateX(p, M_PI*1.0) - vec3(0.0,-2.0, 1.0);
    q /= s;
    res = opU(res, mapSide(q, dd)*s);
    }

    return res;
}

vec2 iSphere( in vec3 ro, in vec3 rd, in float rad )
{
	float b = dot( ro, rd );
	float c = dot( ro, ro ) - rad*rad;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0);
    h = sqrt(h);
	return vec2(-b-h, -b+h );
}

vec3 calcNormal( in vec3 p )
{
    const float h = 0.05;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ).x + 
                      k.yyx*map( p + k.yyx*h ).x + 
                      k.yxy*map( p + k.yxy*h ).x + 
                      k.xxx*map( p + k.xxx*h ).x );
}   

vec4 raycast( in vec3 ro, in vec3 rd )
{
    vec4 res = vec4(-1.0);
    vec2 tminmax = iSphere( ro, rd, 20.0 );
	if( tminmax.y>0.0 )
    {
        float t = max(tminmax.x,0.001);
        for( int i=0; i<1024 && t<tminmax.y; i++ )
        {
            vec4 h = map(ro+t*rd);
            if( h.x<0.01 ) { res=vec4(t,h.yzw); break; }
            t += 0.1*h.x;
        }
    }

    return res;
}

vec3 gradient(float z) {
    vec3 a = vec3(0.46,0.76,1);
    vec3 b = vec3(0.11,0.98,1);
    vec3 c = vec3(0.69,0.49,1);
    vec3 d = vec3(1,1,1);
    if (z < 0.3) {
        return mix(a, b, z);
    } else if (z < 0.99999) {
        return mix (b, c, z);
    } else {
        return mix(c,d, z);
    }
}

vec3 render( in vec3 ro, vec3 rd )
{ 
    vec4 res = raycast(ro, rd);
    vec3 pos = ro + res.x*rd;
    vec3 col = vec3(0.0);
    vec3 mat =  mix(vec3(1.0), gradient(res.z), res.y);
    vec3 normal = calcNormal(pos);
    if (res.x < 0.0) {
        return vec3(0.0);
    } 

    vec3 lin = vec3(0.0);
    lin += 1.0;
    col = lin * mat;
	return vec3(clamp(col,0.0,1.0));
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

mat3 viewMat( in vec3 eye, in vec3 look )
{
    vec3 w = normalize(eye-look);
    vec3 u = normalize(cross(w, vec3(0,1,0)));
    vec3 v = cross(w, u);
    return mat3( u, v, w );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec2 mouse = iMouse.xy /iResolution.xy;
    if (iMouse.xy == vec2(0)) {
        mouse = vec2(0.4,0.7);
    }
    vec2 spr = vec2(mouse.x * M_PI - M_PI/2.0, mouse.y*M_PI-M_PI)*0.8;
    float r = 20.0;
    vec3 eye = vec3(-r*sin(spr.x)*sin(spr.y), -r*cos(spr.y),-r*sin(spr.y)*cos(spr.x)) + vec3(0.0, -2.0, 0.0);
    vec3 look = vec3(0.0,0.0,0);
    mat3 view = viewMat(eye, look);
    dir = view * dir;
    vec3 col = render(eye, dir);
    fragColor = vec4(col, 1.0);
}

