float PrSphDf (vec3 p, float r);
float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf);
float PrEllipsDf (vec3 p, vec3 r);
float Maxv3 (vec3 p);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

vec3 footPos[8], kneePos[8], hipPos[8], ltDir, qHit;
float tCur, dstFar, legLenU, legLenD, bdyHt, spdVel;
int idObj;
const int idBdy = 1, idHead = 2, idEye = 3, idAnt = 4, idLegU = 5, idLegD = 6;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float SpdDf (vec3 p, float dMin)
{ // (see "Moebius, Menger, Spiders")
  vec3 q;
  float d, s, len, szFac;
  szFac = 0.12;
  p /= szFac;
  dMin /= szFac; 
  p.y -= bdyHt - 0.1;
  q = p - vec3 (0., -0.15, 0.2);
  d = PrEllipsDf (q, vec3 (0.7, 0.5, 1.3));
  DMINQ (idBdy);
  q = p - vec3 (0., 0.1, 1.1);
  d = PrEllipsDf (q, vec3 (0.2, 0.4, 0.5));
  DMINQ (idHead);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.15, 0.25, 1.5);
  d = PrSphDf (q, 0.13);
  DMINQ (idEye);
  q -= vec3 (0., 0.15, -0.3);
  d = PrEETapCylDf (q, 1.3 * vec3 (0.3, 1.1, 0.4), vec3 (0.), 0.07, 0.7);
  DMINQ (idAnt);
  p.y += bdyHt;
  for (int j = 0; j < 8; j ++) {
    q = p - hipPos[j];
    d = 0.6 * PrEETapCylDf (q, kneePos[j], hipPos[j], 0.25, 0.3);
    DMINQ (idLegU);
    q = p - kneePos[j];
    d = 0.6 * PrEETapCylDf (q, footPos[j] - vec3 (0.3), kneePos[j] - vec3 (0.3), 0.2, 1.2);
    DMINQ (idLegD);
  }
  dMin *= szFac;
  return dMin;
}

float ObjDf (vec3 p)
{ // (see "Spiraling Out")
  vec3 q;
  float dMin, d, r, a;
  dMin = dstFar;
  q = p;
  r = length (q.xz);
  if (r > 0.01) {
    a = atan (q.z, q.x) / pi;
    q.xz = - (mod (vec2 (0.5 * (pi * log (r) + a) - 0.05 * tCur, 5. * a) + 0.5, 1.) - 0.5);
    q.y *= 0.7 / sqrt (r);
    dMin = 0.5 * r * SpdDf (q, dMin);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (h, 0.01);
    if (h < 0.001 || d > dMax) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SpdCol (vec3 vn)
{
  vec3 col, c1, c2;
  c1 = vec3 (0.5, 1., 0.2);
  c2 = vec3 (0.5, 0.2, 0.2);
  if (idObj == idBdy) {
    col = mix (c1, c2, SmoothBump (0.2, 0.7, 0.05, mod (4. * qHit.z, 1.)));
  } else if (idObj == idHead) {
    col = c2;
    if (qHit.z > 0.4) col = mix (vec3 (0.2, 0.05, 0.05), col,
       smoothstep (0.02, 0.04, abs (qHit.x)));
  } else if (idObj == idEye) {
    col = (vn.z < 0.6) ? vec3 (0., 1., 0.) : c1;
  } else if (idObj == idLegU || idObj == idLegD) {
    col = mix (c2, c1,  SmoothBump (0.4, 1., 0.2, fract (3.5 * length (qHit))));
  } else if (idObj == idAnt) {
    col = vec3 (1., 1., 0.3);
  }
  return col;
}

void SpdSetup (float gDisp)
{
  vec3 v;
  float a, az, fz, d, ll;
  for (int j = 0; j < 4; j ++) {
    a = 0.2 * (1. + float (j)) * pi;
    hipPos[j] = 0.5 * vec3 (- sin (a), 0., 1.5 * cos (a));
    hipPos[j + 4] = hipPos[j];
    hipPos[j + 4].x *= -1.;
  }
  bdyHt = 1.5;
  legLenU = 2.2;
  legLenD = 3.;
  ll = legLenD * legLenD - legLenU * legLenU;
  for (int j = 0; j < 8; j ++) {
    fz = fract ((gDisp + 0.93 + ((j < 4) ? -1. : 1.) + mod (7. - float (j), 4.)) / 3.);
    az = smoothstep (0.7, 1., fz);
    footPos[j] = 5. * hipPos[j];
    footPos[j].x *= 1.7;
    footPos[j].y += 0.7 * sin (pi * clamp (1.4 * az - 0.4, 0., 1.));
    footPos[j].z += ((j < 3) ? 0.5 : 1.) - 3. * (fz - az);
    hipPos[j] += vec3 (0., bdyHt - 0.3, 0.2);
    v = footPos[j] - hipPos[j];
    d = length (v);
    a = asin ((hipPos[j].y - footPos[j].y) / d);
    kneePos[j].y = footPos[j].y + legLenD *
       sin (acos ((d * d + ll) / (2. * d *  legLenD)) + a);
    kneePos[j].xz = hipPos[j].xz + legLenU * sin (acos ((d * d - ll) /
       (2. * d *  legLenU)) + 0.5 * pi - a) * normalize (v.xz);
  }
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, sh;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = SpdCol (vn);
    if (idObj != idEye) col = Maxv3 (col) * vec3 (1.);
    col = col * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.2 * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    col = mix (vec3 (0.3), col, smoothstep (0.05, 0.1, length (vec2 (ro.xz))));
  } else if (rd.y < 0.) {
    ro += (- ro.y / rd.y) * rd;
    vn = vec3 (0., 1., 0.);
    sh = ObjSShadow (ro + 0.01 * vn, ltDir, dstFar);
    col = vec3 (0.4) * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.));
  } else col = vec3 (0.4);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.03 * pi * tCur;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
  }
  el = clamp (el, -0.35 * pi, -0.15 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -30.);
  zmFac = 4.;
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (0.7, 1., -1.));
  spdVel = 1.5;
  SpdSetup (spdVel * tCur);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  col = ShowScene (ro, rd);
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf)
{
  vec3 v;
  float s;
  v = v1 - v2;
  s = clamp (dot (p, v) / dot (v, v), 0., 1.);
  return length (p - s * v) - r * (1. - rf * s * s);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

//Fork from @alijaya Ray Marching Phong Template https://www.shadertoy.com/view/4t2fR1
//Added the color picking system I learnt from @samlo https://www.shadertoy.com/view/wd2SR3

#define EPSILON 0.01
#define MIN_DISTANCE 0.0
#define MAX_DISTANCE 2000.0
#define MAX_MARCHING_STEP 256

#define COLOR1 1.
#define COLOR2 2.
#define COLOR3 3.


struct Ray {
    vec3 o; // origin
    vec3 d; // direction, always normalized
};

struct Camera {
    vec3 pos;
    vec3 lookAt;
    float focal;
    vec2 sensorSize;
};

struct Light {
    vec3 pos;
    vec3 color;
};

vec3 xe = vec3(EPSILON, 0., 0.);
vec3 ye = vec3(0., EPSILON, 0.);
vec3 ze = vec3(0., 0., EPSILON);

// get corresponding ray from uv (0.0 -> 1.0)
Ray getRay( Camera cam, vec2 uv ) {
    vec3 camDir = normalize(cam.lookAt - cam.pos); // the camera direction
    vec3 center = cam.pos + cam.focal * camDir; // get the coordinate of the screen center
    vec2 coord = (uv - 0.5) * cam.sensorSize; // convert from 0.0 -> 1.0 to -sensorSize / 2. -> sensorSize / 2.

    // axis relative to camera
    vec3 zpos = -camDir; // k hat, outward the screen
    vec3 xpos = cross(vec3(0., 1., 0.), zpos); // i hat, cross from ypos global and zpos local
    vec3 ypos = cross(zpos, xpos); // j hat, cross from zpos local and xpos local

    // target in screen
    vec3 target = center + coord.x * xpos + coord.y * ypos; // get pixel target coordinate from the screen

    Ray ray;
    ray.o = cam.pos; // the origin of the ray is from the camera duh
    ray.d = normalize(target - cam.pos); // the direction is from the camera to the target

    return ray;
}

// project point to ray
vec3 projPoint2Ray( vec3 point, Ray ray ) {
    vec3 rel = point - ray.o; // get relative vector from the point to the ray origin
    float len = dot(rel, ray.d); // project that vector to vector direction of the ray, we get the length
    return len * ray.d + ray.o; // multiply the length with unit vector direction and add to ray origin
}

// get the distance from point to ray (or line)
float distPoint2Ray( vec3 point, Ray ray ) {
    vec3 proj = projPoint2Ray(point, ray); // project it first to the ray
    return distance(proj, point); // get the distance from the projected position to the original position
}

// just linear interpolation, map value v between fromA and fromB to between toA and toB
float lerp( float v, float fromA, float fromB, float toA, float toB ) {
    return (v - fromA) / (fromB - fromA) * (toB - toA) + toA;
}

// sphere function
float sdSphere( vec3 p, float radius ) {
    return length(p) - radius;
}

// box function
float sdBox( vec3 p, float width, float height, float depth ) {
    vec3 d = abs(p) - vec3(width, height, depth);
    return max(d.x, max(d.y, d.z));
}

// cylinder function
float sdCylinder( vec3 p, float radius, float height ) {
    return max(length(p.xz) - radius, abs(p.y) - height);
}

float sdUnion( float sd1, float sd2 ) {
    return min(sd1, sd2);
}

float sdIntersect( float sd1, float sd2 ) {
    return max(sd1, sd2);
}

float sdSubstract( float sd1, float sd2 ) {
    return max(sd1, -sd2);
}

float add (inout vec2 a, vec2 b){
    a = mix(a,b,step(b.x,a.x));
    return 0.;
}

// change this            \\\now, instead of returning a float with the distance, returns vec2(distance,colorID)
vec2 sdScene( vec3 p ) {
    float sphere = sdSphere( p-vec3(-100), 220. );
    float box = sdBox(p-vec3(-150), 200., 50., 200.);
    float box2 = sdBox(p, 50., 200., 200.);
    float cylinder = sdCylinder(p-vec3(100), 150., 250.);
    vec2 S = vec2(sphere,COLOR1);
    vec2 B = vec2(box,COLOR2);
    vec2 C = vec2(max(-box2,cylinder),COLOR3);
    vec2 result = C;
    add(result,S);
    add(result,B);
    return result;
}

// estimate normal with nudging
vec3 estimateNormal( vec3 p ) {
    return normalize(vec3(
        sdScene(p+xe).x - sdScene(p-xe).x,
        sdScene(p+ye).x - sdScene(p-ye).x,
        sdScene(p+ze).x - sdScene(p-ze).x
    ));
}

// ray marching algorithm
vec2 getSDF( Ray r ) {
    float depth = MIN_DISTANCE;
    float matID;
    for (int i=0; i<MAX_MARCHING_STEP; i++) {
        float dist = sdScene( r.o + depth * r.d ).x;      //checks distance
        matID = sdScene (r.o + depth * r.d).y;            //checks colorID

        if (dist < EPSILON) { // inside a scene
            return vec2(depth,matID);
        }

        depth += dist; // circle marching!

        if (depth >= MAX_DISTANCE) {
            return vec2(MAX_DISTANCE,matID);
        }
    }

    return vec2(MAX_DISTANCE,matID);
}

vec3 ambientLight( vec3 ambientColor ) {
    return ambientColor;
}

vec3 phongLight( vec3 diffuseColor, vec3 specularColor, float shininess, Ray ray, float dist, Light light ) {
    vec3 p = ray.o + dist * ray.d;

    vec3 N = estimateNormal(p); // surface normal
    vec3 L = normalize(light.pos - p); // surface to light direction
    vec3 V = -ray.d; // surface to camera direction
    vec3 R = reflect(-L, N); // mirror of L by the axis N

    float dotLN = dot(L, N); // project light direction to surface normal
    float dotRV = dot(R, V); // project light reflection direction to direction to camera

    vec3 color = vec3(0.);
    if (dotLN > 0.) { // we can see the diffuse
        color += diffuseColor * dotLN;
    }
    if (dotRV > 0.) { // we can see the specular
        color += specularColor * pow(dotRV, shininess);
    }
    color *= light.color; // influence of color of the light
    return color;
}

vec3 calcMaterial (float id) { //here you assign the colors you want
    if (id == COLOR1)      return vec3(1.,0,0);
    if (id == COLOR2)     return vec3(0,1.,0);
    if (id == COLOR3)     return vec3(0,0,1.);
    return vec3(1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // convert from fragCoord (0.0 -> iResolution.xy) to uv (0.0 -> 1.0)
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 circleMovement = vec3(600. * cos(iTime * 2.), 600. * cos(iTime * 2.), 600. * sin(iTime * 2.)); // to get circle movement

    // set camera
    Camera cam;
    cam.sensorSize = iResolution.xy; // set sensor size to the screenSize
    cam.focal = 200.; // set the focal length (distance between sensor (screen) to the aperture
    cam.lookAt = vec3(0., 0., 0.); // where to look at (this camera use two point system)
    cam.pos = circleMovement; // the position of the camera

    // get ray
    Ray ray = getRay(cam, uv); // get corresponding ray, given the uv


    // render
    float dist = getSDF(ray).x;
    float colID = getSDF(ray).y;


    if (dist > MAX_DISTANCE - EPSILON) { // don't hit anything, set to background
        fragColor = vec4(0.);
        return; // or discard?
    }

    vec3 ambientColor = vec3(0.1);
    vec3 diffuseColor = calcMaterial(colID);//transforms the colorID to assigned colors
    vec3 specularColor = vec3(1.0, 1.0, 1.0);
    float shininess = 50.0;

    Light light1;
    light1.pos = vec3(500. * sin(iTime), 500. * cos(iTime), 400.);
    light1.color = vec3(1., .3, .1);

    Light light2;
    light2.pos = vec3(1000. * cos(iTime), 400., 1000. * sin(iTime));
    light2.color = vec3(.1, .3, 1.);

    vec3 color = vec3(0.);
    color += ambientLight(ambientColor);
    color += phongLight(diffuseColor, specularColor, shininess, ray, dist, light1);
    color += phongLight(diffuseColor, specularColor, shininess, ray, dist, light2);
    fragColor = vec4(color, 1.);
}

void mainImage(out vec4 o, vec2 u)
{
    u /= iResolution.y*.5;
    for(int i=0;i<16;++i) {
        vec2 v = fract(u.yx-iTime*.7)-.5,
        s = min(fract(u += v*v),1.-fract(u));
        o = fract(u.xyyy+sin(u.yxyx))*min(s.x,s.y);
    }
}

// The triangle UI is base on https://iquilezles.org/www/articles/distfunctions/distfunctions.htm

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 ac = a - c; vec3 pc = p - c;
  vec3 nor = cross( ba, ac );

  return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}


vec3 caculate_col(vec3 p, vec3 pos_a, vec3 pos_b, vec3 pos_c, 
                    vec3 col_a, vec3 col_b, vec3 col_c){
    float alpha = (-(p.x-pos_b.x)*(pos_c.y-pos_b.y) + (p.y-pos_b.y)*(pos_c.x-pos_b.x))/(-(pos_a.x-pos_b.x)*(pos_c.y-pos_b.y)+(pos_a.y-pos_b.y)*(pos_c.x-pos_b.x));
    float beta = (-(p.x-pos_c.x)*(pos_a.y-pos_c.y) + (p.y-pos_c.y)*(pos_a.x-pos_c.x))/(-(pos_b.x-pos_c.x)*(pos_a.y-pos_c.y)+(pos_b.y-pos_c.y)*(pos_a.x-pos_c.x));
    float gamma = 1. - alpha - beta;

    return alpha*col_a + beta*col_b + gamma*col_c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 pos_a = vec3(0.25, 0.5,0.);
    vec3 pos_b = vec3(0.5, 0.75,0.);
    vec3 pos_c = vec3(0.75, 0.25,0.);

    vec3 col_a = vec3(1.,0.,0.);
    vec3 col_b = vec3(0.,1.,0.);
    vec3 col_c = vec3(0.,0.,1.);

    float d = udTriangle(vec3(uv.xy,0.),pos_a,pos_b,pos_c);

    vec3 col = vec3(0.);

    if(d > 0.){
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }else{
        col = caculate_col(vec3(uv.xy,0.),pos_a,pos_b,pos_c,col_a,col_b,col_c);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// This shader is base on https://www.shadertoy.com/view/4d2XWV

//-------------------------------------------------------------------------------------------
// sphere related functions
//-------------------------------------------------------------------------------------------

float sphIntersect( in vec3 ro, in vec3 rd, in vec4 sph )
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return -1.0;
	return -b - sqrt( h );
}


float sphSoftShadow( in vec3 ro, in vec3 rd, in vec4 sph, in float k )
{
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;

#if 1
    // physically plausible shadow
    float d = sqrt( max(0.0,sph.w*sph.w-h)) - sph.w;
    float t = -b - sqrt( max(h,0.0) );
    return (t<0.0) ? 1.0 : smoothstep(0.0, 1.0, 2.5*k*d/t );
#else
    // cheap but not plausible alternative
    return (b>0.0) ? step(-0.0001,c) : smoothstep( 0.0, 1.0, h*k/b );
#endif    
}    

float sphOcclusion( in vec3 pos, in vec3 nor, in vec4 sph )
{
    vec3  r = sph.xyz - pos;
    float l = length(r);
    return dot(nor,r)*(sph.w*sph.w)/(l*l*l);
}

vec3 sphNormal( in vec3 pos, in vec4 sph )
{
    return normalize(pos-sph.xyz);
}

//=====================================================

vec2 hash2( float n ) { return fract(sin(vec2(n,n+1.0))*vec2(43758.5453123,22578.1459123)); }

float iPlane( in vec3 ro, in vec3 rd )
{
    return (-1.0 - ro.y)/rd.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;

	vec3 ro = vec3(0.0, 0.0, 4.0 );
	vec3 rd = normalize( vec3(p,-1.5) );

    // sphere animation
    vec4 sph = vec4( cos( iTime + vec3(2.0,1.0,1.0) + 2.0 )*vec3(1.5,0.0,1.0), 1.0 );

    vec3 lig = normalize( vec3(sin(iTime),sin(iTime)+1.5,-sin(iTime)) );
    vec3 col = vec3(0.0);

    float tmin = 1e10;
    vec3 nor;
    float occ = 1.0;

    // deal plane intersection
    float t1 = iPlane( ro, rd );
    if( t1>0.0 )
    {
        tmin = t1;
        vec3 pos = ro + t1*rd;
        nor = vec3(0.0,1.0,0.0);
        occ = 1.0-sphOcclusion( pos, nor, sph );

    }

    // deal sphere intersection
    float t2 = sphIntersect( ro, rd, sph );
    if( t2>0.0 && t2<tmin )
    {
        tmin = t2;
        vec3 pos = ro + t2*rd;
        nor = sphNormal( pos, sph );
        occ = 0.5 + 0.5*nor.y;
	}

    // deal sphere soft-shadow
    if( tmin<1000.0 )
    {
        vec3 pos = ro + tmin*rd;

		col = vec3(1.0);
        col *= clamp( dot(nor,lig), 0.0, 1.0 ); // this line code is to caculate diffuse term
        col *= sphSoftShadow( pos, lig, sph, 2.0 ); // actually this line code is to caculate sphere soft-shadow
        col += 0.05*occ; // this line code is to caculate ambient occlusion term
	    col *= exp( -0.05*tmin ); // this line code is to make the plane's border softly
    }

    fragColor = vec4( col, 1.0 );
}

// Constants
const float pi                   = 3.14159265359;
const float plancks_constant     = 6.62607015 * 10e-35;
const float boltzmanns_constant  = 1.38064852 * 10e-24;
const float speed_of_light       = 299792458.0;

// Settings
const uint number_of_iterations       = 64u;
const float min_temperature_in_kelvin = 1000.0;
const float max_temperature_in_kelvin = 12000.0;

// Gaussian function - see : https://en.wikipedia.org/wiki/CIE_1931_color_space
float gaussian(float x, float a, float u, float a1, float a2)
{
    float t = (x - u) / (x < u ? a1 : a2);
	return a * exp(-(t * t) / 2.0);
}

// Wave length to XYZ conversion - see : https://en.wikipedia.org/wiki/CIE_1931_color_space
vec3 convert_wave_length_to_XYZ(float wave_length_nm)
{
	// The analytical approximation assumes the wave length is measured in angstroms
	float wave_length_angstroms = wave_length_nm * 10.0;

	// Approximate with the sum of gaussian functions
	vec3 XYZ;
	XYZ.x = gaussian(wave_length_angstroms, 1.056, 5998.0, 379.0, 310.0) + gaussian(wave_length_angstroms, 0.362, 4420.0, 160.0, 267.0) + gaussian(wave_length_angstroms, -0.065, 5011.0, 204.0, 262.0);
	XYZ.y = gaussian(wave_length_angstroms, 0.821, 5688.0, 469.0, 405.0) + gaussian(wave_length_angstroms, 0.286, 5309.0, 163.0, 311.0);
	XYZ.z = gaussian(wave_length_angstroms, 1.217, 4370.0, 118.0, 360.0) + gaussian(wave_length_angstroms, 0.681, 4590.0, 260.0, 138.0);

    // Done
	return XYZ;
}

// XYZ to sRGB conversion - see : https://en.wikipedia.org/wiki/SRGB
vec3 convert_XYZ_to_sRGB(vec3 XYZ)
{
	// CIE XYZ -> sRGB
	vec3 sRGB;
	sRGB.x =  3.2406 * XYZ.x - 1.5372 * XYZ.y - 0.4986 * XYZ.z;
	sRGB.y = -0.9689 * XYZ.x + 1.8758 * XYZ.y + 0.0415 * XYZ.z;
	sRGB.z =  0.0557 * XYZ.x - 0.2040 * XYZ.y + 1.0570 * XYZ.z;

	// Apply gamma correction
	float a = 0.055;
	sRGB.x = (sRGB.x <= 0.0031308) ? 12.92 * sRGB.x : (1.0 + a) * pow(sRGB.x, 1.0 / 2.4) - a;
	sRGB.y = (sRGB.y <= 0.0031308) ? 12.92 * sRGB.y : (1.0 + a) * pow(sRGB.y, 1.0 / 2.4) - a;
	sRGB.z = (sRGB.z <= 0.0031308) ? 12.92 * sRGB.z : (1.0 + a) * pow(sRGB.z, 1.0 / 2.4) - a;

	// Done
	return vec3(sRGB.x, sRGB.y, sRGB.z);
}

// Implementation of Planck's law - see : https://en.wikipedia.org/wiki/Planck%27s_law
float convert_wave_length_to_black_body_spectrum(float wave_length_nm, float temperature_in_kelvin)
{
    float wave_length_in_meters = wave_length_nm * 1e-9;
    float c1                    = 2.0 * pi * plancks_constant * speed_of_light * speed_of_light;
    float c2                    = plancks_constant * speed_of_light / boltzmanns_constant;
    float m                     = c1 / pow(wave_length_in_meters, 5.0) * 1.0 / (exp(c2 / (wave_length_in_meters * temperature_in_kelvin)) - 1.0);
    return m;
}

// Information on remapping the black body temperature to sRGB - see : https://astronomy.stackexchange.com/questions/39994/what-is-the-rgb-curve-for-blackbodies
// Another good reference for implementation : https://www.fourmilab.ch/documents/specrend/specrend.c
vec3 convert_black_body_temperature_to_sRGB(float temperature_in_kelvin) 
{
	vec3 XYZ = vec3(0.0);

    // Integrate
    for (uint k = 0u ; k < number_of_iterations ; ++k)
    {
        float wave_length_nm = mix(380.0, 780.0, float(k) / float(number_of_iterations - 1u));
        float spectrum       = convert_wave_length_to_black_body_spectrum(wave_length_nm, temperature_in_kelvin);

        // Accumulate
        XYZ += spectrum * convert_wave_length_to_XYZ(wave_length_nm);
    }

    // Normalize to the tristimulus values
    vec2 xy = XYZ.xy / (XYZ.x + XYZ.y + XYZ.z);

	// The sRGB colour space assumed that the XYZ has been scaled to (0.9505, 1, 1.0890) for CIE D65, i.e. Y = 1
	XYZ = vec3(xy.x / xy.y, 1.0, (1.0 - xy.x - xy.y) / xy.y);

	// XYZ to sRGB
	vec3 sRGB = convert_XYZ_to_sRGB(XYZ);

    // Normalize RGB to preserve chromaticity
	return sRGB / max(sRGB.x, max(sRGB.y, sRGB.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Normalised cursor
    float cursor_position = (iMouse.z > 0.0) ? (iMouse.x / iResolution.x) : cos(0.25 * iTime) * 0.5 + 0.5;

    if( uv.y < 0.5 )
    {
        // Black body temperature
        float temperature_in_kelvin = mix(min_temperature_in_kelvin, max_temperature_in_kelvin, cursor_position);
        vec3 sRGB = convert_black_body_temperature_to_sRGB(temperature_in_kelvin);

        fragColor = vec4(sRGB,1.0);
    }
    else
    {
        // Black body temperature
        float temperature_in_kelvin = mix(min_temperature_in_kelvin, max_temperature_in_kelvin, uv.x);
        vec3 sRGB = convert_black_body_temperature_to_sRGB(temperature_in_kelvin);

        // Output to screen
        fragColor = vec4(sRGB,1.0);

        // Show cursor
        if( abs(fragCoord.x - cursor_position * iResolution.x) < 1.0 )
        {
            fragColor = mix(fragColor, vec4(1.0) - fragColor, 0.1);
        }
    }
}

