void mainImage( out vec4 O, in vec2 XY )
{
    vec2 UV = XY / iResolution.xy;

    //----------------------------------------------
    // Setup the value

    float runtimeInf = uintBitsToFloat(uint(0x7f800000));
    float runtimeNaN = uintBitsToFloat(uint(0x7fffffff));

    const float NumSetups = 9.0;

    float t = iTime * 3.14159265 * 2.0 / 10.0;

    if (false) { /* noop */ }
    else if (UV.y > 8.0 / NumSetups) {       // O
        /* noop */
    }
    else if (UV.y > 7.0 / NumSetups) {       // iFrame
        O = vec4(float(iFrame));
    }
    else if (UV.y > 6.0 / NumSetups) {       // sin(t) in [-1, 1]
        O = vec4(sin(t));
    }
    else if (UV.y > 5.0 / NumSetups) {       // NaN
        O = vec4(runtimeNaN);
    }
    else if (UV.y > 4.0 / NumSetups) {       // Inf
        O = vec4(runtimeInf);
    }
    else if (UV.y > 3.0 / NumSetups) {       // O -= O
        O -= O;
    }
    else if (UV.y > 2.0 / NumSetups) {       // O += 0.5 - O
        O += 0.5 - O;
    }
    else if (UV.y > 1.0 / NumSetups) {       // O -= O - 0.5
        O -= O - 0.5;
    }
    else if (UV.y > 0.0 / NumSetups) {       // O -= -0.5 + O
        O -= -0.5 + O;
    }

    //----------------------------------------------
    // Examine the value

    const float NumTests = 4.0;
    if (false) {}
    else if (UV.x < 1.0 / NumTests) {
        O.rgb = vec3(isnan(O.r) ? 0.5 : 0.0);
    }
    else if (UV.x < 2.0 / NumTests) {
        O.rgb = vec3(isinf(O.r) ? 0.5 : 0.0);
    }
    else if (UV.x < 3.0 / NumTests) {
        float testWidthPx = iResolution.x / NumTests;
        int i = int(XY.x - testWidthPx * 2.0);
        int bitIndex = int(float(i) * 32.0 / testWidthPx);
        uint bitValue = (floatBitsToUint(O.r) >> (31 - bitIndex)) % uint(2);
        O = vec4(bitValue == uint(1) ? 1.0 : 0.0);
        O = mix(O, vec4(0.5), step(0.7, fract(float(i)  * 32.0 / testWidthPx)));
    }
    else { /* noop */ }

    bool isGridLine = false;
    isGridLine = isGridLine || !bool(step(3.0, mod(XY.y, (iResolution.y - 2.0) / NumSetups)));
    isGridLine = isGridLine || !bool(step(3.0, mod(XY.x, (iResolution.x - 2.0) / NumTests )));

    if (isGridLine) {
        O.rgb = vec3(0.7, 0.0, 0.5);
    }


    O.a = 1.0;
}

// Author: Thomas Stehle
// Title: Hommage Ã  Ben Kovach 2 ("Luminary", 2021)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License
//
// Animated reproduction of "Luminary" by Ben Kovach:
// https://bendotk.com/#/luminary/
//
// Technical note: I didn't find a good solution for analytical
// anti-aliasing so this piece does the unsatisfactory multipass
// approach instead.

// Constants
const int AA = 2; // Anti aliasing passes = AA^2
const int NCOLORS = 5;
const int NLAYERS = NCOLORS - 1;
const int GRIDSIZE = 9;
const vec2 GRID = vec2(GRIDSIZE);

// 1D hash for 1D input by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash(in float p) {
    p = fract(p * 0.011);
    p *= p + 7.5;
    p *= p + p;
    return fract(p);
}

// 1D hash for 2D input by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash12(in vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * 0.13);
    p3 += dot(p3, p3.yzx + 3.333);
    return fract((p3.x + p3.y) * p3.z);
}

// 2D value noise by Morgan McGuire
// https://www.shadertoy.com/view/4dS3Wd
float vnoise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Four corners in 2D of a tile
    float a = hash12(i);
    float b = hash12(i + vec2(1.0, 0.0));
    float c = hash12(i + vec2(0.0, 1.0));
    float d = hash12(i + vec2(1.0, 1.0));

    // Smooth interpolation (smoothstep without clamping)
    vec2 u = f*f * (3.0 - 2.0*f);

    // Mix 4 coorners
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// SDF for a circle
float sdCircle(in vec2 p, in vec2 c, in float r) {
    return length(p - c) - r;
}

// Circles arranged in a grid
// Returns the distance to a circle and its ID
vec2 circleLayer(in vec2 p) {
    p *= GRID;
    vec2 uv = fract(p);
    vec2 id = floor(p);

    float d = sdCircle(uv, vec2(0.5), 0.5);
    return (d < 0.0) ?
        vec2(d, 1.0 + id.x + GRID.x * id.y) :
        vec2(d, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 result = vec3(0.0);

    // Anti-aliasing loop
    for (int j = 0; j < AA; ++j) {
    for (int i = 0; i < AA; ++i) {
        // Screen coordinates
        vec2 off = vec2(i,j) / float(AA);
        vec2 st = (fragCoord.xy + off) / iResolution.xy;
        st.x *= iResolution.x / iResolution.y;

        // Circle layer with specific offset
        vec2 layers[NLAYERS];
        layers[0] = circleLayer(st);
        layers[1] = circleLayer(st + vec2(0.5, 0.0));
        layers[2] = circleLayer(st + vec2(0.0, 0.5));
        layers[3] = circleLayer(st + vec2(0.5));

        // Principal color per layer
        vec3 colors[NCOLORS];
        colors[0] = vec3(251, 246, 223) / 255.0; // beige
        colors[1] = vec3(247, 210,  73) / 255.0; // yellow
        colors[2] = vec3(153, 164, 137) / 255.0; // green
        colors[3] = vec3( 60,  62,  58) / 255.0; // brown
        colors[4] = vec3( 19,  54, 131) / 255.0; // blue

        // Time
        float t = 0.5 * iTime;

        // Bias the noise towards the right
        float f = st.x;

        // Noise value per pixel per layer
        float noise[NCOLORS];
        noise[0] = 1.0;
        noise[1] = f * vnoise(1.0 * st + t);
        noise[2] = f * vnoise(2.0 * st + t + 1.0);
        noise[3] = f * vnoise(3.0 * st + t - 1.0);
        noise[4] = f * vnoise(4.0 * st + t + 1.0);

        // Combine the IDs to form one ID that represents a circle fragment
        int id = int(layers[0].y) +
                 int(layers[1].y) * GRIDSIZE +
                 int(layers[2].y) * GRIDSIZE*GRIDSIZE +
                 int(layers[3].y) * GRIDSIZE*GRIDSIZE*GRIDSIZE;

        // Hash for the combined ID
        float h = hash(float(id));

        // Mix colors of all layers per circle fragment
        vec3 col = colors[0];
        for (int k = 1; k < NCOLORS; ++k) {
            float w = h * step(h, noise[k]);
            col = mix(col, colors[k], w);
        }

        // Accumulate result color
        result += col;
    }
    }
    result /= float(AA * AA);

    // Contrast stretching
    result = pow(result, vec3(1.2));

    // Final color
    fragColor = vec4(result, 1.0);
}

#define E 2.718281828459

float t(){
    return pow(E,-1. * pow(2. * cos(iTime),2.));
}

float line(vec2 uv, vec2 p1, vec2 p2){
    float k = (p1.y-p2.y)/(p1.x-p2.x);
    float b = p1.y - k * p1.x;
    return k*uv.x + b - uv.y;
}

vec2 line_pos(float t, vec2 b0, vec2 b1){
    return (1.-t)*b0 + t*b1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.8);


    vec2 b0 = vec2(0.25,0.25);
    vec2 b1 = vec2(1.,0.75);
    vec2 b2 = vec2(1.5,0.25);

    float t = t();
    vec2 b01 = line_pos(t, b0, b1);
    vec2 b11 = line_pos(t, b1, b2);
    vec2 b02 = line_pos(t, b01, b11);

    //draw line
    float d = abs(line(uv,b0,b1));
    if(d < 0.005 && uv.x>b0.x && uv.x<b1.x){
        col *= mix(col, vec3(0.1), smoothstep(-fwidth(d), fwidth(d), d));
    }

    d = abs(line(uv,b1,b2));
    if(d < 0.005 && uv.x>b1.x && uv.x<b2.x){
        col *= mix(col, vec3(0.1), smoothstep(-fwidth(d), fwidth(d), d));
    }

    // draw point
    float ps = 0.02;
    float r = length(uv-b0);
    if(r<ps){
        col *= mix(col, vec3(1.,0.1,0.1), smoothstep(-fwidth(r), fwidth(r), r));
    }

    r = length(uv-b1);
    if(r<ps){
        col *= mix(col, vec3(0.1,1.,0.1), smoothstep(-fwidth(r), fwidth(r), r));
    }

    r = length(uv-b2);
    if(r<ps){
        col *= mix(col, vec3(0.1,0.1,1.), smoothstep(-fwidth(r), fwidth(r), r));
    }

    r = length(uv-b01);
    if(r<ps){
        col *= mix(col, vec3(1.,1.,0.1), smoothstep(-fwidth(r), fwidth(r), r));
    }

    r = length(uv-b11);
    if(r<ps){
        col *= mix(col, vec3(0.1,1.,1.), smoothstep(-fwidth(r), fwidth(r), r));
    }

    r = length(uv-b02);
    if(r<ps){
        col *= mix(col, vec3(1.,0.1,1.), smoothstep(-fwidth(r), fwidth(r), r));
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define time iTime

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float sdBoxFrame( vec3 p, vec3 b, float e )
{
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

// All components are in the range [0â¦1], including hue.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}


// All components are in the range [0â¦1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

