float star_mot (float x) {
    return fract_usd(x*2.)*step(floor(mod(x,2.)),.9);
}

vec3 pattern (vec2 st, float t,float size) {
    st = st*2. - 1.;
    st *= size;
    vec3 color = vec3(1.);
    float r = length(st),th=atan(st.y,st.x);

    // circle
    float c_size = fract_usd(t/2.);

    r = length(st),th=atan(st.y,st.x);
    color *= ring(st,.9*smoothstep(0.,1.,c_size),.18);

    // star
    float s_size = smoothstep(.2,.85,star_mot(-.5+t/2.000));
    float s_angle = smoothstep(.1,.9,fract(t/4.))*TAU;

    th += s_angle;
    color *= step(.5,(r*(pow(abs(sin(th*2.5))+1.,1.216))*.85)/s_size);
    return color;
}

