    float SCALE = 0.280;
    float TILT_ANGLE = PI/11.112;
    vec2 SLIDE_VEL = vec2(-.04,.02);

    st = st*2. -1.;
    st = SCALE*(1.+.1*sin(t/10.))*rotate2d(TILT_ANGLE)*st + t*SLIDE_VEL ;


    // pattern tiling
    vec2 WAVE_VECTOR = vec2(0.770,0.740);
    float WAVE_VEL = 1.;

    float n = 16.;
    st *= n;
	vec2 ipos = floor(st), fpos = fract(st);

    vec3 color = pattern(fpos,t*WAVE_VEL+dot(ipos,WAVE_VECTOR),.9);

    // coloring	-- CHANGE THE COLORS OUTSIDE OF MAIN()
    st = ipos+fpos;
    color += mix(color1,color2,smoothstep(0.,1.,fract_usd(st.x+st.y))/6.);

    // sliders animation
