    //loop through neighboring cells to check points
    for(int i=-1; i<=1; i++){
        for(int j=-1; j<=1; j++){
            //find the neighbor
            vec2 neighbor = vec2(float(i), float(j));
            //random (deterministicallly) point
            vec2 point = random2(i_st+neighbor);
            // point = 0.5 + 0.5*sin(u_time + 6.2831*point); //animate
