// From this lesson:
// https://www.scratchapixel.com/lessons/3d-basic-rendering/rasterization-practical-implementation/perspective-correct-interpolation-vertex-attributes

struct TriIntersect {
    bool hit;
    vec3 pos;
    float dist;
    vec3 bary;
};

TriIntersect iTriangle(in vec3 ro, in vec3 rd, in vec3 a, in vec3 b, in vec3 c) {
    ro -= a; b -= a; c -= a; a = vec3(0.0);
    vec3 ba = b - a, cb = c - b, ac = a - c;

    vec3 n = cross(ba, -ac);
    float denom = dot(rd, n);
    if (denom != 0.0) {
        float t = -dot(ro, n) / denom;

        vec3 p = ro + rd * t;
        vec3 pa = p - a, pb = p - b, pc = p - c;

        float abc = length(n);
        float u = length(cross(cb, pb)) / abc;
        float v = length(cross(ac, pc)) / abc;
        float w = length(cross(ba, pa)) / abc;

        return TriIntersect(abs(u + v + w - 1.0) < 0.001, p, t, vec3(u, v, w));
    }

    return TriIntersect(false, vec3(0.0), 0.0, vec3(0.0));
}

bool pointInTriangle(in vec2 p, in vec2 a, in vec2 b, in vec2 c) {
    bool pa = p.y > a.y, pb = p.y > b.y, pc = p.y > c.y;
    bool wa = pa != pc && pc != ((c.x - a.x) * (p.y - a.y) > (c.y - a.y) * (p.x - a.x));
    bool wb = pb != pa && pa != ((a.x - b.x) * (p.y - b.y) > (a.y - b.y) * (p.x - b.x));
    bool wc = pc != pb && pb != ((b.x - c.x) * (p.y - c.y) > (b.y - c.y) * (p.x - c.x));
    return wa ^^ wb ^^ wc;
}

vec3 barycentric(in vec2 p, in vec2 a, in vec2 b, in vec2 c) {
    vec2 pa = p - a, pb = p - b, pc = p - c;
    vec2 ba = b - a, cb = c - b, ac = a - c;

    float abc = abs(ba.y * ac.x - ba.x * ac.y);
    float abp = abs(ba.x * pa.y - ba.y * pa.x);
    float bcp = abs(cb.x * pb.y - cb.y * pb.x);
    float cap = abs(ac.x * pc.y - ac.y * pc.x);

    return vec3(bcp, cap, abp) / abc;
}

void render(inout vec4 fragColor, in vec2 fragCoord) {
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord / iResolution.y - vec2(0.5 * aspect, 0.5);
    float unit = 2.0 / iResolution.y;
    float offset = 0.25 * aspect;
    vec3 color = vec3(0.0);

    // Vertices
    float transition = smoothstep(1.0, 0.0, abs(mod(iTime * 0.25, 2.0) - 1.0));
    vec3 a = mix(vec3(-2.0, -2.0, -6.0), vec3(-2.0, -1.0, -7.0), transition);
    vec3 b = mix(vec3( 2.0, -2.0, -6.0), vec3(2.0, -1.0, -5.0), transition);
    vec3 c = mix(vec3( 2.0,  2.0, -6.0), vec3(0.0, 1.5, -11.0), transition);

    // UVs
    vec2 uv1 = vec2(0.0, 0.0);
    vec2 uv2 = vec2(1.0, 0.0);
    vec2 uv3 = vec2(1.0, 1.0);

    if (uv.x > 0.0) { // Raytrace triangle on right
        uv.x -= offset; // Recenter

        vec3 ro = vec3(0.0, 0.0, 0.0);
        vec3 rd = normalize(vec3(uv, -1.0));

        // Blue gradient background
        color.b += exp(-2.0 * length(fragCoord / iResolution.xy * vec2(2.0, 1.0) - vec2(1.5, 0.5)));

        TriIntersect tri = iTriangle(ro, rd, a, b, c);
        if (tri.hit) {
            vec2 triUv = uv1 * tri.bary.x + uv2 * tri.bary.y + uv3 * tri.bary.z;

            float grid = 1.0 - max(smoothstep(0.01, 0.0, abs(mod(triUv.x + 0.1, 0.2) - 0.1) - 0.002),
                                   smoothstep(0.01, 0.0, abs(mod(triUv.y + 0.1, 0.2) - 0.1) - 0.002));

            color = vec3(grid);
        }

        uv.x += offset;
    }

    if (uv.x < 0.0) { // Rasterize triangle on left
        uv.x += offset; // Recenter

        // Toggle perspective correction
        bool correct = mod(iTime, 16.0) < 8.0;

        // Gradient background (red when perspective correction is off, green when its on)
        color.rg = vec2(!correct, correct) * exp(-2.0 * length(fragCoord / iResolution.xy * vec2(2.0, 1.0) - 0.5));

        // Compute inverse z coordinates
        float azInv = 1.0 / a.z;
        float bzInv = 1.0 / b.z;
        float czInv = 1.0 / c.z;

        // Project (flipping the z due to the camera facing in the -z direction)
        vec2 aProj = a.xy * -azInv;
        vec2 bProj = b.xy * -bzInv;
        vec2 cProj = c.xy * -czInv;

        if (pointInTriangle(uv, aProj, bProj, cProj)) {
            vec3 bary = barycentric(uv, aProj, bProj, cProj);

            // Divide attributes by corresponding z coordinate
            if (correct) {
                uv1 *= azInv;
                uv2 *= bzInv;
                uv3 *= czInv;
            }

            vec2 triUv = uv1 * bary.x + uv2 * bary.y + uv3 * bary.z; // Interpolate like usual

            if (correct) {
                float z = 1.0 / (azInv * bary.x + bzInv * bary.y + czInv * bary.z); // Perspective correct z coordinate
                triUv *= z;
            }

            float grid = 1.0 - max(smoothstep(0.01, 0.0, abs(mod(triUv.x + 0.1, 0.2) - 0.1) - 0.002),
                                   smoothstep(0.01, 0.0, abs(mod(triUv.y + 0.1, 0.2) - 0.1) - 0.002));

            color = vec3(grid);
        }

        uv.x -= offset;
    }

    color = mix(color, vec3(0.8, 0.6, 0.0), smoothstep(unit, 0.0, abs(uv.x) - 0.005));
    fragColor.rgb += color;
}

// Supersample
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
     fragColor = vec4(0.0);
     render(fragColor, fragCoord);
     render(fragColor, fragCoord + vec2(0.5, 0.0));
     render(fragColor, fragCoord + vec2(0.0, 0.5));
     render(fragColor, fragCoord + vec2(0.5, 0.5));
     fragColor.rgb /= 4.0;
}

