vec2 boxIntersection( in vec3 ro, in vec3 rd, in vec3 rad) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );

    if( tN>tF || tF<0.0) return vec2(-1.0); 
    return vec2( tN, tF );
}

vec2 plant(vec3 p,vec2 center) {
    const float sh=.3; //shift
    float a =hash(center*1.1),b=hash(center*11.1);
    vec3 s1= vec3(sin(a*25.),cos(a*25.),0)*sh; //bottom
    vec3 s2= vec3(sin(a*37.+iTime)*sh,cos(a*37.+iTime)*sh,5.+b*2.5); //top
     float tk = .15 *smoothstep(30.,5.,p.z)+ .2* smoothstep(.6,0.1,abs(s2.z-p.z -.3))+ .2* smoothstep(.6,0.1,p.z); //thickness
     return vec2(sdCapsule(p- vec3(center, 0),s1,s2,tk),abs(s2.z-p.z -.3)<0.6? b*5.:0.);
}
#define mmin(a,b) (a.x<b.x?a:b)
vec2 scene(vec3 p) {
    vec2 center = floor(p.xy) + .5;
    vec2 neighbour = center + edge(p.xy - center);
    float height = sin(center.y + center.x+iTime)*2.;
    vec2 me = plant(p,center);
    vec2 next =plant(p,neighbour); //closest plant can "invade" current cell (but must stay in the 2x 45 degrees square)
    return mmin(mmin(me, next),vec2(p.z,0.));
}

vec3 norm(vec3 p) {
    mat3 k = mat3(p,p,p)-mat3(0.01);
    return normalize(scene(p).x - vec3(scene(k[0]).x,scene(k[1]).x,scene(k[2]).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    vec3 cam = normalize(vec3(1.5,uv));
    vec3 init = vec3(-30,0,0);

    float yrot = .45;
    float zrot = iTime*.05;
    if (iMouse.z > 0.) {
        yrot = clamp(1.-2.*mouse.y,-0.,3.14/2.);
        zrot = 4.*mouse.x;
    }

    cam = erot(cam,vec3(0,1,0),yrot);
    init = erot(init,vec3(0,1,0),yrot);
    cam = erot(cam,vec3(0,0,1),zrot);
    init = erot(init,vec3(0,0,1),zrot);
    //init.z += 1.;

    vec3 p =init;
    bool hit = false;
    vec2 d;
    for (int i = 0; i < 200 && !hit; i++) {
       d = scene(p);

        //if inside a box, don't march after border
        vec3 center = vec3(floor(p.xy) + .5,10.);
        vec2 tb= boxIntersection(  p-center,  cam, vec3(.501,.501,10.) ) ;
        if( tb.x<0. &&tb.y>0. && tb.y<d.x) d.x=tb.y;       

        hit = d.x*d.x < 1e-6;
        p += d.x*cam;
        if(distance(p,init)>70.)break;
    }
    vec3 n = norm(p);
    vec3 r = reflect(cam, n);
    vec3 col=  mix( hue(vec3(d.y*.4+3.6 +mod(p.z,.1)*15.)) ,vec3(0.278,0.835,0.204), smoothstep(5.3,4.2,p.z));     

    float l =length(sin(r*2.)*.5+.5)/sqrt(3.);
    l = l*.1 + pow(l, 6.);
    col= hit ? col*l: vec3(.05);
    col = mix(col,vec3(0.529,0.525,0.796),exp(min(0.,-5.+distance(init,p)/10.))); //fog
    fragColor =  vec4(sqrt(col),0) ;

}

