    uv *= mat2(cos(angle),-sin(angle),sin(angle),cos(angle));//rotation matrix
    vec3 col = vec3(0);//set bg

    //changes num to check based on mousepos, sin(time, uv)
    if(is_prime(uv.x + iMouse.x + sin(iTime+uv.x)) == true 
    && is_prime(uv.y + iMouse.y + cos(iTime+uv.y)) == true){
        //sets color to change based on uv offset by mousepos
        col = vec3(0,normalize((uv.yx+iMouse.xy)/size));

    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define fGlobalTime iTime
float diam(vec3 p,float s){
   p=  abs(p);
   return (p.x+p.z+p.y-s)*inversesqrt(3.);  
 }
float box(vec3 p, vec3 b){
    vec3 q = abs(p)-b;
    return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.y,q.z)));
 }
 float sdBoxFrame( vec3 p, vec3 b, float e )
{
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}
mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
float smin( float a, float b, float k )
{
float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float bbox(vec3 p,float q){
    float h = sdBoxFrame(p,vec3(1.),.1);
    return smin(h, diam(abs(p)-.9,.3),q);

 }
vec3 extp,intp;
 vec2 struc(vec3 p,float q){

     vec2 h,t;
     vec3 pp = p;
     p.xy*=rot(fGlobalTime);
     p.xz*=rot(fGlobalTime);
     extp=p;
     h.x = box(p,vec3(2.55));
     h.y = 1.;
     p = abs(p)-.9;
     p = abs(p)-.9;
     h.x = max(-h.x,bbox(p,q));


     pp.xy*=rot(-fGlobalTime);
     pp.xz*=rot(-fGlobalTime);
     t.x = bbox(pp,q);
     t.y = 1.5;
     h= t.x < h.x ? t:h;
     intp=pp;
   return h;

 }

vec2 sdf(vec3 p){

     p.yz*=rot(atan(inversesqrt(1./2.)));
     p.xz*=rot(3.141592/4.);
      p.xz/=4.;
     p.xz = asin(sin(p.xz)*(.925-texture(iChannel0,p.xz*.1).r*.075));
     p.xz*=4.;
    vec2 h;

     h= struc(p*.92,-.01);
     vec2 t = struc(p,.0);
     t.y += 1.;
     h= t.x < h.x ? t:h;
    return h;
}
