  return .5+.5*cos(6.28*(.1*t+vec3(1.,.4,.3)));
  }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
vec3  col = vec3(.1);
  vec3 ro = vec3((uv)*10.,-30.);
  vec3 rd = vec3(0.,0.,1.);
  vec3 light = vec3(7.,5.,-5.);
  vec3 rp = ro;
  vec3 acc = vec3(.0);
  for(float i=0.;i<=128.;i++){
      vec2 d = sdf(rp);
    if(d.y >= 2.){
         vec3 off = d.y == 2.5 ? intp:extp;
         acc +=sqrt(pal(length(off)+iTime*2.))*exp(2.*-abs(d.x))/(29.+sin(atan(off.x,off.y)*10.)*20.);
       d.x = max(.002+sin(off.y*20.+off.x*20.)*.01,abs(d.x));
     }
    if(length(rp)>50.) break;
     if(d.x <=.001){
         if(d.y==1.){
          vec3 n = norm(rp);
          float diff = max(0.,dot(normalize(light-rp),n));
          float sp = max(0.,dot(normalize(ro-rp),reflect(-normalize(light),n)));
            col = vec3(.5)*diff+vec3(5.,0.,0.)*pow( sp,32.);
            col *=AO(rp,n)/1.7;
           }
        break;
      }
      rp+=d.x*rd;
  }
  col +=acc;

    fragColor = vec4(col,1.0);
}

// A simplication of https://www.shadertoy.com/view/tlSGzG

const float PI = 3.14159;

float arcSDF(vec2 pos, vec2 loc,  float r, float start, float end) {
    vec2 dist = pos - loc;
    float mid = mod(atan(dist.x, dist.y), 2.*PI),
          d = max( start - mid, mid - end );     

   return max( length(dist)*d ,  length(dist) - r );
}

float sceneSDF(vec2 uv) {

    float location = mod(iTime, 4.*PI);
    float angleStart, angleEnd;

    if(location > 2.*PI) {
        angleStart = 0.;
        angleEnd = mod(iTime, 2.*PI);
    } else {
        angleStart = mod(iTime, 2.*PI);
        angleEnd = 2.*PI;
    }


    return arcSDF(uv, vec2(0.), 0.3, angleStart, angleEnd);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;

    float dist = sceneSDF(uv);
    fragColor = vec4( smoothstep(2./iResolution.y, 0., dist ) );
}

