#define PI 3.14159265359
#define TWO_PI 6.28318530718

float fold(in float x) {
    return abs(mod(x+1.0,2.0)-1.0);
}

float sine_ramp(in float x, in float s) {
    float y = fract(x);
    float h = 0.5 - (0.5/(s*s));
    return floor(x) + y + h*sin(6.28318530718*y);
}

float boot_ramp(in float x, in float s) {
    float y = fract(x);
    return floor(x) + pow(y, s*y+(1.0/s));
}

float shelf_ramp(in float x, in float s) {
    float y = fract(x);
    return floor(x) + 1.0-pow(1.0-y, s*y+(1.0/s));
}

float N_ramp(in float x, in float s) {
    float y = fract(x);
    float h = max(0.3, s);
    return floor(x) + length(vec2(y,0.0)-vec2(pow(y,1.0/h),pow(y,h)));
}

vec2 rotate2D(in vec2 vUV, in float theta) {
    vec2 r = vec2(cos(theta), sin(theta));
    return vUV * mat2(r.x, -r.y, r.y, r.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 vUV = fragCoord.xy / iResolution.xy;
    vUV -= 0.5;
    vUV *= 2.5;
    vUV = rotate2D(vUV, -iTime*0.025);
    vUV += iTime*vec2(0.05*(-vUV.y*0.01), 0.05*(vUV.x*0.01)) + (iTime*0.05);
    vUV.x *= iResolution.x / iResolution.y;
    vec3 color = vec3(0.0);

    float scl = 4.0;
    float slope = scl* pow(fold(iTime*0.05), 2.0) + 1.0;
    vUV = vec2(sine_ramp(vUV.x,slope), sine_ramp(vUV.y,slope));
    vUV = vec2(fold(vUV.y*scl),fold(vUV.x*scl));

    vUV /= N_ramp(length(vUV) * 0.9, 2.0);

    float dx = length(vUV);

    dx = length(vUV);
    dx = fold(3.0*dx);
    dx = clamp(dx, 0.0, 1.0);
    dx = N_ramp(dx, 5.0);

    float br = 3.0*shelf_ramp(fold(iTime*0.25), 1.0) + 4.0;

    color += boot_ramp(dx, br);
    color = color * 1.4;
    color = clamp(color, 0.0, 1.0);

    vec3 A = vec3(1.0, 0.3686, 0.3686) * color;
    vec3 B = vec3(0.3843, 0.7333, 1.0) * color;
    vec3 C = vec3(0.5098, 1.0, 0.5098) * color;

    color = mix(A, C, N_ramp(fold(length(vUV)), 4.0));
    color = mix(color, B, fold((vUV.y+vUV.x)*0.5 + (iTime*0.1)));
    color = smoothstep(0.0, 1.0, color);

    fragColor = vec4(color, 1.0);
}

const float PI=3.1415926535897932384626433832795;


float arctangens(float a)
{
    return asin(a/sqrt(1.0+a*a));
}


float getAngle(float x, float y)
{
    // For i915 with Linux replace atan() to arctangens()
    float alpha=atan( abs(y/x) );

    if(x>=0.0 && y>=0.0)
    {
        return alpha;
    }

    if(x<0.0 && y>=0.0)
    {
        return PI-alpha;
    }

    if(x<0.0 && y<0.0)
    {
        return PI+alpha;
    }

    return 2.0*PI-alpha;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 color = vec3(0.0, 0.0, 0.0);

    float x=sin(iTime);
    float y=cos(iTime);


    // Square move by circle
    float visX=x/2.0+0.5;
    float visY=y/2.0+0.5;

    if(uv.y>=visY && uv.y<visY+0.01)
      if(uv.x>=visX && uv.x<visX+0.01)
        color=vec3( 0.4, 0.4, 1.0 );


    // Angle visualization
    float angle=getAngle(x, y)/(2.0*PI);

    if(uv.y>=0.9 && uv.y<1.0)
        if(uv.x>=angle && uv.x<(angle+0.01))
            color=vec3( 0.1, 0.8, 0.8 );    


    // Output to screen
    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


//increase this constant to increase the amount of detail
#define OCTAVES 4

float fbm(in vec2 uv)
{
    //this function generates the terrain height
    float value = 0.;
    float factor = -3.;
    uv *= factor;
    for (int i = 0; i < OCTAVES; i++)
    {
        uv += max(sin(uv*factor)/factor,cos(uv/factor)*factor).yx;
        value = 3.+max(value+value*sin(uv.x)/factor,value+cos((uv.y/value)/factor)/factor);
        uv = uv.yx*1.5/factor;
        //factor /= 1.5;

    }

    return value;
}


struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 1.;
    return fbm(uv/factor)*factor/2.;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    #endif
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 1.;

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

float p;
#define AAstep(edge, x) smoothstep(edge - p, edge + p, x)

float getCross(vec2 uv, float size, float len)
{
    uv = abs(uv);
    vec2 line = step(uv.yx * 2., step(uv,vec2(len)) * size);
    return max(line.x, line.y);
}

vec3 georgianFlag(vec2 uv)
{

    float rot = uv.x * 6. - iTime * 2.5 + uv.y * 2.;
    uv.y += sin(rot) * 0.05;

    if(abs(uv.y) > .5 || abs(uv.x) > 0.75) 
        return vec3(0);

    float mainCross = getCross(uv, 0.175, .75);
    float crosses = getCross(-abs(uv) + vec2( 0.5,  0.3), 0.07, 0.15);
    float flag = mainCross + crosses;

    float shade = .9 + cos(rot) * .2;

    const vec3 WHITE = vec3(1);
    const vec3 RED = vec3(0.85, 0.16, 0.2); 
    vec3 col = mix(WHITE, RED, flag) * shade; 

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord - R.xy * 0.5) / R.y; 
    p = length(fwidth(fragCoord / R.xy))*8.;
    fragColor = vec4(georgianFlag(uv * 1.25),1.0);
}

/*
    Author: Felipe Tovar-Henao [www.felipe-tovar-henao.com]
    Description: Kaleidoscopic tiling using shaping functions and tile rotations.
*/

#define u_resolution iResolution
#define u_time iTime

float fold(in float x) {
    return abs(mod(x+1.0,2.0)-1.0);
}

float camel_ramp(in float x, in float s) {
    float y = fract(x);
    return floor(x) + pow(0.5 - (0.5 * cos(6.28318530718*y) * cos(3.14159265359*y)), s);
}

float cosine_ramp(in float x, in float s) {
    float y = cos(fract(x)*3.14159265359);
    return floor(x) + 0.5 - (0.5*pow(abs(y), 1.0/s)*sign(y));
}

float bump_ramp(in float x, in float s) {
    float y = fract(x);
    return floor(x) + pow(y, exp(s*y));
}

vec2 rotate2D(in vec2 vUV, in float theta) {
    vec2 r = vec2(cos(theta), sin(theta));
    return vUV * mat2(r.x, -r.y, r.y, r.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUV = fragCoord.xy / u_resolution.xy;
    vUV -= 0.5;
    float aspect = u_resolution.x / u_resolution.y;
    vUV.x *= aspect;
    float theta = cosine_ramp(u_time * 0.075, 2.0);
    vUV = rotate2D(abs(vUV+vec2(0.15*aspect, 0.0))*3.7, theta);;
    vec2 vUVA = mod(vUV, max(vec2(0.1), mod(abs(fract(vUV)*2.3 - fract(vUV+0.3) + 0.2), 2.0)));    
    vUV = mix(vUV, vUVA, fold(theta));
    vUV = vec2(fold(vUV.x), fold(vUV.y));
    vec2 fUV = fract(vUV);
    vec2 iUV = floor(vUV);
    float dx = length(iUV-0.5);
    fUV = rotate2D(fUV, cosine_ramp(u_time*0.3, 2.0));

    float s = fold(u_time*0.4)*2.0 + 2.0;
    float border = dx * 0.05 + 0.25;
    float x1 = camel_ramp(fUV.x,s) * border;
    float y1 = camel_ramp(fUV.y,s) * border;

    float edge = 0.015;
    float bottom = smoothstep(edge,0.0, abs(x1-fUV.y));
    float top = smoothstep(edge,0.0, abs(x1+(1.0-border)-fUV.y));
    float left = smoothstep(edge,0.0, abs((1.0-border)+y1-fUV.x));
    float right = smoothstep(edge, 0.0, abs(y1-fUV.x));

    float tile = clamp(top+bottom+left+right, 0.0, 1.0);
    tile = smoothstep(0.0, 1.0, tile);
    float len = length(vec2(x1, y1)*1.2);

    vec3 color = vec3(0.0);
    vec3 c1a = vec3(0.9412, 0.5725, 0.5725);
    vec3 c1b = vec3(0.9216, 0.4275, 0.4196);
    vec3 c1 = tile * mix(c1a, c1b, fold(u_time*0.125));
    color = clamp(color-c1, 0.0, 1.0) + c1;

    vec3 c2a = vec3(0.5294, 0.6784, 0.8039);
    vec3 c2b = vec3(0.3922, 0.4627, 0.5804);
    vec3 c2 = bump_ramp(fold(len*13.0 + (sin(0.3*u_time + vUV.x) * 3.0)), 3.5) * mix(c2a, c2b, fold(u_time * 0.2));
    c2 = smoothstep(0.0,1.0,c2);
    color = clamp(color-c2, 0.0, 1.0) + c2;
    vec3 shade = clamp(1.0-color, 0.0, 1.0) * vec3(0.098, 0.098, 0.1098);
    color = clamp(color-shade, 0.0, 1.0) + shade;

    fragColor = vec4(color, 1.0);
}

// Author: paperu
// Title: quantum field

float t;
#define P 6.283185307

vec3 SpectrumPoly(in float x) {
    // https://www.shadertoy.com/view/wlSBzD
    return (vec3( 1.220023e0,-1.933277e0, 1.623776e0)+(vec3(-2.965000e1, 6.806567e1,-3.606269e1)+(vec3( 5.451365e2,-7.921759e2, 6.966892e2)+(vec3(-4.121053e3, 4.432167e3,-4.463157e3)+(vec3( 1.501655e4,-1.264621e4, 1.375260e4)+(vec3(-2.904744e4, 1.969591e4,-2.330431e4)+(vec3( 3.068214e4,-1.698411e4, 2.229810e4)+(vec3(-1.675434e4, 7.594470e3,-1.131826e4)+ vec3( 3.707437e3,-1.366175e3, 2.372779e3)*x)*x)*x)*x)*x)*x)*x)*x)*x;
}
mat2 rot(in float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }

float box(in vec3 p, in float s) { p = abs(p) - s; return max(p.x,max(p.y,p.z)); }
float cyl(in vec3 p, in float h) { return length(p.xz) - h; }

float df(in vec3 p) {
    vec3 pp = p;

    float d = 10e9;

    for(int i = 0; i < 15; i++) {
        d = min(d, cyl(p, -.002));
        p.xy *= rot(P/3. + t + length(p)*2.);
        p.z = abs(p.z) - .5;
        p.xz *= rot(P/3. - t + length(p)*5.1);
        p.x = abs(p.x) - .5;
    }

    return max(d, box(pp, .75));
}

#define LIM .001
#define MAX_D 5.
#define MAX_IT 50
int rm(in vec3 c, in vec3 r) {
    vec3 p = c;
    int it = 0;
    bool hit = false;
    for(int i = 0; i < MAX_IT; i++) {
        float d = df(p);
        if(d < LIM || distance(c,p) > MAX_D) break;
        p += d*r;
        it = i;
    }
    return it;
}

vec3 plane2sphere(in vec2 p) {
    float t = -4./(dot(p,p) + 4.);
    return vec3(-p*t, 1. + 2.*t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord.xy - iResolution.xy*.5)/iResolution.y;
    t = iTime*.25 + 3.;

    vec3 c = vec3(0.,0.,0.);
    vec3 r = normalize(vec3(st,1.));
    r = plane2sphere(st*12.);

    r.xy *= rot(t);
    r.xz *= rot(t);
    r.yz *= rot(t);

    int it = rm(c,r);

    float s = pow(float(it)*.02,2.);
    vec3 color = mix(vec3(0.), SpectrumPoly((max(1.-s,.55))), s*1.);
    color = mix(color*1., vec3(1.), s);

    fragColor = vec4(color,1.0);
}

// License CC0: DragonEye II
// A year or so ago I played with quasi crystals and I found they looked like "dragon eyes" with certain parameters
// This is an evolution of that idea.

#define TIME        iTime
#define TTIME       (TAU*TIME)
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define LAYERS      6
#define FBM         3
#define DISTORT     1.4
#define PCOS(x)     (0.5+0.5*cos(x))

// https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const float eyeAngle = 0.8;
const mat2  eyeRot = ROT(eyeAngle);
const vec2  eyeRef = vec2(cos(eyeAngle), sin(eyeAngle));

float g_psy_th = 0.0;
float g_psy_hf = 0.0;

vec2 g_psy_vx = vec2(0.0);
vec2 g_psy_vy = vec2(0.0);

vec2 g_psy_wx = vec2(0.0);
vec2 g_psy_wy = vec2(0.0);

const vec3 lightPos1 = 100.0*vec3(-1.3, 1.9, 2.0);
const vec3 lightPos2 = 100.0*vec3(9.0,  3.2, 1.0);
const vec3 lightDir1 = normalize(lightPos1);
const vec3 lightDir2 = normalize(lightPos2);
const vec3 lightCol1 = vec3(8.0/8.0,7.0/8.0,6.0/8.0);
const vec3 lightCol2 = vec3(8.0/8.0,6.0/8.0,7.0/8.0);
const vec3 skinCol1  = vec3(0.6, 0.2, 0.2);
const vec3 skinCol2  = vec3(0.6);

vec3 saturate(in vec3 a) { return clamp(a, 0.0, 1.0); }
vec2 saturate(in vec2 a) { return clamp(a, 0.0, 1.0); }
float saturate(in float a) { return clamp(a, 0.0, 1.0); }

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// IQ's smooth min: https://www.iquilezles.org/www/articles/smin/smin.htm
float pmin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float pabs(float a, float k) {
  return pmax(a, -a, k);
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return vec2(p.x*cos(p.y), p.x*sin(p.y));
}

// https://mercury.sexy/hg_sdf/
float modMirror1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize,size) - halfsize;
  p *= mod(c, 2.0)*2.0 - 1.0;
  return c;
}

float smoothKaleidoscope(inout vec2 p, float sm, float rep) {
  vec2 hp = p;

  vec2 hpp = toPolar(hp);
  float rn = modMirror1(hpp.y, TAU/rep);

  float sa = PI/rep - pabs(PI/rep - abs(hpp.y), sm);
  hpp.y = sign(hpp.y)*(sa);

  hp = toRect(hpp);

  p = hp;

  return rn;
}

float vesica(vec2 p, vec2 sz) {
  if (sz.x < sz.y) {
    sz = sz.yx;
  } else {
    p  = p.yx; 
  }
  vec2 sz2 = sz*sz;
  float d  = (sz2.x-sz2.y)/(2.0*sz.y);
  float r  = sqrt(sz2.x+d*d);
  float b  = sz.x;
  p = abs(p);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

// https://www.iquilezles.org/www/articles/spherefunctions/spherefunctions.htm
float raySphere(vec3 ro, vec3 rd, vec4 sph) {
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;
    if( h<0.0 ) return -1.0;
    h = sqrt( h );
    return -b - h;
}

float outer(vec2 p) {
  p *= eyeRot;
  return vesica(p, 1.0*vec2(0.5, 0.25))-0.15;
}

float inner(vec2 p) {
  p *= eyeRot;
  return vesica(p, 1.0*vec2(0.125, 0.35));
}


float qc_wave(float theta, vec2 p) {
  return (cos(dot(p,vec2(cos(theta),sin(theta)))));
}

float qc_noise(vec2 p) {
  float sum = 0.;
  float a = 1.0;
  for(int i = 0; i < LAYERS; ++i)  {
    float theta = float(i)*PI/float(LAYERS);
    sum += qc_wave(theta, p)*a;
    a*=DISTORT;
  }

  return abs(tanh_approx(sum));
}

float qc_fbm(vec2 p, float time) {
  float sum = 0.;
  float a = 1.0;
  float f = 1.0;
  for(int i = 0; i < FBM; ++i)  {
    sum += a*qc_noise(p*f);
    a *= 2.0/3.0;
    f *= 2.31;
  }

  return 0.45*(sum);
}

float qc_height(vec2 p) {
  float od = outer(p);
  float l = length(p);
  const float s = 5.0;
  p *= s;
//  return -5.0*pmin(fbm(p), 0.75, 2.5)*exp(-5.0*l);
  float sm = 0.05;
  float oh = smoothstep(0.0, sm, od); 

  float h =  -5.0*qc_fbm(p, TIME)*exp(-4.0*l)*oh;
  return h;
}

vec3 qc_normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = qc_height(p + e.xy) - qc_height(p - e.xy);
  n.y = 2.0*e.x;
  n.z = qc_height(p + e.yx) - qc_height(p - e.yx);

  return normalize(n);
}

float psy_noise(vec2 p) {
  float a = sin(p.x);
  float b = sin(p.y);
  float c = 0.5 + 0.5*cos(p.x + p.y);
  float d = mix(a, b, c);
  return d;
}

float psy_fbm(vec2 p, float aa) {
  const mat2 frot = mat2(0.80, 0.60, -0.60, 0.80);

  float f = 0.0;
  float a = 1.0;
  float s = 0.0;
  float m = 2.0;
  for (int x = 0; x < 4; ++x) {
    f += a*psy_noise(p); 
    p = frot*p*m;
    m += 0.01;
    s += a;
    a *= aa;
  }
  return f/s;
}

float psy_warp(vec2 p, out vec2 v, out vec2 w) {
  float id = inner(p); 

  const float r  = 0.5;
  const float rr = 0.25;
  float l2 = length(p);
  float f  = 1.0;

  p   -= eyeRef*pmax(0.0, dot(p, eyeRef), 0.25)*2.0;
  p   -= 0.25*eyeRef;

  f = smoothstep(-0.1, 0.15, id);
  const float rep = 50.0;
  const float sm = 0.125*0.5*60.0/rep;
  float  n = smoothKaleidoscope(p, sm, rep);
  p.y += TIME*0.125+1.5*g_psy_th;

  g_psy_hf = f;
  vec2 pp = p;

  vec2 vx = g_psy_vx;
  vec2 vy = g_psy_vy;

  vec2 wx = g_psy_wx;
  vec2 wy = g_psy_wy;

  //float aa = mix(0.95, 0.25, tanh_approx(pp.x));
  float aa = 0.5;

  v = vec2(psy_fbm(p + vx, aa), psy_fbm(p + vy, aa))*f;
  w = vec2(psy_fbm(p + 3.0*v + wx, aa), psy_fbm(p + 3.0*v + wy, aa))*f;

  return -tanh_approx(psy_fbm(p + 2.25*w, aa)*f);
}

vec3 psy_normal(vec2 p) {
  vec2 v;
  vec2 w;
  vec2 e = vec2(4.0/RESOLUTION.y, 0);

  vec3 n;
  n.x = psy_warp(p + e.xy, v, w) - psy_warp(p - e.xy, v, w);
  n.y = 2.0*e.x;
  n.z = psy_warp(p + e.yx, v, w) - psy_warp(p - e.yx, v, w);

  return normalize(n);
}

vec3 psy_weird(vec2 p) {
  vec3 ro = vec3(0.0, 10.0, 0.0);
  vec3 pp = vec3(p.x, 0.0, p.y);

  vec2 v;
  vec2 w;

  float h  = psy_warp(p, v, w);
  float hf = g_psy_hf;
  vec3  n  = psy_normal(p);

  vec3 lcol1 = lightCol1;
  vec3 lcol2 = lightCol2;
  vec3 po  = vec3(p.x, 0.0, p.y);
  vec3 rd  = normalize(po - ro);

  float diff1 = max(dot(n, lightDir1), 0.0);
  float diff2 = max(dot(n, lightDir2), 0.0);

  vec3  ref   = reflect(rd, n);
  float ref1  = max(dot(ref, lightDir1), 0.0);
  float ref2  = max(dot(ref, lightDir2), 0.0);

  const vec3 col1 = vec3(0.1, 0.7, 0.8).xzy;
  const vec3 col2 = vec3(0.7, 0.3, 0.5).zyx;

  float a = length(p);
  vec3 col = vec3(0.0);
//  col -= 0.5*hsv2rgb(vec3(fract(0.3*TIME+0.25*a+0.5*v.x), 0.85, abs(tanh_approx(v.y))));
//  col -= 0.5*hsv2rgb(vec3(fract(sqrt(0.5)*TIME+0.25*a+0.125*w.x), 0.85, abs(tanh_approx(w.y))));
  col += hsv2rgb(vec3(fract(-0.1*TIME+0.125*a+0.5*v.x+0.125*w.x), abs(0.5+tanh_approx(v.y*w.y)), tanh_approx(0.1+abs(v.y-w.y))));
  col -= 0.5*(length(v)*col1 + length(w)*col2*1.0);
   /*
  col += 0.25*diff1;
  col += 0.25*diff2;
  */
  col += 0.5*lcol1*pow(ref1, 20.0);
  col += 0.5*lcol2*pow(ref2, 10.0);
  col *= hf;

  return max(col, 0.0);
}

float vmax(vec2 v) {
  return max(v.x, v.y);
}

float corner(vec2 p) {
  return length(max(p, vec2(0))) + vmax(min(p, vec2(0)));
}

vec3 skyColor(vec3 ro, vec3 rd) {
  float ld1      = max(dot(lightDir1, rd), 0.0);
  float ld2      = max(dot(lightDir2, rd), 0.0);
  vec3 final     = vec3(0.0);

  rd.xy *= ROT(-1.);
  vec2 bp = rd.xz/max(0.0,rd.y);
  float bd = corner(-bp);
  final += 0.3*exp(-5.0*max(bd, 0.0)); 
  final += 0.20*smoothstep(0.025, 0.0, bd); 

  final += 8.0*lightCol1*pow(ld1, 100.0);
  final += 8.0*lightCol2*pow(ld2, 100.0);

  return final;
}

vec3 eyeColor(vec2 p, vec3 ro, vec3 rd, vec3 po, float od) {
  float aa = 2.0/RESOLUTION.y;
  vec3 sc    = vec3(0.0);
  float sd   = raySphere(ro, rd, vec4(sc, 0.75));
  vec3 spos  = ro + sd*rd;
  vec3 snor  = normalize(spos - sc);
  vec3 refl  = reflect(rd, snor);
  vec3 scol  = skyColor(spos, refl);
  float dif1 = max(dot(snor, lightDir1), 0.0);
  float dif2 = max(dot(snor, lightDir2), 0.0);


  vec3 pcol = psy_weird(p);
  vec3 col1 = vec3(0.0);
  col1 += pcol;
  col1 += scol;
  col1 += 0.025*(dif1*dif1+dif2*dif2);

  vec3 col2 = 0.125*(skinCol1)*(dif1 + dif2)+0.125*sqrt(scol);

  snor.xz *= ROT(-0.5*eyeAngle);
  snor.xy *= ROT(-2.4*smoothstep(0.99, 1.0, sin(TTIME/12.0)));
  float a = atan(snor.y, snor.x);

  vec3 col = mix(col1, col2, step(a, 0.0));

  col *= smoothstep(0.0, -0.1, od);

  return col;
}

vec3 skinColor(vec2 p, vec3 ro, vec3 rd, vec3 po, float od) {
  float lp = length(p);
  float aa = 2.0/RESOLUTION.y;

  float qch = qc_height(p);
  vec3  qcn = qc_normal(p);

  float diff1 = max(dot(qcn, lightDir1), 0.0);
  float diff2 = max(dot(qcn, lightDir2), 0.0);

  vec3  ref   = reflect(rd, qcn);
  vec3  scol  = skyColor(po, ref);

  vec3 lcol1 = lightCol1;
  vec3 lcol2 = lightCol2;
  vec3 lpow1 = 0.25*lcol1;
  vec3 lpow2 = 0.5*lcol2;
  vec3 dm = mix(1.0*skinCol1, skinCol2, 1.0+tanh_approx(2.0*qch))*tanh_approx(-qch*10.0+0.125);
  vec3 col = vec3(0.0);
  col += dm*sqrt(diff1)*lpow1;
  col += dm*sqrt(diff2)*lpow2;

  const float ff = 0.6;
  float f = ff*exp(-2.0*od);

  col *= f;
  col += 0.5*ff*sqrt(scol);
  col -= (1.0-tanh_approx(10.0*-qch))*f;
  col *= smoothstep(0.0, 0.025, od);
  return col;
}

void compute_globals() {

  vec2 vx = vec2(0.0, 0.0);
  vec2 vy = vec2(3.2, 1.3);

  vec2 wx = vec2(1.7, 9.2);
  vec2 wy = vec2(8.3, 2.8);

  vx *= ROT(TTIME/1000.0);
  vy *= ROT(TTIME/900.0);

  wx *= ROT(TTIME/800.0);
  wy *= ROT(TTIME/700.0);

  g_psy_vx = vx;
  g_psy_vy = vy;

  g_psy_wx = wx;
  g_psy_wy = wy;
}

vec3 color(vec2 p) {
  compute_globals();

  float aa = 2.0/RESOLUTION.y;
  float od = outer(p);


  vec3 ro = vec3(0.0, 10.0, 0.0);
  vec3 pp = vec3(p.x, 0.0, p.y);

  vec3 po = vec3(p.x, 0.0, p.y);
  vec3 rd = normalize(po-ro);


  vec3 col = od > 0.0 ? skinColor(p, ro, rd, po, od) : eyeColor(p, ro, rd, po, od); 

  return col;
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/vec3(2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float a = PCOS(TTIME/60.0);
  p *= mix(0.8, 1.2, 1.0-a);
  vec3 col = color(p);

  col = postProcess(col, q);
  fragColor = vec4(col, 1.0);
}

#define PI 3.14159265
#define MAXIT 150
#define EPSILON 0.05
#define STEP 0.5

#define minx4(a, b) ((a.x) < (b.x) ? (a) : (b))
#define minx2(a, b) ((a.x) < (b.x) ? (a) : (b))

#define GROUNDCOL 1.
#define KRAKENCOL 2.
#define TENTACLECOL 3.
#define BEAKCOL 4.
#define EYECOL 5.

mat3 rotX(float a)
{
    return mat3(
        1., 0., 0.,
        0., cos(a), -sin(a),
        0., sin(a), cos(a)
    );
}

mat3 rotY(float a)
{
    return mat3(
        cos(a), 0.0, -sin(a),
        0., 1., 0.,
        sin(a), 0.0, cos(a)
    );
}

mat3 rotZ(float a)
{
    return mat3(
        cos(a), -sin(a), 0.,
        sin(a), cos(a), 0.,
        0., 0., 1.
    );
}

float hash(vec2 r) {
    return fract(sin(dot(r, vec2(15.5921, 96.654654))) * 23626.3663);
}

float t;

float sphere(vec3 r, float a)
{
    return length(r) - a;
}

float beak(vec3 r, float a)
{
	vec3 p = r;
	p.x *= 0.8;
	p.y *= 0.6;

	vec3 b1 = rotZ(PI/4. - 0.4 - abs(sin(t*3.)) * 0.9)*vec3(p.x - p.y * p.y * p.y, p.y, 0.);
	vec3 b2 = rotZ(PI/4. + 0.4 + abs(sin(t*3.)) * 0.9)*vec3(p.x + p.y * p.y * p.y, p.y, 0.);

    return length(p) - a + max(-b1.x-b1.y + 0.12, 0.)*max(-b2.x-b2.y + 0.12, 0.)*10.;
}

float body(vec3 r, float a)
{
	r.y -= abs(r.y)*0.33;
    return length(r) - a;
}

float torus(vec3 r, vec3 a)
{
    vec2 p = vec2(length(r.xz) - a.x, r.y);
    return length(p) - a.y;
}

float plane(vec3 r, vec3 o, vec3 n) {
	r.y += sin(r.x * 0.1) * 2.;
	r.y += cos(r.z * 0.1) * 2.;
	r.y += (sin(r.x)) * 0.2;
	r.y += (sin(r.x) + cos(r.z * 0.5)) * 0.2;

    return dot(r - o, n);
}

float tentacle(vec3 r, vec3 a, float tt)
{
	float d = a.y - r.y;
	r.x += sin(r.x * 0.2 + r.y * 0.5 + tt) * d * 0.3;
	r.z += cos(r.z * 0.1 + r.y * 0.8 + tt) * d * 0.3;
	a.z = clamp(a.z - d * a.z / a.y / 2., 0., a.z);

	vec2 p = abs(vec2(length(r.xz), r.y)) - a.xy;
	p.x = abs(p.x) - a.z;

	return min(max(p.x, p.y), 0.0) + length(max(p, 0.));
}

float box(vec3 r, vec3 a)
{
    vec3 p = (abs(r) - a);

    return length(max(p + 0.1, 0.));
}

float shade(vec3 n, vec3 rd)
{
    return clamp(max(dot(n, -rd), 0.) + 1., 0., 1.);
}

vec3 fog(float z, vec3 col, vec3 fogCol)
{
    return mix(fogCol, col, exp(-z));
}

vec3 matCol(vec4 o)
{
	vec2 cc = o.zw;

    if (o.y == GROUNDCOL) {
	    cc.x += cos(.3*t) * 2.;
	    cc.y += sin(.3*t) * 2.;
        return normalize(vec3(0.8 + sin(cc.x + cc.y) * 0.05, 0.5 + sin(cc.x) * 0.1, 0.0));
    }

    if (o.y == KRAKENCOL) {
	    float fade = cos(clamp(cc.y*1.2, 0., PI/2.));
	    vec3 top = normalize(vec3(0., 0.9, 0.)) * (sin(cc.x * 6.) * fade * 0.6 + 0.4);
	    float bfade = clamp(-cc.y+.3, 0., 1.);
	    vec3 bot = vec3(0.7, 0.3, 0.);
	    return top * (1.-bfade) + bot * bfade;
    }

    if (o.y == TENTACLECOL) {
	    float fade = clamp(abs(cc.y), 0., PI/2.);
	    vec3 top = normalize(vec3(0., 0.9, 0.)) * ((fade) * 0.8) * 0.5;
	    return top;
    }

    if (o.y == EYECOL) {
	    float fade = cos(clamp(cc.y*10., 0., PI/2.));

	    float pupil = smoothstep(-0.6,-0.5, cc.y);
	    float lid = smoothstep(0.0,-0.2, cc.y);
	    vec3 top = vec3(1.2, 1.2, 0.) * (cos(cc.x * 6.) * fade * 0.6 + 0.4) + vec3(0.9, 1., 0.);

	    return top * pupil * lid + vec3(0.2, 0.0, 0.2) * (1. - pupil) + vec3(0.5, 0.8, 0.) * (1.-lid);
    }

    if (o.y == BEAKCOL) {
	    float fade = clamp(abs(sin(cc.y * cc.y)), 0., PI/2.);
	    vec3 top = normalize(vec3(1.7, 1., 0.8)) * (fade * 0.6 + 0.4);
	    return top * fade;
    }

    return normalize(vec3(0.8, 0.2, 0.));
}

mat3 obj;

vec4 kraken(vec3 r, float tt)
{
	vec3 br = r * rotY(0.7) * rotX(-PI / 2.);
	br *= rotY((sin(t) + t * 1.5) * 0.5);
	vec2 bc = vec2(atan(br.x, br.z), br.y / 3.);

	vec4 bd = vec4(
		body(br, 3.), matCol(vec4(0., KRAKENCOL, bc))
	);

	vec4 td = vec4(1000., 0., 0., 0.);
	for (int a = 0; a < 6; ++a) {
		float aa = float(a) - PI / 4.;
		float split = (floor(float(a) / 3.) * 2. - 1.) * 0.3;
		td = minx4(td, 
            vec4(tentacle(br + 1.7 * vec3(cos(float(aa)) - split, 1.8, sin(float(aa))),
                vec3(.01, 2., .6), tt * 0.5), 
            matCol(vec4(0., TENTACLECOL, bc))
		));
	}

	vec3 er = br + vec3(0., 2., -1.5);
	float eax = sin(t * 2.) * 0.4;
	float eaz = cos(t * 2.) * 0.4;
	er *= rotX(eax * sin(t * 0.71)) * rotZ(eaz * sin(t * 0.54));
	vec2 ec = vec2(atan(er.x, er.z), er.y);
	vec4 ed1 = vec4(
		sphere(er, 0.6), matCol(vec4(0., EYECOL, ec))
	);
	er = br + vec3(0., 2., 1.5);
	er *= rotX(-eax * cos(t * 0.32)) * rotZ(-eaz * sin(t * 0.76));
	ec = vec2(atan(er.x, er.z), er.y);
	vec4 ed2 = vec4(
		sphere(er, 0.6), matCol(vec4(0., EYECOL, ec))
	);
	vec4 ed = minx4(ed1, ed2);

	vec3 kr = br + vec3(0., 2.3, 0.);
	vec4 kd = vec4(
		beak(kr, 0.6), matCol(vec4(0., BEAKCOL, bc))
	);

	return minx4(minx4(minx4(bd, td), ed), kd);
}

vec4 map(vec3 r)
{
	float tt = t * 10. - sin(t) * 5.;

	r *= rotY(sin(t * 0.5));

	vec4 kd = kraken(r, tt);

	vec3 gr = r;
	gr.x += tt * 0.4;
	gr.z += tt;
	gr.y += 7.;

	vec4 gd = vec4(
		plane(gr, vec3(0., 0., 0.), vec3(0., 1., 0.)), matCol(vec4(0., GROUNDCOL, gr.xz))
	);

	return minx4(gd, kd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    t = iTime;

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    mat3 cam = rotY(-PI) * rotX(0.3);

    vec3 ro = vec3(0., 2.0, -10.0);
    vec3 rd = cam * normalize(vec3(uv * 2., -1.));
    vec3 r = ro;

    vec3 bcol = vec3(0.2, 0.7, 0.8) * 1.1;
    vec4 col = vec4(0.);
    col.rgb = bcol;

    float sh = 1.;

    float glow = 0.;

    int ch = 1;

    for (int i = 0; i < MAXIT; ++i) {
        vec4 d = map(r);
        float z = length(r - ro);

        glow += exp(-d.x);

        if (d.x < EPSILON) {
            col.rgb = mix(col.rgb, d.yzw,
                shade(normalize(r), rd));

            col.rgb = fog(z * 0.1, col.rgb, bcol);
            break;
        }

        d.x *= 0.8 - 0.2 * hash(uv);
        r += rd * d.x * STEP;

        sh = (float(i) / float(MAXIT));
    }

    if (sh < 0.5)
        col.rgb *= clamp(exp(-sh * 2.0 + 1.0), 0., 1.);

    fragColor = vec4(col.rgb, 1.);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


//increase this constant to increase the amount of detail
#define OCTAVES 4

float fbm(in vec2 uv)
{
    //this function generates the terrain height
    float value = 0.;
    float factor = -3.;
    uv *= factor;
    for (int i = 0; i < OCTAVES; i++)
    {
        uv += vec2(sin(uv.x*factor)/factor,cos(uv.y/factor)*factor).yx;
        value = 1.+max(value+value*sin(uv.x/factor)/factor,value+cos((uv.y/factor)/factor)/factor);
        uv += max(uv,uv.yx);
        uv /= factor/1.5;
        if(i%2 == 0){
           uv = -uv.yx;
        }

        //factor /= 1.5;

    }

    return value;
}


struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 1.5;
    return fbm(uv/factor);
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*(iTime+11.));
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    #endif
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 1.;

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

