#define WHITE vec3(255,255,255)/255.
#define BLUE vec3(0,56,184)/255.
#define PI 3.14159265359
#define S smoothstep 



float StarOfDavid(vec2 uv, float size){

    uv *= size;
    uv.x = abs(uv.x);
    uv.y *= 1.4;
    float a = 1.08;
    float a2 = 4.286389;
    float a3 = 2.03;

    float offs = .1;
    float d1 = S(-.501,-.50,dot(uv , vec2(0.,1.05 - offs)));
    float d2 = S(.541,.54,dot(uv , vec2(sin(a),cos(a))));

    float d11 = S(-.001-offs,-.00-offs,dot(uv , vec2(0.,0.4 - offs)));
    float d12 = S(.501-offs,.50-offs,dot(uv, vec2(sin(a),cos(a))));

    float d3 = S(.501,.50,dot(uv, vec2(sin(a3),cos(a3))));
    float d4 = S(.501,.50,dot(uv , vec2(0.,.8)));

     float d21 = S(.001+offs,.00+offs,dot(uv , vec2(0.,0.33 - offs)));
     float d22 =  S(.251+offs,.250+offs,dot(uv, vec2(sin(a3),cos(a3))));

    float m1 = min(d1,d2);

    float m11 = min(d11,d12);
    m1 -= m11;


    float m2 = min(d4,d3);

    float m21 = min(d21,d22);
    m2 -= m21;

    //if(uv.x>=0. && uv.x <= .01)col = vec3(1.,0.,0.);
     //if(uv.y>=0. && uv.y <= .01)col = vec3(1.,0.,0.);
     float s = max(m1, m2);

  return s; 
   }


   vec3 Flag(vec2 uv){
    //blue stripes
    uv *= 1.5;
    vec2 st = uv;
    uv.x *= 1.2;
    st.y = abs(st.y);


    float s = S(0.41,.4,abs(st.y));
    s  -= S(0.26,.25,abs(st.y));
    vec3 nCol = mix(WHITE,BLUE,s);
    vec3 col = vec3(nCol);


    float star = StarOfDavid(uv,4.);
    vec3 cstar =  mix(col, BLUE,star);


    cstar *= S(0.01,0.,abs(uv.y)-.5+0.012);
     //cstar *= S(0.01,0.,abs(uv.y)-.5+0.01);
    return cstar;

    }
