precision highp float;

// Change OBJECTS_NUMBER up to 6 to add more objects, but making the app heavier
#define OBJECTS_NUMBER 6

// Turn REFLECTIONS off by setting the value to zero
#define REFLECTIONS 1

#define MAX_STEPS 150
#define MAX_DIST 75.0
#define SURF_DIST 0.01
#define PI 3.14159265359
#define ZERO 0
#define FOV 0.55
#define ORBIT 6.0
#define T iTime * 0.15
#define S smoothstep

mat2 rotate( float a ){ float s = sin( a ), c = cos( a ); return mat2( c, -s, s, c ); }
float hash21( vec2 seed ){ return fract( sin( dot( seed * .1684,vec2( 54.649, 321.547 ) ) ) * 450315. ); }
float smin( float a, float b, float k ){ float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 ); return mix( b, a, h ) - k*h*(1.0-h) + 0.01; }
float ssmin( float a, float b, float k ){ float h = S( 0.275, 0.725, 0.5+0.5*(b-a)/k ); return mix( b, a, h ) - h*(1.0-h); }
float smax( float a, float b, float k ){ return log( exp( k * a ) + exp( k * b ) ) / k; }

struct Mat {
    vec3   albedo;          // RGB color
    float  reflectivity;    // Reflection strength
    vec2   specular;        // Specular highlight : Smoothstep min(x) and max(y)
    vec4   fresnel;         // Used for inner glow. 'w' Is used for application strength
    vec4   emissive;        // If this value is > 0.0 it will be re-applied after diffuse
};

struct Solution {
    float d; // Distance to the scene
    Mat m; // Material properties
};


vec3 black   = vec3( 0.0, 0.0, 0.0 );
vec3 white   = vec3( 1.0, 1.0, 1.0 );
vec3 red     = vec3( 0.9, 0.1, 0.2 );
vec3 blue    = vec3( 0.2, 0.1, 0.9 );
vec3 yellow  = vec3( 1.0, 0.6, 0.2 );
vec3 green   = vec3( 0.05, 0.6, 0.3 );



Mat GetMat( float m, vec3 p ){

    Mat mat;

    if( m == 0.0 ){ // B L A C K
        mat.albedo = black;
        mat.reflectivity = 0.0;
        mat.specular = vec2( 0.0, 1.0 );
        mat.fresnel = vec4( black, 0.0 );
        mat.emissive = vec4( black, 0.0 );
        return mat;
    }
    if( m == 1.0 ){ // B L U E
        mat.albedo = mix( black, blue, 0.5 );
        mat.reflectivity = 0.0;
        mat.specular = vec2( 0.5, 0.9 );
        mat.fresnel = vec4( mix(blue, white, 0.25), 0.75 );
        mat.emissive = vec4( blue, 0.0 );
        return mat;
    }
    if( m == 2.0 ){ // R E D
        mat.albedo = mix( red, yellow, 0.2 );
        mat.reflectivity = 0.0;
        mat.specular = vec2( 0.8, 0.9 );
        mat.fresnel = vec4( yellow, 0.0 );
        mat.emissive = vec4( mix( red, blue, 0.15 ), 0.15 );
        return mat;
    }
    if( m == 3.0 ){ // M I R R O R 
        mat.albedo = black;
        mat.reflectivity = 1.0;
        mat.specular = vec2( 0.92, 0.95 );
        mat.fresnel = vec4( white, 0.25 );
        mat.emissive = vec4( black, 0.0 );
        return mat;
    }
    if( m == 4.0 ){ /// G R E E N
        mat.albedo = mix( green, yellow, 0.2 );
        mat.reflectivity = 0.0;
        mat.specular = vec2( 0.0, 1.5 );
        mat.fresnel = vec4(  mix(green, blue, 0.2), 0.3 );
        mat.emissive = vec4( mix(green, blue, 0.1), 0.2 );
        return mat;
    }
    if( m == 5.0 ){ /// W H I T E
        mat.albedo = white;
        mat.reflectivity = 0.3;
        mat.specular = vec2( 0.0, 1.0 );
        mat.fresnel = vec4( white, 0.05 );
        mat.emissive = vec4( white, 0.6 );
        return mat;
    }
    if( m == 6.0 ){ /// Y E L L O W
        mat.albedo = mix( black, blue, 0.15 );
        mat.reflectivity = 0.0;
        mat.specular = vec2( 0.2, 0.9 );
        mat.fresnel = vec4( red, 0.0 );
        mat.emissive = vec4( yellow, 2.0 );
        return mat;
    }

}

Mat MixMat( Mat m1, Mat m2, float k, vec3 p ){

    Mat newMat;

    newMat.albedo       = mix( m1.albedo, m2.albedo, k );
    newMat.reflectivity = mix( m1.reflectivity, m2.reflectivity, k );
    newMat.specular     = mix( m1.specular, m2.specular, k );
    newMat.fresnel      = mix( m1.fresnel, m2.fresnel, k );
    newMat.emissive     = mix( m1.emissive, m2.emissive, k );

    return newMat;

}


// S D F
float sdSphere( vec3 p, float s ){
    return length( p ) - s;
}
float sdBox( vec3 p, vec3 b ){
    vec3 q = abs( p ) - b;
    return length( max( q,0.0 ) ) + min( max( q.x, max( q.y,q.z ) ), 0.0 );
}
float sdTorus( vec3 p, vec2 t ){
    vec2 q = vec2( length( p.xz ) - t.x, p.y );
    return length( q ) - t.y;
}
float sdRoundedCylinder( vec3 p, float ra, float rb, float h ){
    vec2 d = vec2( length( p.xz ) - 2.0 * ra + rb, abs( p.y ) - h );
    return min( max( d.x, d.y ), 0.0 ) + length( max( d, 0.0 ) ) - rb;
}


// M A P
Solution map( vec3 p ){

    float d  = 9e9;
    float d2 = 9e9;
    Mat m = GetMat( 0.0, p );
    float t = 0.0;
    float k = 0.0;


// O B J E C T S

    float amp = 1.0 / length(p) * 20.;
    float offset = T*50.0;
    float dots = ( cos( p.x * amp ) +
                   cos( p.y * amp + offset ) + 
                   cos( p.z * amp ) ) / amp;

    // Center sphere
    float sphere = sdSphere( p, 1.25);
    Mat sM = GetMat( 4.0, p );

    // Perforated cube
    vec3 cP = p;
         cP.yz *= rotate( T * 1.05);
         cP.xz *= rotate( -T * 0.93 );
         cP += vec3( sin( T * 0.2 ) * 3.25, 0.0, cos( T * 0.2 ) * ( 3.0 + sin(T) ) );
    float cube = sdBox( cP, vec3( 1.0, 0.8, 1.0 ) ) - 0.2;
    float cubeS = sdBox( cP, vec3( 0.4, 1.5, 0.4 ) );
    float sphereS = sdSphere( p, 2.0 );
          cube = max( cube, -cubeS );
    Mat cM = GetMat( 2.0, p );

    // Pole / french fry
    float pole = sdBox( cP + vec3( 0.0, sin(T*5.0)*1.15, 0.0), vec3( 0.3, 0.5, 0.3 ) )-0.1;
    Mat poleM = GetMat( 6.0, p );

    vec3 tP = p - vec3( 0.0, sin(T/2.0)*3.35, 0.0 );
         tP.xy *= rotate(T/2.0);
         tP.xz *= -rotate(T/2.0);
    float torus = sdTorus( tP, vec2( 1.65, 0.5 ) );
    Mat tM = GetMat( 3.0, p ); 

    // Cylinder
    vec3 cylP = p - vec3( sin(T)*2.5, sin(-T)*3.0, cos(T)*2.5 );
         cylP.xy *= rotate(  T );
         cylP.xz *= rotate( -T * 1.4 );
         cylP.yz *= rotate(  T * 3.1 );
    Mat cylM = GetMat( 1.0, p );
    float cylT = S( 0.49, 0.51, T );
    float cyl = sdRoundedCylinder( cylP, 0.75, 0.15, 0.3 );

    // Plus
    vec3 plusP = p - vec3( sin(T*0.457)*6.73, 0.0, 0.0 );
         plusP.xy *= rotate(sin(T*0.457)*6.73);
    float shield = sdSphere( plusP, 2.0 );
    float cubeP = sdBox( plusP, vec3( 0.75, 0.25, 0.25 ) ) ;
    float cubeM = sdBox( plusP, vec3( 0.25, 0.25, 0.75 ) ) ;
    float plus = min( cubeM, cubeP)-0.1;
    Mat plusM = GetMat( 5.0, p );



// B O O L

    if( OBJECTS_NUMBER > 0 ){
        d = min( d, sphere ); // Center sphere
        k = d < sphere ? 0.0 : 1.0;
        m = MixMat( m, sM, k, p );
    }

    if( OBJECTS_NUMBER > 1 ){
        d = min( d, torus ); // Torus
        k = d < torus ? 0.0 : 1.0;
        m = MixMat( m, tM, k, p );
    }

    if( OBJECTS_NUMBER > 2 ){
        d = smin( d, cyl, 2.25 ); // Cylinder
        t = d + cyl;
        k = S( 0.0, 1.0, 1.0 - ( t - d ) * 0.75); // 0.35
        m = MixMat( m, cylM, k, p );
    }

    if( OBJECTS_NUMBER > 3 ){
        d = ssmin( d, cube, 0.85 ); // Perforated cube
        t = d + cube;
        k = S( 0.4, 0.6, 1.0 - ( t - d ) * 1.4 );
        m = MixMat( m, cM, k, p );
    }
    if( OBJECTS_NUMBER > 4 ){
        d2 = min( d2, pole ); // Pole / french Fry
        t = d2 + pole;
        k = S( 0.0, 1.0, 1.0 - ( t - d2 ) * 0.65 );
        m = MixMat( m, poleM, k, p );
    }
    if( OBJECTS_NUMBER > 5 ){
        d = smax( d, -shield, 5.0 ); // Shield
        d2 = smin( d2, plus, 2.0 ); // Plus
        t = d2 + plus;
        k = S( 0.0, 1.0, 1.0 - ( t - d2 ) * 2.0 );
        m = MixMat( m, plusM, k, p );
    }


// O U T

    Solution so;
    so.d = min(d, d2);
    so.m = m;

    return so;

}


// M A R C H
Solution RayMarch( vec3 ro, vec3 rd ){

    float d = 0.0; // Distance total
    Solution dS;

    for( int i = 0; i < MAX_STEPS; i++ ){

    	vec3 p = ro + rd * d;
             dS = map( p ); // Distance per step
             d += dS.d;

        if( abs( dS.d ) < SURF_DIST || d > MAX_DIST ){
            break;
        }

    }

    dS.d = d;
    return dS;

}

vec3 GetNormal( vec3 p ){

	float d = map( p ).d;
    vec2 e = vec2( .01, 0 );
    vec3 n = d - vec3(
        map( p-e.xyy ).d,
        map( p-e.yxy ).d,
        map( p-e.yyx ).d );

    return normalize( n );

}

float calculateObstruction( vec3 p, vec3 lp, float lrad ){

    float h = hash21( vec2( lp.x, p.y ) ) * 0.1;
    vec3 toLight = normalize( lp - p );
    float distToLight = length( lp - p );
    float d; 
    float t = lrad * h;
    float obstruction = 0.0;
    for( int j=0; j<128; j++ ){

        d = map( p + t * toLight ).d;
        obstruction = max( 0.5 + ( -d ) * distToLight / ( 2.0 * lrad * t ), obstruction );
        if( obstruction >= 1.){ break; }
        t += max( d, lrad * 0.1 / distToLight );
        if( t >= distToLight ) break;

    }
    float obs = clamp( S(0.0, 1.0, obstruction ), 0., 1.);
    return obs;

}

vec3 GetSpecular( vec3 p, vec3 r, Mat m, vec3 lp ){

    float spec = max( 0.0, 0.5 * dot( r, normalize( lp - p ) ) + 0.45 );
          spec = S( m.specular.x, m.specular.y, spec );
    float obstruction = calculateObstruction( p, lp, 2.0 );

    float spec_o = 1.0 + abs( m.specular.x - m.specular.y );
          spec_o = 1.0 / ( spec_o * spec_o * spec_o );

    spec *= 1.0 - obstruction;
    vec3 level = spec * mix( m.albedo, white, spec_o );
         level.r = S(0.0, 1.0, level.r);
         level.g = S(0.1, 0.9, level.g);
         level.b = S(0.2, 0.8, level.b);
return level;
}


vec3 GetDiffuse( vec3 p, vec3 n, vec3 lp ){

    float lightRadius = 2.0;
    float lightStrength = 5.0;

    float obstruction = calculateObstruction( p, lp, lightRadius );
    vec3  toLight = normalize( lp - p );
    float distToLight = length( lp - p);
    float diffuse = max( dot( n, toLight ), 0. ) /  distToLight * lightStrength;

    vec3 dif = vec3( diffuse * ( 1.0 - obstruction ));
    return dif;

}

float GetFresnel( vec3 rd, vec3 n ){
    float e = dot( rd, n );
    e = 0.5 + e * 0.5;
    float e2 = S( 0.2, 0.6, e );
    return S( 0.0, 1.0, e+e2 );
}

float GetAO( in vec3 p, in vec3 n ){
	float occ = 0.0;
    float sca = 1.0;
    for( int i = ZERO; i < 5; i++ ){
        float h = 0.0075 + 0.1 * float( i ) / 4.0;
        float d = map( p + h * n ).d;
        occ += ( h - d ) * sca;
        sca *= 0.95;
        if( occ > 0.3 ) break;
    }
    return 1.0 - occ;
}

vec3 GetReflection( vec3 inp, vec3 rd, vec3 inn, vec3 lp ){

    vec3 r = reflect( rd, inn );
    vec3 ro = inp + ( SURF_DIST * 2. ) * r;
    Solution so = RayMarch( ro, r );

    vec3 col;
    vec3 p = ro + r * so.d;
    vec3 n = GetNormal( p );
    float angle = abs( dot( r, n ) );

    if( so.d < MAX_DIST ){

        // A L B E D O 
        col = so.m.albedo;

        // D I F F U S E
        vec3 dif = GetDiffuse( p, n, lp );
        col *= dif;

        // E M I S S I V E
        col = mix( col, so.m.emissive.rgb, so.m.emissive.a );

        // I N N E R   G L O W   /   F R E S N E L
        if( so.m.fresnel.a > 0.0 ){
            float fresnel = GetFresnel( r, n );
            col += so.m.fresnel.rgb * so.m.fresnel.a * fresnel;
        }

        // S P E C 
        vec3 spec = GetSpecular( p, r, so.m, lp );
        col += spec * angle;

    } else { col = black; }

    return col;

}

vec3 GetRayDir( vec2 uv, vec3 ro, vec3 ta, float fov ){
    vec3 f = normalize( ta - ro ),
         r = normalize(cross( vec3( 0, 1, 0 ), f )),
         u = cross( f, r ),
         c = f * fov, 
         i = c + uv.x * r + uv.y * u,
         d = normalize( i );
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = ( fragCoord - 0.5 * iResolution.xy ) / iResolution.y; 
    vec2 m  = iMouse.xy / iResolution.xy;
    vec2 mn = vec2( ( m +0.5 ) * 2.5 );

    vec3 ro = vec3( 0.0, 0.0, -6.5 );
         ro.yz *= rotate( 4.0 * mn.y ), ro.xz *= rotate( -4.0 * mn.x );

    vec3 ta_wiggle = vec3( sin(T * 0.36 ) * 0.17, sin(T * 2.7 ) * 0.2, cos(T) * 0.23 );
    vec3 ta = ta_wiggle * 0.5;


    vec3 rd = GetRayDir( uv, ro, ta, FOV );

    // Pointlight position
    vec3 lp = vec3( sin( T *3.) * ORBIT, 
                    3.0, 
                    cos( T *3.) * ORBIT );

    vec3 col = black;


    Solution so = RayMarch( ro, rd );
    vec3 p = ro + rd * so.d;
    vec3 n  = GetNormal( p );
    vec3 r  = reflect( rd, n );
    float angle = S( 0.01, 0.25, abs( dot( rd, n ) ) );

    if( so.d < MAX_DIST ){

        // A L B E D O 
        col = so.m.albedo;

        // D I F F U S E
        vec3 dif = GetDiffuse( p, n, lp );
        col *= dif;

        // E M I S S I V E
        col = mix( col, so.m.emissive.rgb, so.m.emissive.a );

        // R E F L E C T I O N
        if( REFLECTIONS > 0 ){
            if( so.m.reflectivity > 0.0 ){
                vec3 ref = GetReflection( p, rd, n, lp );
                col = mix( col, col+ref, so.m.reflectivity );
            }
        }
        // I N N E R   G L O W   /   F R E S N E L
        if( so.m.fresnel.a > 0.0 ){
            float fresnel = GetFresnel( rd, n );
            col += so.m.fresnel.rgb * so.m.fresnel.a * fresnel * angle;
        }

        // S P E C 
        vec3 spec = GetSpecular( p, r, so.m, lp );
        col += spec * angle;

        // A O 
        float ao = GetAO( p, n );
        col *= S(0.0, 1.2, ao*ao );


    } else { col = black; }

    fragColor = vec4( col, 1.0 );


}

float checker(in vec2 st)
{
  const float checkSize = 3.0;
  vec2 tile = abs( mod(checkSize * st, 2.) - 1.);
  tile = smoothstep( checkSize*length(fwidth(st)),0.,tile-.5);
  tile = tile*2.-1.;
  return tile.x*tile.y *.5 + .5;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Set coordinate system from -1.5 to 1.5 on y-axis
    // dist 1.0 is where distance will go to infinite in hyperbolic space.
    vec2 uv = 1. * ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    uv = rotate2d(iTime/5.) * uv;

    //Transfrom to hyperbolic space.
    if (length(uv) > .5) {
        fragColor = vec4(vec3(1),1.);
        return;
    }
    uv /=  (1. / (length(uv) + .5) ) - 1.;
    uv += vec2(iTime, 2.*sin(iTime/3.));
    fragColor = vec4(vec3(checker(uv)),1.);
}

const uint GLYPH_SIZE = 5u;
const uint GLYPH_SEP = 2u;

// https://nullprogram.com/blog/2018/07/31/
uint triple32(uint x) {
    x ^= x >> 17;
    x *= 0xed5ad4bbU;
    x ^= x >> 11;
    x *= 0xac4c1b51U;
    x ^= x >> 15;
    x *= 0x31848babU;
    x ^= x >> 14;
    return x;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    uvec2 cellIndex = uvec2(fragCoord / 5.);

    uint colIndex = uint(float(cellIndex.x) / float(GLYPH_SIZE + GLYPH_SEP));


    cellIndex.y += uint(iTime * (float(triple32(colIndex)) / float( 0xffffffffU ) * 10. + 5.));

    vec3 col = vec3(0.);

    vec2 glyphCellIndex = mod(vec2(cellIndex), vec2(float(GLYPH_SIZE + GLYPH_SEP)));

    if(!(glyphCellIndex.x < float(GLYPH_SEP) || glyphCellIndex.y < float(GLYPH_SEP))) {
        col = step(0.5, float(triple32(cellIndex.x + triple32(cellIndex.y))) / float( 0xffffffffU )) * vec3(0., 1., 0.);
    }

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}

