float PipeDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 rp;
  float d;
  dMin /= szFac;
  p /= szFac;
  p.x += 1.;
  q = p;
  d = SmoothMax (abs (PrCapsDf (q.xzy, 0.55, 0.25)) - 0.06, q.y - 0.5, 0.05);
  q.y -= smoothstep (0.5, 2.5, q.x) - 0.5;
  q.x -= 1.3;
  rp = vec2 (0.1, 0.17) - vec2 (0.05, 0.06) * (q.x / 0.6 - 1.);
  d = 0.5 * SmoothMin (d, max (SmoothMin (PrEllCylDf (q.yzx, rp, 1.2),
     PrEllCylDf (q.yzx - vec3 (0., 0., 1.2), rp + 0.007, 0.007), 0.05),
     0.03 - length (q.yz * vec2 (1.1, 0.35))), 0.12);
  DMIN (idPipe);
  q = p;
  q.y -= 0.3;
  d = PrCylDf (q.xzy, 0.5, 0.01);
  DMIN (idCoal);
  return szFac * dMin;
}

vec3 DodecSym (vec3 p)
{
  vec2 csD;
  csD = sin (0.5 * atan (2.) + vec2 (0.5 * pi, 0.));
  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), csD);
  p.xy = Rot2D (p.xy, -0.1 * pi);
  p.x = - abs (p.x);
  for (int k = 0; k <= 3; k ++) {
    p.zy = Rot2Cs (p.zy, vec2 (csD.x, - csD.y));
    p.y = - abs (p.y);
    p.zy = Rot2Cs (p.zy, csD);
    if (k < 3) p.xy = Rot2Cs (p.xy, sin (-2. * pi / 5. + vec2 (0.5 * pi, 0.)));
  }
  p.xy = sin (mod (atan (p.x, p.y) + pi / 5., 2. * pi / 5.) - pi / 5. +
     vec2 (0., 0.5 * pi)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, a1, a2, s;
  dMin = dstFar;
  dMin = PipeDf (p, dMin);
  q = DodecSym (p);
  a1 = 0.5 * acos (-1. / sqrt (5.));
  a2 = 0.5 * acos (- (80. + 9. * sqrt (5.)) / 109.);
  s = abs (dot (q.yz, sin (a1 - a2 + vec2 (0., 0.5 * pi))) + 2.);
  d = SmoothMax (s - 0.07, min (dot (q.yz, sin (a1 - pi + vec2 (0., 0.5 * pi))),
     dot (q.xy, sin (pi / 5. + vec2 (0.5 * pi, 0.)))) - 0.04, 0.05);
  DMIN (idEdge);
  d = s - 0.05;
  DMIN (idFace);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 100; j ++) {
