mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }

float _cube(vec3 p, vec3 s)
{
    vec3 l =abs(p)-s;
    return max(l.x, max(l.y, l.z));
}

float _cucube(vec3 p, vec3 s)
{
    vec3 l = abs(p)-s;
    float cube = max(max(l.x, l.y), l.z);
    l = abs(l)-0.01;
    float x = max(l.y, l.z);
    //l.xy *= r2d(iTime*.5+l.y);
float y = max(l.x, l.z);
    float z = max(l.x, l.y);

    float cucube = max(min(min(x, y), z), cube);
    return cucube;
}

float map(vec3 p)
{
p.xy += vec2(sin(iTime), cos(iTime*.5))*.5;
p*=2.+sin(iTime*.5)*.2;
    vec3 pouter = p;
    float acc = 1000.;
    for (int i = 0; i < 5; ++i)
    {
        pouter.xy *= r2d(-iTime*.2);
        pouter.yz *= r2d(iTime*.25);
        acc = min(acc, _cucube(pouter, vec3(.4+float(i+1)*.07)));
    }

    p.xy *= r2d(iTime);
    p.xz *= r2d(iTime*.5);
	return acc;//min(acc,_cube(p, vec3(.5)));
}

#define sat(a) clamp(a, 0., 1.)
vec3 accCol;
float rayMarch(in vec3 ro, in vec3 rd, in float offT)
{
  float t = offT;
  for (int i = 0; i < 200; i++)
  {
      vec3 p = ro + rd * t;
    float h = map(p);
    t += h*.5;
    if (h < 0.001 || t > MAX_DIST)
        break;
        accCol += vec3(sin(p.z*3.+iTime)*.5+.5,.3+.3*sin(p.x),.3)*(1.-sat(h/.25))*.2;
  }
  return t;
}

#define EPS  0.01

#if BIASED_NORMAL
vec3 calcNormal(vec3 pos)
{
    float	ref;
    float	trans;
    vec3	absorb;
    vec3	col;
    vec2	eps = vec2(EPS, 0);
	float d = map(pos);
	return normalize(vec3(map(pos + eps.xyy) - d, map(pos + eps.yxy) - d, map(pos + eps.yyx) - d));
}
#else
vec3 calcNormal( in vec3 pos )
{
    const float ep = EPS;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}
#endif

vec3 applyFog(vec3 rgb,float distance, vec3 fogColor)
{
    float fogAmount = 1.0 - exp( -distance*0.2 );
    return mix( rgb, fogColor, fogAmount );
}


vec3 Render(in vec3 ro, in vec3 rd)
{
    accCol = vec3(0.);
    float t = rayMarch(ro, rd, 0.);
    vec3	col = vec3(0);

    vec2 fakeuv = rd.xy-vec2(-1.,1.)*.5+vec2(sin(iTime), cos(iTime))*.1;
    col = vec3(.34, .2, .3)*pow(length(fakeuv), 5.)*texture(iChannel1, vec2(atan(fakeuv.y, fakeuv.x)*.1, length(fakeuv.xy*.01)-.02*iTime)).xxx;
    if(t < MAX_DIST)
    {
        vec3 p = ro + rd * t;
        vec3 n = calcNormal(p);
        vec3 lpos = vec3(5.);
        vec3 ldir = lpos-p;
        //col = texture(iChannel0, reflect(rd, n)).rgb;
        col = vec3(.05)
        +sat(dot(n, ldir))*vec3(.1,.2,.4)*(length(ldir)/4.)*.0
        +pow(sat(dot(n, normalize(ldir+rd))),.2)*vec3(.1,.2,.3);

    }
    col += accCol;
    return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{
    fragColor = vec4(Render(ro + vec3(0, 0, 9), rd), 1);
}


