vec3 render( in vec2 px )
{
    float s = exp2( -(1.0+cos(6.283185*iTime/20.0)) );
    uvec2 p = uvec2( abs(floor((px-iResolution.xy*0.5)*s)) );

    const uint S = uint(INTS_PER_PIXEL);

    float f = 0.0;
    for( uint j=0u; j<S; j++ )
    for( uint i=0u; i<S; i++ )
    {
        uvec2 q = S*p + uvec2(i,j);
        uint h2 = q.x*q.x + q.y*q.y;

        #if USE_EXACT_SQRT==0
        uint h = uint(round(sqrt(float(h2))));
        #else
        uint h = intsqrt(h2);
        #endif

        if( h*h==h2 ) { f = 1.0; break; }
    }

    return vec3(0.5*f,f,0.0);
}


// --------------------------------------
// Integer square root
// https://en.wikipedia.org/wiki/Integer_square_root
// --------------------------------------
#if USE_EXACT_SQRT==1
uint intsqrt( uint s )
{
	uint x0 = s >> 1;
    if( x0==0u ) return s;
    uint x1 = ( x0 + s / x0 ) >> 1;
    while( x1 < x0 )
    {
        x0 = x1;
        x1 = ( x0 + s / x0 ) >> 1;
    }
    return x0;
}
#endif



