const vec3 lmd=vec3(680,550,440);
const vec3 invplmd=1./pow(lmd,vec3(4.));
const float P=1e6*550.1;//scattering magic coeff
const vec3 PC=vec3(0,0,0);//planet center
const float R=7000.;//planet radius
const float H=60.;//atmosphere thick
const float IS=0.9;//sun intensity
float iS(vec3 ro,vec3 rd,vec4 s)
{
    vec3 co=ro-s.xyz;
    float b=2.*dot(co,rd),c=dot(co,co)-s.w*s.w;
    float delta=b*b-4.*c;
    if(delta<0.)
        return -1.;
    if(c<0.)
        return (-b+sqrt(delta))/2.;
    if(b>0.)
        return -1.;
    return (-b-sqrt(delta))/2.;
}
float density(float h)
{
    return exp(-h/H);
}
//Rayleigh's phase function
float pf(float cosTheta)
{
    return (1.+cosTheta*cosTheta);
}
//opticalDepth
float od(vec3 from,vec3 to,float steps)
{
    vec3 dir=to-from;
    float l=length(dir),ds=l/steps;dir/=l;
    float sum=0.;
    vec3 p=from+dir*(.5)*ds;//sample seg middle point
    for(float i=0.;i<steps;i+=1.)
    {
        sum+=density(length(p-PC)-R)*ds;
        p+=dir*ds;
    }
    return sum;
}
void mainImage( out vec4 O, in vec2 C)
{
    vec2 uv=(C-.5*iR)*2./iR.y;uv.y+=.8;
    vec3 ro=PC+vec3(0,R+1.,0),
         rd=normalize(vec3(uv,1)),
         sp=vec3(0,0,100000);//sun Position
         //sd=normalize(vec3(0,0,1));//sunDirection
    float t=mod(iTime*.3,1.3)-.1,sunH=t;
    mat2 rot=mat2(cos(t),sin(t),-sin(t),cos(t));sp.yz*=rot;
    vec3 sum=vec3(0);
    vec4 e1=vec4(PC,R),e2=e1;e2.w+=H;
    float d=iS(ro,rd,e2),
          tsp=TOTALSAMPLES,
          ds=d/tsp;

    for(float i=0.;i<tsp;i+=1.)
    {
        float time=ds*(i+.5);
        vec3 p=ro+rd*time;
        vec3 sd=normalize(sp-p);
        //oc
        if(iS(p,sd,e1)>0.||iS(p,-rd,e1)<time)
            continue;

        vec3 transmittance=exp(-P*invplmd*(od(ro,p,tsp)+od(p,p+iS(p,sd,e2)*sd,tsp)));
        vec3 scat=P*invplmd*density(length(p-PC)-R)*pf(dot(rd,sd));
        sum+=scat*transmittance*ds;
    }
    O.rgb =sum*IS;

    //sun
    #ifdef SUN3D
    d=iS(ro,rd,vec4(sp*100.,300000.));
    vec3 sd=normalize(sp-ro);
    if(d>0.)
        O.rgb=mix(vec3(1.,.2,.01),vec3(.89,.34,.0)*1.5,pow((1.-dot(rd,sd))*200.,1.3));
    #endif

    float sunDist=length(uv-vec2(0,sunH)),sunR=.08;
    O.rgb=mix(O.rgb,vec3(1.+pow((sunDist+.1),2.)*4.2/sunR,.5,.1)*exp(sunH*.5),smoothstep(1.2*sunR,.01,sunDist));

    //land
    O.rgb=mix(vec3(.3,.8,.2)*.5*max((1.04-dot(vec3(0,0,1),normalize(sp-PC)))*2.,0.)*(-uv.y*2.+.5),O.rgb,
          smoothstep(.0,.1,uv.y));

    O.rgb=pow(O.rgb,vec3(0.4545));

}

