    }
}

uint uhash(uint x) {
    x = ((x >> uint(16)) ^ x) * uint(73244475);
    x = ((x >> uint(16)) ^ x) * uint(73244475);
    x = (x >> uint(16)) ^ x;
    return x;
}

float rand_from_seed(inout uint seed) {
    int k;
    int s = int(seed);
    if (s == 0)
    s = 305420679;
    k = s / 127773;
    s = 16807 * (s - k * 127773) - 2836 * k;
    if (s < 0)
        s += 2147483647;
    seed = uint(s);
    return float(seed % uint(65536)) / 65535.0;
}

float hash11(float v){
    uint hs = uhash(floatBitsToUint(v * 12.9898));
    return rand_from_seed(hs);
}

float hash21(vec2 v){
    uint hs = uhash(floatBitsToUint(v.y + v.x * 12.9898));
    return rand_from_seed(hs);
}

float hash31(vec3 v){
    uint hs = uhash(floatBitsToUint(v.y + v.x * 12.9898 + v.z * 25.831));
    return rand_from_seed(hs);
}

vec3 hash33(vec3 v){
    uint hs = uhash(floatBitsToUint(v.y + v.x * 12.9898 + v.z * 25.831));
    float a = rand_from_seed(hs);
    float b = rand_from_seed(hs);
    float c = rand_from_seed(hs);
    return vec3(a,b,c);
}

float get_rand(vec2 pos,vec2 box){
