#define MAX_DIST 1000.
#define MIN_DIST .001

// ffix float precision fix, cy height shift animation, stimer shift timer 0-1
// ffix also used in reflections to control voxel size to remove white borders on glow reflections
bool VoxelsIntersect(int steps, vec2 box, float max_h, float ffix, in vec3 ro, in vec3 rd,
                        out vec3 normal , out float tnew, out vec2 idx, inout float cy, float stimer) {
    vec2 pos = floor(ro.xz);

    vec3 rdi = 1./rd;

    vec3 rda = abs(rdi);
    vec3 rds = sign(rd);
    vec2 dis = (pos - ro.xz + .5 + rds.xz*.5) * rdi.xz;

    vec3 roi = rdi*(ro-vec3(.5,0.,.5));

    vec2 mm = vec2(0.0);
    for( int i=0; i<steps; i++ ) {    
        float tps = get_rand(pos, box);
        float tm=1.;
        if (any(greaterThan(pos,box*2.))||any(lessThan(pos,vec2(1.))))tm=0.;
        cy = mix(-(tm*max_h*tps-1.)+max_h*2.-2.,tm*max_h*tps-1.,stimer);
        vec3 n = roi - rdi * vec3(pos.x, cy, pos.y);
        vec3 k = rda*vec3(.5, -0.00099+tm*max_h*tps, .5);

        vec3 t1 = -n - k;
        vec3 t2 = -n + k;

        float tN = max( max( t1.x, t1.y ), t1.z )-ffix;
        float tF = min( min( t2.x, t2.y ), t2.z );

        if ( tN < tF && tN>MIN_DIST && tN<MAX_DIST ) {
            normal = -rds*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
            tnew = tN;
            idx = pos;
            return true;
        }

    mm = step( dis.xy, dis.yx ); 
    dis += mm*rda.xz;
    pos += mm*rds.xz;
    }

    return false;
}


// using iq's intersectors: http://iquilezles.org/www/articles/intersectors/intersectors.htm

bool SphereIntersectInv(vec3 SpPos, float SpRad, vec3 ro, vec3 rd, out float t, out vec3 norm) {
    ro -= SpPos;

    float A = dot(rd, rd);
    float B = 2.0*dot(ro, rd);
    float C = dot(ro, ro)-SpRad*SpRad;
    float D = B*B-4.0*A*C;
    t=-1.;
    norm=vec3(0.,1.,0.);
    if (D < 0.0) return false;

    D = sqrt(D);
    A *= 2.0;
    float t1 = (-B+D)/A;
    float t2 = (-B-D)/A;
    if (t1 < 0.0) t1 = t2;
    if (t2 < 0.0) t2 = t1;
    t1 = max(t1, t2);
    //t1 = min(t1, t2);
    if (t1 < 0.0) return false;
    norm = ro+t1*rd;
    t = t1;
    norm = normalize(norm);
    return true;
}
