vec2 lUV(vec3 p) {
    float x = dot(p, vec3(-1,0.,0.));
    float y = dot(p, vec3(0.,1.,0.));
    float td = dot(p, vec3(0.,0.,1.));
    td += 0.0001 * (1.0 - abs(sign(td)));
    float d = -1. / td;
    float u = d * x + 0.5;
    float v = d * y + 0.5;
    return vec2(u, v);
}

vec3 color_spiral(vec3 p, float d) {
    p.z+=-1.;
    vec2 uv=((lUV(p))-0.5)*.75;
    uv *=MD(-0.5/max(length(uv),0.0001));
    float td=(1.-smoothstep(-2.0,-.815,p.z))*(smoothstep(-2.,-1.95,p.z))*(1.-smoothstep(-1.5,2.35,length(uv)));
    float c = d / max(abs(5.0/uv.x),0.0001);
    return clamp(.85-c,0.,1.)*((blue+vec3(0.4,0.8,1.5)))*td;
}

