        float timer=mod(gt+1.25*float(a-1),timers[timers.length()-1]);
        timer+=timers[0];
        dx[a]=anim_camera[0];
        for(int i=1;i<anim_camera.length()+iZero;i++){
            float t=smoothstep(timers[i-1],timers[i],timer);
            dx[a]=mix(dx[a],anim_camera[i],t);
        }
        float t=smoothstep(timers[timers.length()-1],timers[timers.length()-1]+timers[0],timer);
        dx[a]=mix(dx[a],anim_camera[0],t);
    }
    float timer=mod(gt,timers[timers.length()-1]);
    timer+=timers[0];
    atimer=1.-smoothstep(timers[4]+4.,timers[4]+6.5,timer);
    atimer=max(atimer,smoothstep(timers[8]+0.,timers[9]+1.5,timer));
    return mix(dx[1],mix(dx[0],dx[2],0.5),0.5);
}

vec2 get_look(float gt){
    float timer=mod(gt,timers[timers.length()-1]);
    timer=smoothstep(0.,timers[timers.length()-1],timer);
    float et=easeInOutCubic(timer);
    float ot=((1.-smoothstep(0.,0.5,timer))-1.5*smoothstep(0.5,0.6,timer)*(1.-smoothstep(0.75,0.9,timer)))+smoothstep(0.9,1.,timer);
    timer=(timer*(1.-et))-0.5;
    return vec2(0.2-3.*sin((0.5+timer)*PI),-0.25*sin(0.5*ot*PI));
}

Ray SetCamera(vec2 uv, vec3 ro, vec2 m)
{
    if(iMouse.z>0.)
        m+=(iMouse.xy/iResolution.y-0.5*iResolution.xy/iResolution.y)*2.5;
    m.y = -m.y;
    float fov=70.;
    float aspect = iResolution.x / iResolution.y;
    float screenSize = (1.0 / (tan(((180.-fov)* (3.1415926 / 180.0)) / 2.0)));
    vec3 rd = normalize(vec3(uv*screenSize, 1./aspect));
    mat3 rotX = mat3(1.0, 0.0, 0.0, 0.0, cos(m.y), sin(m.y), 0.0, -sin(m.y), cos(m.y));
    mat3 rotY = mat3(cos(m.x), 0.0, -sin(m.x), 0.0, 1.0, 0.0, sin(m.x), 0.0, cos(m.x));

    rd = (rotY * rotX) * rd;
    return Ray(ro,rd);
}

bool boxAABB(in vec3 dims, vec3 ro, vec3 rd) {
    vec3 n = ro / rd;
    vec3 k = dims / abs(rd);
    vec3 t1 = -k - n, t2 = k - n;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    return tN < tF && tF > 0.0;
}

void VoxelsIntersectMin_ref(vec2 box, float max_h , vec3 posl,vec3 ro, vec3 rd,
                            inout HitInfo hit, float rfnl, float sky_pow, float col_pow, inout float refc) {
    float tnew;
    vec2 idx;
    vec3 normnew;
    float cy;

    if(VoxelsIntersect(6, box, max_h, -0.001, ro, rd, normnew, tnew, idx, cy, atimer)){
        bool in_box=(idx.x<=box.x*2.)&&(idx.y<=box.y*2.);
        in_box=in_box&&all(greaterThan(idx,vec2(0.)));
        float fnl = fresnel(rd,normnew);
        if(in_box){
            float alb = hash21(idx+1.);
            float trnd = get_rand(idx+20., box*0.7);
            float emi = step(0.65,trnd-0.75*trnd*hash21(idx+5.));
            alb*=0.25;
            alb *= rfnl * (1.-fnl) 
                * (.8 + .2 * dot(normnew, vec3(-.25916,.8639,-.4319)));

            emi *= col_pow;
            vec3 tuv = fract(ro+tnew*rd) * 2.0 - 1.0;
            float vig=max(tuv.x*tuv.x, tuv.z*tuv.z)*0.5;
            vec3 tc = white+blue;
            tc=mix(tc,tc*.65,vig);
            vec3 col=max(alb*white,emi*1.175*tc);
            hit.color.rgb += col;
            hit.color.a += emi*0.25;
            refc*=0.12;
        }
        else {
            vec3 bg=clamp(vec3(.5,.8,1)*0.+1.*blurred_background(rd),0.,1.);
            hit.color.rgb  += sky_pow*clamp(bg * (rfnl*(5.-2.5*rd.y)),vec3(0.),bg);
        }
    }
    else {
        vec3 bg=clamp(vec3(.5,.8,1)*0.+1.*blurred_background(rd),0.,1.);
        hit.color.rgb += sky_pow*clamp(bg * (rfnl*(5.-2.5*rd.y)),vec3(0.),bg);
    }
}

void VoxelsIntersectMin(vec2 box, float max_h , vec3 posl,vec3 ro, vec3 rd,
                        inout bool result, inout HitInfo hit, vec3 sky) {
    float tnew;
    vec2 idx;
    vec3 normnew;

    posl.xz+=1.;
    ro+=posl;

    if(!boxAABB(vec3(box.x,max_h,box.y), ro+vec3(-1.,-max_h+1.,-1.), rd))return;
    ro.xz+=box;
    float vros = length(ro.xz-(box+1.));
    if(VoxelsIntersect(min(int(vros*1.5)+18,40), box, max_h, 0.0001, ro, rd, normnew, tnew, idx, hit.exdata.y, atimer)){
        bool in_box=(idx.x<=box.x*2.)&&(idx.y<=box.y*2.);
        in_box=in_box&&all(greaterThan(idx,vec2(0.)));
        if (in_box&&(tnew>0.)) {
            float tmx = 0.;
            if((hit.obj_type==OBJ_BALL)&&(tnew>hit.t))tmx=clamp(dot(hit.color.rgb,vec3(1.))*0.25,0.,1.);
            hit.color = vec4(0.,0.,0.,0.);
            float fnl = fresnel(rd,normnew);

            float alb = hash21(idx+1.);
            float trnd = get_rand(idx+20., box*0.7);
            float emi = step(0.65,trnd-0.75*trnd*hash21(idx+5.));
            vec3 col = vec3(0.);
            alb*=0.1*alb;

            alb *= (1.-fnl) 
                * (.8 + .2 * dot(normnew, vec3(-.25916,.8639,-.4319)));

            vec3 tuv = fract(ro+tnew*rd) * 2.0 - 1.0;
            float vig=max(tuv.x*tuv.x, tuv.z*tuv.z)*0.5;
            vec3 tc = white+blue;
            tc=mix(tc,tc*.85,vig);
            col=max(alb*white,emi*2.35*(tc));
            vec3 skyo = background_sky(rd);
            hit.color.rgb += mix(col,skyo,fnl*fnl);
            hit.color.a += emi;

            hit.obj_type = OBJ_FLOOR;
            result = true;
            hit.t = tnew;
            hit.norm = normnew;

            float vrds = length(tnew * rd.xz);
            if((emi<0.5)&&(fnl > .001)){
                hit.obj_type = OBJ_REFLOOR;
                hit.norm = normnew;
                hit.exdata.x = fnl;
            }

            hit.color.rgb = mix(hit.color.rgb,sky,smoothstep(24.,27.5,vrds));
            hit.color.rgb = mix(hit.color.rgb,sky,tmx);
        }
    }
}


float glow_sphere(vec3 rd, vec3 n){
    float a = pow(0.7122 + max(dot(n, reflect(rd,n)),0.), 10.85);
    float b = pow(0.7122 + max(1.-dot(n, reflect(rd,n)),0.), 3.85);

    return clamp(min(a,b),0.,10.);
}

void SphereIntersectMin(vec3 SpPos, float SpRad, vec3 ro, vec3 rd, inout bool result, inout HitInfo hit, bool refl, bool sph2, float ot) {
    float tnew;
    vec3 normnew;
    if (SphereIntersectInv(SpPos, SpRad, ro, rd, tnew, normnew)) {
        if(!refl){
        if (tnew < hit.t||sph2&&(tnew < ot)) 
        {
            hit.t = min(tnew,hit.t);
            hit.norm = normnew;
            if(sph2){
                vec3 spotLightOrigin 	= (SpPos - vec3(0.,0.,1.)*22.9);
                vec3 Ln = normalize(spotLightOrigin-(ro+rd*tnew));
                hit.color.rgb += color_spiral(Ln,20.)*4.;
            }
            else
            hit.color.rgb += glow_sphere(rd,-normnew)*(sqrt(blue))*3.;
            hit.obj_type = OBJ_BALL;
            result = true;
        }
        }else{
            if(sph2)
            {
                vec3 spotLightOrigin 	= (SpPos - vec3(0.,0.,1.)*19.9);
                vec3 Ln = normalize(spotLightOrigin-(ro+rd*tnew));
                hit.color.a += color_spiral(Ln,10.).b*.85;
            }
            else
            hit.color.a = glow_sphere(rd,-normnew);
            hit.obj_type = OBJ_BALL;
            result = true;
        }
    }
}


HitInfo render(Ray r, inout vec3 sky)
{
    HitInfo hit;
    hit.t = MAX_DIST;
    hit.obj_type = OBJ_SKY;
    hit.color = vec4(sky,0.);
    hit.norm = vec3(0.,1.,0.);
    hit.exdata = vec2(0.);

    vec4 col = vec4(0.);

    vec3 ro = r.pos; vec3 rd = r.dir;

    bool result = false;
    float angle=0.;

    vec3 spherex=vec3(0.,8.15,8.5+voxbox.y+15.);
    vec3 spherex2=vec3(0.,8.15,8.5+voxbox.y+15.-22.25);

    rd += 0.0001 * (1.0 - abs(sign(rd)));

    float ot=hit.t;
    SphereIntersectMin(spherex, 13.5, ro, rd, result, hit, false, false,0.);
    SphereIntersectMin(spherex2, 23.5, ro, rd, result, hit, false, true,ot);
    sky=hit.color.rgb;

    VoxelsIntersectMin(voxbox, voxmax_h , voxposl,ro, rd, result, hit, sky);

    return hit;
}

// reflections
HitInfo render_refl(Ray r, HitInfo hit, float fbmx, inout vec3 sky)
{
    vec3 skyo = background_sky(r.dir);

    if(hit.obj_type == OBJ_REFLOOR){
        vec3 ro = r.pos;
        ro = ro+r.dir*hit.t;
        float vrds = length(hit.t * r.dir.xz);
        fbmx*=1.-smoothstep(10.,14.5,vrds);
        float gfnl = fresnel((r.dir),hit.norm);
        fbmx*=max(1.-gfnl*2.,0.);
        vec3 ord = reflect(r.dir-(.0295*fbmx*hit.norm), hit.norm);
        vec3 rd = reflect(r.dir-(.295*fbmx*hit.norm), hit.norm);
        rd += 0.0001 * (1.0 - abs(sign(rd)));

        bool result = false;
        float angle=0.1;

        vec3 spherex=vec3(0.,10.15,8.5+voxbox.y+15.);
        vec3 spherex2=vec3(0.,10.15-2.,8.5+voxbox.y+15.-22.25);
        float oemi=hit.color.a;
        float ot=hit.t;
        SphereIntersectMin(spherex, 13.5, ro, rd, result, hit, true, false,0.);
        SphereIntersectMin(spherex2, 23.5, ro, ord, result, hit, true, true,ot);
        float refc=0.;
        float fnl = 0.; 
        if(hit.obj_type == OBJ_BALL){
            fnl=1.;refc=hit.color.a;hit.color.a=oemi;
        }
        ro+=voxposl;ro.xz+=voxbox+1.;

        rd = reflect(r.dir+(.695*fbmx*hit.norm), hit.norm);
        rd += 0.0001 * (1.0 - abs(sign(rd)));

        VoxelsIntersectMin_ref(voxbox, voxmax_h , voxposl+1.,ro, rd, hit, hit.exdata.x, 01.5, 01.+0.25*fbmx,refc);
        hit.color.rgb = 0.985*hit.color.rgb+0.015*hit.color.rgb*fbmx;

        fnl = max(fresnel(rd,hit.norm),fnl);
        hit.color.rgb = mix(hit.color.rgb,skyo,1.-sqrt(fnl))+refc*(sqrt(blue))*3.;

        hit.color.rgb = mix(hit.color.rgb,sky,smoothstep(24.,27.5,vrds));
    }
    return hit;
}

