#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01
#define Light_pos vec3(5.,5.,9.)
#define selfcol vec3(.9,.9,.9)

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 spectrum(float n) {
    return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.1,0.5,0.9) );
}
float smin(float a,float b,float h){
    float c=clamp((b-a)/h*0.5+0.5,0.,1.);
    //return c;
    return mix(b,a,c)-h*c*(1.0-c);
}

mat2 rot(float a){
   float c=sin(a);
   float s=cos(a);
    return mat2(c,s,-s,c);

}
float GetDist(vec3 p){
    vec4 s=vec4(0.,1.,6.,2.);
    float spheredist=length(p-s.xyz)-s.w;
    float planedist=p.y;
    float d=smin(spheredist,planedist,.9);
    return d;
}
float rayMarch(vec3 ro,vec3 rd){
    float d0=0.;
    for(int i=0;i<MAX_STEPS;i++){
        vec3 p=ro+rd*d0;
        float ds=GetDist(p);
        d0+=ds;
        if(d0>MAX_DIST||ds<SURF_DIST)break;
    }
    return d0;
}
vec3 getNormal(vec3 p){
    float l= GetDist(p);
    vec2 cau=vec2(.01,0.);
    vec3 n=normalize(vec3(l-GetDist(p-cau.xyy),
                          l- GetDist(p-cau.yxy),
                          l- GetDist(p-cau.yyx)
                           ));
    return n;

}
float  getLight(vec3 p){
    vec3 ld=Light_pos;
    //shadow
    ld.xz*=rot(iTime);
    vec3 li=normalize(ld-p);
    vec3 sn=getNormal(p);
    float sh=rayMarch(p+sn*SURF_DIST*2.,li);
    float dif=clamp(dot(sn,li),0.,1.) ;
        if(sh<length(ld-p)) {
            dif*=.5;
        }
    return dif;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
     vec3 ld=Light_pos;
    ld.xz*=rot(iTime);
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 ro=vec3(0.,3.,-3);
    vec3 rd=normalize(vec3(uv.x,uv.y-.2,1));
    float d= rayMarch(ro,rd);
    vec3 p=ro+rd*d;
    vec3 n=getNormal(p);
    float difu=getLight(p);
    float ndotv=max(0.,dot(normalize(rd),n));
    vec3 halfh=normalize(normalize(-rd)+normalize(ld-p));
    float ndothalfh=max(0.,dot(halfh,n));
    vec3 col =mix(vec3(difu)*selfcol,spectrum(ndothalfh),0.1);
    col+=vec3(pow(ndothalfh,25.));
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define time iTime
#define resolution iResolution
float c  =0.;

mat2 rot(float f)
{
	float s = sin(f), c = cos(f);
	return mat2(c, -s , s, c);
}
float scene(vec3 p)
{

    for( int i = 0; i<10; ++i){
        float t = time*.2;
        p = abs(p -.2);
        p.xy *= rot(t + c);

        c -= .1;
	}

	float thing = max(dot(p , normalize(vec3(45., 0.,-524.))), length(p) - 2.);

	return max(thing, p.y);
}


vec3 calcnorm(vec3 value)
{
    vec3 n = vec3(0);
    if(length(n - value) == 0.)
    	return vec3(0);
    for(int i = 0; i < 4; i++)
    {
        vec3 eps = 0.001 * (vec3(9 >> i&1, i >> 1 & 1, i & 1) * 2. - 1.);
        n += eps * scene(value + eps);
    }
    return normalize(n);
}


vec4 rc(vec3 ro, vec3 rd)
{
	float len = 0.;
	float closest = 100.;
	for(int i = 0; i < 255; i ++)
	{
		float dist = scene(ro + rd * len);

		closest = min(closest, dist/.01);
		if(dist < 0.001)
			return vec4(ro + rd * len,closest);

		len += dist;

		if(len  > 100. ) 
			break;
	}

	return vec4(ro + rd * len,closest);
}

mat3 cam(vec3 pos, vec3 la, vec3 up)
{
	vec3 ww = normalize(la - pos);
	vec3 vv = normalize(up);
	vec3 uu = normalize(cross(ww, vv));
	return mat3(ww, uu ,vv);
} 


vec3 pallete( vec3 pos)
{
	float len = length(pos) / 15.;

	return mix(1.- vec3 (0., 0.5, 0.7), 1.-vec3(1., 0.,10.), len)/100.; 
}
vec3 color(vec4 pos)
{
	vec3 lightcolor = vec3( 1, 1, 1), lightpos = vec3(0, 4, -4);
	vec3 norm = calcnorm(pos.xyz);

	return pallete(pos.xyz)- (1./(1. + (pos.w * 0.3))) * vec3(0,1,1);
}

