/*
Author: Felipe Tovar-Henao [www.felipe-tovar-henao.com]

Description: Rotation-based generalization of a regular polygon's signed distance field (SDF), 
based on Inigo Quilez's method in https://youtu.be/62-pRVZuS5c
The length of the apothem is constant for all polygons.
*/

#define u_resolution iResolution
#define u_time iTime

float polygonSDF(in vec2 vUV, in int V) {
    float N = max(1.0, float(V));
    float rot = floor(N*(atan(-vUV.x,-vUV.y) / 6.28318530718 + 0.5));
    float offset = 3.14159265359*(N+2.0)/(2.0*N);
    float theta = (6.28318530718/N) * rot + offset;
    float side = tan(3.14159265359/max(N, 3.0));
    vec2 r = vec2(cos(theta), sin(theta));
    vUV *= mat2(r.x, -r.y, r.y, r.x);
    float scl = 0.5 * (float(N==2.0)*(0.25/sqrt(3.0)) + step(2.0,N)) / (step(1.0,N) + step(N, 2.0));
    vec2 s = vec2(step(3.0, N), side) * scl;
    vec2 p = abs(vUV)-s;
    return length(max(vec2(0.0), p)) + min(0.0, max(p.x, p.y/side));
}

float fold(in float x) {
    float y = fract(x);
    return mix(y, 1.0-y, float(mod(x, 2.0) >= 1.0));
}

float relu_ramp(in float x, in float s) {
    return floor(x) + clamp((max(1.0, s)*(fract(x) - 0.5)) + 0.5, 0.0, 1.0);
}

float dx_ramp(in float x, in float s) {
    float y = fract(x);
    float h = max(0.0, pow(y, s));
    return floor(x) + 1.0 - length(vec2(y-1.0,(h*h)-1.0)) * 0.707106781187;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUV = fragCoord.xy / u_resolution.xy;
    float scl = 2.25;
    vUV = (vUV-0.5) * scl;
    vUV.x *= u_resolution.x / u_resolution.y;
    float max_sides = 15.0;
    vec3 color = vec3(0.0);

    float t = max_sides*pow(fract(u_time/max_sides), 1.25);
    float V1 = mod(t, max_sides) + 1.0;
    float V2 = mod(t+1.0, max_sides) + 1.0;
    float sdfA = polygonSDF(vUV, int(V1));
    float sdfB = polygonSDF(vUV, int(V2));
    float sdf = mix(sdfA, sdfB, relu_ramp(fract(V1), 3.0));
    float border = 1.0-clamp(smoothstep(0.0,0.005,abs(sdf)), 0.0, 1.0);
    sdfA = max(0.0, sdf);
    sdfB = abs(min(0.0, sdf));
    vec3 c1 = vec3(1.0, 0.1961, 0.1961) * pow(sdfA, 0.5);
    vec3 c2 = vec3(0.1765, 1.0, 0.9882)  * pow(sdfB, 0.5);    
    color = dx_ramp(fold(sdfA*scl*15.0), 4.0) * c1;
    color += dx_ramp(fold(sdfB*scl*15.0), 4.0) * c2;
    color += border*vec3(0.7412, 1.0, 0.9608);
    fragColor = vec4(color, 1.0);
}

/*
    Author: Felipe Tovar-Henao [www.felipe-tovar-henao.com]
    Description: Beating blob, using cellular noise and shaping functions.
*/

#define u_resolution iResolution
#define u_time iTime
#define TWO_PI 6.28318530718

vec3 random3D(in vec3 vUV, in int seed) {
    return fract(abs(sin(vec3(dot(vUV, vec3(11.13, 57.05, 41.87) + float(seed)), dot(vUV, vec3(43.17, 85.39, 38.97)), dot(vUV, vec3(56.91, 39.59, 81.33)))) * 48240.41));
}

float worley_noise3D(in vec3 vUV, in int seed) {
    vec3 xyz = floor(vUV);
    vec3 f = fract(vUV);
    float mindist = 90000.0;
    for (float x = -1.0; x <= 1.0; x++) {
        for (float y = -1.0; y <= 1.0; y++) {
            for (float z = -1.0; z <= 1.0; z++) {
                vec3 node = random3D(xyz + vec3(x,y,z), seed) + vec3(x,y,z);
                mindist = min(mindist, distance(node,f));
            }
        }
    }
    return mindist;
}

vec2 rotate2D(in vec2 vUV, in float theta) {
    vec2 r = vec2(cos(theta), sin(theta));
    return vUV * mat2(r.x, -r.y, r.y, r.x);
}

float fold(in float x) {
    return abs(mod(x+1.0,2.0)-1.0);
}

float cosine_ramp(in float x, in float s) {
    float y = cos(fract(x)*3.14159265359);
    return floor(x) + 0.5 - (0.5*pow(abs(y), 1.0/s)*sign(y));
}

float sine_ramp(in float x, in float s) {
    float y = fract(x);
    float h = 0.5 - (0.5/(s*s));
    return floor(x) + y + h*sin(6.28318530718*y);
}

float seat_ramp(in float x, in float s) {
    float y = (2.0*fract(x))-1.0;
    return floor(x) + 0.5 + 0.5*pow(abs(y), 1.0/s)*sign(y);
}

float N_ramp(in float x, in float s) {
    float y = fract(x);
    float h = max(0.3, s);
    return floor(x) + length(vec2(y,0.0)-vec2(pow(y,1.0/h),pow(y,h)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 vUV = fragCoord.xy / u_resolution.xy;
    vUV -= 0.5;
    vUV *= 3.0;
    vUV.x *= u_resolution.x / u_resolution.y;

    vec3 color = vec3(0.0);
    float t = sine_ramp(u_time * 0.09, 9.0);
    float theta = worley_noise3D(vec3(vUV*length(vUV), seat_ramp(t, 2.0)), 4) * pow(length(vUV)*TWO_PI, 0.8);
    theta = sine_ramp(theta, 1.0);
    vUV = rotate2D(vUV, fold(theta + u_time*0.2) + N_ramp(t*0.23, 2.0));
    vUV = clamp(vUV, 0.0, 1.0);
    vUV.x = fold(vUV.x*3.0);
    vUV.y = fold(vUV.y*2.0);

    vUV.x = cosine_ramp(vUV.x, 2.5 + sin(u_time*0.17)*0.25);
    vUV.y = cosine_ramp(vUV.y, 2.5 + cos(u_time*0.03)*0.25);

    vec3 c1a = vUV.x * vec3(0.8039, 0.6196, 0.4824);
    vec3 c2a = vUV.y * vec3(0.2118, 0.3922, 0.549);
    vec3 c1b = vUV.x*vUV.y * vec3(0.6353, 0.4667, 0.4392);
    vec3 c2b = abs(vUV.x-vUV.y) * 0.5 * vec3(0.3922, 0.7373, 1.0);

    color += mix(c1a, c1b, fold(u_time*0.015)) + mix(c2a, c2b, fold(t*0.013));
    color = clamp(color, 0.0, 1.0);

    float shade = 1.0 - (color.r * color.b * color.g);
    vec3 sh1 = shade * vec3(0.1255, 0.1373, 0.149);
    vec3 sh2 = shade * vec3(0.2039, 0.1843, 0.1725);

    color += mix(sh1, sh2, fold(t*0.007));

    fragColor = vec4(color, 1.0);
}

// Originally done in Blender:
// https://twitter.com/cmzw_/status/1380115994320859147

#define PI 3.14159265359

#define RES 24.0
#define SCALE 1.0
#define SPEED_FAC 3.0

#define ARROW_MASK 0
#define ARROW_COL 1
#define ARROW_HIT_AREA 2
#define ARROW_HIT_AREA_MASK 3

float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
	return length(pa - ba * h);
}

vec3 overlay(in vec3 a, in vec3 b) {
	return vec3((b.r > .5) ? (2. * a.r * b.r) : 1. - 2. * (1. - a.r) * (1. - b.r),
	            (b.g > .5) ? (2. * a.g * b.g) : 1. - 2. * (1. - a.g) * (1. - b.g),
	            (b.b > .5) ? (2. * a.b * b.b) : 1. - 2. * (1. - a.b) * (1. - b.b));
}

float sstep(in float f) { return 1. - step(0.0001, f); }

mat2 rot2(float a) { return mat2(cos(a), -sin(a), sin(a), cos(a)); }

vec3 arrow(in vec2 p, in float scale, int mode, in vec3 acol) {
	p *= scale;
	p = vec2(round(p * RES) / RES);
	vec2 p1 = vec2(abs(p.x), p.y);

	float af = sdSegment(p1, vec2(.58, .0), vec2(.0, .58));
	float al = sdSegment(p, vec2(.0, .48), vec2(.0, -.58));

	float a1 = sstep(af - .08);
	float a2 = sstep(sdSegment(p, vec2(.0, .15), vec2(.0, -.05)) - .15);
	float a3 = sstep(sdSegment(p, vec2(.0, .13), vec2(.0, -.51)) - .11);
	float tp = sstep(dot(p1 + vec2(.0, -mod(iTime / .4, 1.6)) + .4, vec2(.22)));
	float ts = sstep(abs(dot(p1 - vec2(0., -.15), vec2(.21, .26))) - .01);
	float rect = sstep(length(max(abs(p - vec2(.0, .42)) - vec2(.3, .2), 0.)));

	float arrow =
	    clamp(rect * sstep(dot(p1, vec2(.22)) - .11) + a1 - a2, 0., 1.) + clamp(a3 - ts, 0., 1.);
	float region = min(af, al) - .2;
	float outline = sstep(abs(region) - .02);

	vec3 color = overlay(acol, vec3(round((p.y + 1.) * 4.) / 4.) + .5);

	color = mix(vec3(0.), color, sstep(region));

	float mask = sstep(region - .02);

	switch ( mode ) {
	case ARROW_MASK:
		return vec3(mask);
	case ARROW_COL:
		return vec3(outline + color + arrow * tp + arrow * .2);
	case ARROW_HIT_AREA:
		return vec3(mask * .3 * mod(iTime / .5, 1.) + .5 - arrow + outline);
	case ARROW_HIT_AREA_MASK:
		return vec3(mask - arrow);
	}
}

vec3 hit_arrow(in vec3 col, in vec2 ha) {
	vec3 acol = vec3(0.);
	return mix(col, arrow(ha, 2., ARROW_HIT_AREA, acol), arrow(ha, 2., ARROW_HIT_AREA_MASK, acol));
}

vec3 ca(in vec3 col, in vec3 ac, in vec2 p, in vec2 ha, in mat2 rot, in vec2 ss) {
	vec3 left_m = vec3(1. - step(0., abs(p.x - .25 * 3.) - .25)) * step(ss.x, mod(p.y * ss.y, 2.));
	vec2 a = mod((p * 2. - .5) + .5, 1.) - .5;
	vec2 ap = a * rot;
	return mix(col, arrow(ap, 2., ARROW_COL, ac),
	           arrow(ap, 2., ARROW_MASK, ac) * left_m * (sstep(ha.x - a.y)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec3 col;
	vec2 p = (2. * fragCoord - iResolution.xy) / iResolution.y;
	p *= SCALE;

	// checker bg
	vec2 cp = floor(p * 2.);
	col += mix(.13, .17, mod(cp.x + cp.y, 2.));

	// hit areas
	vec2 ha = (p * 2. - vec2(-1.5, 1.5)) * -rot2(PI / 2.);
	col = hit_arrow(col, ha);
	col = hit_arrow(col, (p * 2. - vec2(-.5, 1.5)) * rot2(PI));
	col = hit_arrow(col, (p * 2. - vec2(.5, 1.5)) * -rot2(PI));
	col = hit_arrow(col, (p * 2. - vec2(1.5, 1.5)) * rot2(PI / 2.));

	// colors
	vec3 blue = vec3(0., .1, .3);
	vec3 bluep = vec3(0., .202, .139);
	vec3 orange = vec3(.34, .045, .0);
	vec3 green = vec3(.0, .159, .03);
	vec3 red = vec3(.19, .0, .0);
	vec3 redp = vec3(.15, .0, .057);
	vec3 purple = vec3(.106, .001, .34);
	vec3 yellow = vec3(.202, .174, .0);

	// arrows
	vec2 tr = p - vec2(0., iTime * SPEED_FAC);
	col = ca(col, blue, tr - vec2(-1.5, .4), ha, -rot2(PI / 2.), vec2(1.5, 1.));
	col = ca(col, redp, tr - vec2(-1.5, -.2), ha, -rot2(PI / 2.), vec2(1., 2.));
	col = ca(col, green, tr - vec2(-1, 1.1), ha, rot2(PI), vec2(1.5, 1.));
	col = ca(col, orange, tr - vec2(-1, .1), ha, rot2(PI), vec2(1.5, 1.));
	col = ca(col, purple, tr - vec2(-.5, .1), ha, -rot2(PI), vec2(1., 2.));
	col = ca(col, red, tr - vec2(-.5, -.5), ha, -rot2(PI), vec2(1., 2.));
	col = ca(col, yellow, tr - vec2(0., -.5), ha, rot2(PI / 2.), vec2(1.5, 1.));
	col = ca(col, bluep, tr - vec2(0., .4), ha, rot2(PI / 2.), vec2(1.5, 1.));

	fragColor = vec4(col, 1.);
}

float Circle(vec2 uv, vec2 position, float r, float blur)
{
    float distance = length(uv-position);
    float color = smoothstep(r,r-blur,distance);

    return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5; // make viewport center as (0,0)
    uv.x *= iResolution.x/iResolution.y;
    float r = 0.3;
    float face = Circle(uv,vec2(0.,0),r,0.01);

    //eye 1
    face -= Circle(uv,vec2(-0.15,0.1),0.01*sin(iTime)+0.05,0.01); 

    //eye mirror to +x axis
    face -= Circle(uv,vec2(0.15,0.1),0.01*sin(iTime)+0.05,0.01);

    //make mouth
    //lower lip
    float mouth = Circle(uv,vec2(0.,-0.05),0.2,0.01);
    //upper lip
    mouth -= Circle(uv,vec2(0.,0.04*(sin(iTime)+0.02)),0.2,0.01);
    mouth = clamp(mouth,0.0,1.0);

    face -= mouth;

    vec3 col = vec3(0.5+0.3*sin(iTime),0.7+0.*sin(iTime),1.0+0.5*sin(iTime))*face;
    fragColor = vec4(col,1.0);
}

//noise funtion abstract from https://www.shadertoy.com/view/4sc3z2
vec3 hash33(vec3 p3)
{
	vec3 MOD3 = vec3(.1031, .11369, .13787);
	p3 = fract(p3* MOD3);
	p3 += dot(p3, p3.yxz + 19.19);
	return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x + p3.z)*p3.y, (p3.y + p3.z)*p3.x));
}

float simplex_noise(vec3 p)
{
	const float K1 = 0.333333333;
	const float K2 = 0.166666667;

	vec3 i = floor(p + (p.x + p.y + p.z) * K1);
	vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

	vec3 e = step(vec3(0, 0, 0), d0 - d0.yzx);
	vec3 i1 = e * (1.0 - e.zxy);
	vec3 i2 = 1.0 - e.zxy * (1.0 - e);

	vec3 d1 = d0 - (i1 - 1.0 * K2);
	vec3 d2 = d0 - (i2 - 2.0 * K2);
	vec3 d3 = d0 - (1.0 - 3.0 * K2);

	vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
	vec4 n = h * h * h * h * vec4(dot(d0, hash33(i)), dot(d1, hash33(i + i1)), dot(d2, hash33(i + i2)), dot(d3, hash33(i + 1.0)));

	return dot(vec4(31.316, 31.316, 31.316, 31.316), n);
}



float render(vec2 uv)
{
    float side = smoothstep(0.5, 0.3, length(uv));
    float center = smoothstep(0.1, 0.0, length(uv));
    vec3 rd = vec3(uv, 0.);

    float t = pow(iTime+0.5,5.)*0.001;

    float n2 = simplex_noise((rd*t+t) * (1. / length(rd*t+rd)));

    if(iTime>1.5)
    {
        n2 = simplex_noise((rd*t+t) * (1. / length(rd*t+rd))+(iTime-1.5));
    }


    float flare = smoothstep(0.,1.,0.002 / length(rd*length(rd)*n2))*side;

    flare = flare-center*clamp((iTime-1.5)*10.,0.,5.);

    return flare;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;


    vec3 col = vec3(0.102,0.5,1.)*2.;
    col *= render(uv);

    fragColor = vec4(col,1.0);
}

const int steps = 55;

float magnitude(vec3 a)
{
    return sqrt(a.x*a.x + a.y*a.y + a.z*a.z);
}

vec3 normalized(vec3 a)
{
    return a/length(a);
}

float sdfUnion(float sdf1, float sdf2)
{
    return min(sdf1, sdf2);
}

// Polynomial smooth min - https://www.iquilezles.org/www/articles/smin/smin.htm && Media Molecule
float sdfSmoothUnion(float sdf1, float sdf2, float k)
{
    float h = max( k-abs(sdf1-sdf2), 0.0 )/k;
    return min( sdf1, sdf2 ) - h*h*k*(1.0/4.0);
}

float sdfSphere(vec3 origin, vec3 center, float radius)
{
    // if distance from origin to center, is bigger than radius, return positive value
    // if distance from origin to center, is smaller/equal to radius, return negative value  
    return (magnitude(center-origin) - radius);
}


float rayMarch(vec3 origin, vec3[2] center, float radius, vec3 viewDir, vec3 lightPos, float specExpo, inout float diff, inout float spec, inout float ao)
{   
    float sdf;

    for(int i=0; i<steps; i++)
    {
        float sdf1 = sdfSphere(origin, center[0], radius);
        float sdf2 = sdfSphere(origin, center[1], radius);        
        float sdf = sdfSmoothUnion(sdf1, sdf2, 0.75);

        origin += sdf * viewDir;        

        if ( sdf < 0.001 ) 
        {
        // return 0 if center0 is closer than center1
        // return 1 if center0 is distant than center1
        float a =  min(1.0, floor(magnitude(center[0]-origin) / magnitude(center[1]-origin)) );
        vec3 nearestCenter = a * center[1] + (1.0-a) * center[0];

        vec3 normal = normalized(origin - nearestCenter);
        vec3 lightDir = normalized(lightPos - origin);
        // dot product will return negative value is vectors pointing in opposite directions.
        // make sure result >= 0
        diff = max(0.0, dot(normal, lightDir));

        vec3 reflectDir = reflect(-lightDir, normal);
        spec = pow(max(0.0, dot(-viewDir, reflectDir)), specExpo);

        ao =  (float(i) / float(steps));
        return 1.0;
        }
    }   
    return 0.0;
}

vec3[] _center = vec3[]
( 
    vec3(-1.1, 0.0, 5.0), 
    vec3( 1.1, 0.0, 5.0)
);

float _radius = 1.0;

vec3 _matAmbient = vec3(1.0, 1.0, 1.0);
vec3 _matDiffuse = vec3(0.5, 0.5, 0.5);
vec3 _matSpecular = vec3(1.0, 1.0, 1.0);

vec3 _lightPos = vec3(0.0, 10.0, 0.0);

vec3 _lightColor = vec3(1.0, 1.0, 1.0);
float _lightAmbient = 0.2;
float _lightDiffuse = 0.75;
float _lightSpecular = 1.0;
float _lightSpecularExpo = 32.0;

mat4 rotZ(float angle)
{
    mat4 rotMatrix = mat4(
        vec4( cos(angle), sin(angle), 0.0, 0.0),
        vec4(-sin(angle), cos(angle), 0.0, 0.0),
        vec4(        0.0,        0.0, 1.0, 0.0),
        vec4(        0.0,        0.0, 0.0, 1.0)
    );

    return rotMatrix; 
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //_radius = mix(0.5, 2.0, abs(sin(iTime)));
    _lightPos.z = mix(0.0, 10.0, sin(iTime*.5));

    _center[0].x -=  .2 * sin(iTime*.25);
    _center[1].x +=  .2 * sin(iTime*.25);   


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 nfc = 2.0 * uv - 1.0;

    // fragment and camera world-space position
    vec3 fragPos = vec3(nfc.x, nfc.y, 0.0) * vec3((iResolution.x/iResolution.y),1.0,1.0);
    vec3 camPos = vec3(0.0, 0.0, -5.0);

    vec3 viewDir = normalized(fragPos - camPos);

    float diff;
    float spec;
    float ao;
    float test = rayMarch(camPos, _center, _radius, viewDir, _lightPos, _lightSpecularExpo, diff, spec, ao);

    vec3 ambient = (_lightAmbient * _lightColor) * _matAmbient;
    vec3 diffuse = (_lightDiffuse * _lightColor) * (_matDiffuse * diff);
    vec3 specular = (_lightSpecular * _lightColor) * (_matSpecular * spec);

    //float edge = (ao*ao)*(3.0-2.0*ao);

    vec3 bg = vec3(0.3,0.3,0.3);

    vec3 col = ambient + diffuse + specular;
    col = mix(bg, col*(1.0-ao), test);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// "Spiraling In and Out" by dr2 - 2021
