#define iafloat vec2
#define iavec3 mat3 // in glsl: mat3x2
#define PI 3.14159265
#define EPSILON .0001

vec3 rotZ(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return vec3 (
        c*p.x - s*p.y,
        s*p.x + c*p.y,
        p.z
    );
}

vec3 rotY(vec3 p, float a) {
    float s = sin(a);
    float c = cos(a);
    return vec3 (
        c*p.x + s*p.z,
        p.y,
        -s*p.x + c*p.z
    );
}

iavec3 iavec3_new(iafloat x, iafloat y, iafloat z) {
	return iavec3(x, 0.0, y, 0.0, z, 0.0);
}

iavec3 iavec3_new(float x, float y, float z) {
	return iavec3(x, x, 0.0, y, y, 0.0, z, z, 0.0);
}

iavec3 iavec3_new(vec3 p) {
	return iavec3(p.xx, 0.0, p.yy, 0.0, p.zz, 0.0);
}

iavec3 iavec3_new(vec3 p0, vec3 p1) {
	return iavec3(p0.x, p1.x, 0.0, p0.y, p1.y, 0.0, p0.z, p1.z, 0.0);
}

iafloat imin(iafloat a, iafloat b) {
	return iafloat(min(a.x,b.x),min(a.y,b.y));
}

iafloat imax(iafloat a, iafloat b) {
	return iafloat(max(a.x,b.x),max(a.y,b.y));
}
iafloat iabs(iafloat a) {	
	return (a.x>=0.0)?
        iafloat(a):
        (a.y<0.0) ? 
            iafloat(abs((a).yx)):
            iafloat(0.0,max(abs(a.x),abs(a.y)));
}



iafloat iadd(iafloat a, iafloat b) {
	return a + b;
}

iavec3 iadd(iavec3 a, iavec3 b) {
	return iavec3_new(
		a[0].xy + b[0].xy,
		a[1].xy + b[1].xy,
		a[2].xy + b[2].xy);
}

iavec3 iadd(vec3 a, iavec3 b) {
	return iavec3_new(
		a.xx + b[0].xy,
		a.yy + b[1].xy,
		a.zz + b[2].xy);
}

iafloat isub(iafloat a, iafloat b) {
	return a - b.yx;
}

iavec3 isub(iavec3 a, iavec3 b) {
	return iavec3_new(
		a[0].xy - b[0].yx,
		a[1].xy - b[1].yx,
		a[2].xy - b[2].yx);
}

iavec3 isub(iavec3 a, vec3 b) {
	return iavec3_new(
		a[0].xy - b.xx,
		a[1].xy - b.yy,
		a[2].xy - b.zz);
}

iafloat imul(iafloat a, iafloat b) {
	vec4 f = vec4(
		a.xxyy * b.xyxy
	);	
	return iafloat(
		min(min(f[0],f[1]),min(f[2],f[3])),
		max(max(f[0],f[1]),max(f[2],f[3])));
}

iafloat imul(float a, iafloat b) {
	vec2 f = vec2(a*b);	
	return iafloat(
		min(f[0],f[1]),
		max(f[0],f[1]));
}

iavec3 imul(iavec3 a, iavec3 b) {
	return iavec3_new(
		imul(a[0].xy, b[0].xy),
		imul(a[1].xy, b[1].xy),
		imul(a[2].xy, b[2].xy)
	);
}

iavec3 imul(float a, iavec3 b) {
	return iavec3_new(
		imul(a, b[0].xy),
		imul(a, b[1].xy),
		imul(a, b[2].xy)
	);
}

iavec3 imul(vec3 a, iavec3 b) {
	return iavec3_new(
		imul(a.xx, b[0].xy),
		imul(a.yy, b[1].xy),
		imul(a.zz, b[2].xy)
	);
}

iavec3 imul(vec3 a, iafloat b) {
	return iavec3_new(
		imul(a.x, b),
		imul(a.y, b),
		imul(a.z, b)
	);
}


iafloat idiv(iafloat a, iafloat b) {
	vec4 f = vec4(
		a.x/b, a.y/b
	);
	return iafloat(
		min(min(f[0],f[1]),min(f[2],f[3])),
		max(max(f[0],f[1]),max(f[2],f[3])));
}

iavec3 idiv(iavec3 a, iavec3 b) {
	return iavec3_new(
		idiv(a[0].xy, b[0].xy),
		idiv(a[1].xy, b[1].xy),
		idiv(a[2].xy, b[2].xy)
	);
}

iafloat isqrt(iafloat a) {
	return iafloat(sqrt(a.x),sqrt(a.y));
}

// for even powers in general,
// for odd powers, it's just iafloat(a*a)
iafloat ipow2(iafloat a) {	
	return (a.x>=0.0)?
        iafloat(a*a):
        (a.y<0.0) ? 
            iafloat((a*a).yx):
            iafloat(0.0,max(a.x*a.x,a.y*a.y));
}

iavec3 ipow2(iavec3 v) {
	return iavec3_new(
		ipow2(v[0].xy),
		ipow2(v[1].xy),
		ipow2(v[2].xy));
}

iafloat ilensq(iavec3 a) {
	iavec3 c = ipow2(a);
	return c[0].xy + c[1].xy + c[2].xy;
}

iafloat ilength(iavec3 a) {
	iavec3 c = ipow2(a);
	return isqrt(c[0].xy + c[1].xy + c[2].xy);
}

iafloat idot(iavec3 a, iavec3 b) {
	iavec3 c = imul(a,b);
	return c[0].xy + c[1].xy + c[2].xy;
}

bool icontains(iafloat a, float v) {
	return ((v >= a.x) && (v < a.y));
}

iafloat icos(iafloat a) {
    float x = cos(a.x);
    float y = cos(a.y);
    return iafloat(min(x, y), max(x, y));
}

/*
iafloat imod(iafloat a, float b) {

}
*/
iafloat imod(float a, iafloat x) {
    vec2 x1Segment = vec2(ceil(a/abs(x.x)), floor(a/abs(x.x)));
    vec2 x2Segment = vec2(ceil(a/abs(x.y)), floor(a/abs(x.y)));

    if (abs(x1Segment.x - x2Segment.x) < EPSILON && abs(x1Segment.y - x2Segment.y) < EPSILON)
        if (x.x >= 0.)
            return vec2(mod(a, x.y), mod(a, x.x));
        else
            return vec2(mod(a, x.x), mod(a, x.y));
    else
        if (x.x >= 0.)
            return vec2(0, mod(a, x2Segment.x));
        else if (x.y > 0.)
            return vec2(mod(a, -x1Segment.x), mod(a, x2Segment.x));
        else 
            return vec2(mod(a, -x1Segment.x), 0);
}
//////////////////////////////////////////////////////////

iafloat sphere(iavec3 p, float r) {
	// x^2 + y^2 + z^2 - r^2
	return isub(ilensq(p),iafloat(r*r));
}

iafloat teardrop(iavec3 p) {
	// -0.5x^5 - 0.5x^4 + y^2 + z^2

	iafloat x2 = ipow2(p[2].xy);
	iafloat y2 = ipow2(p[1].xy);
	iafloat z2 = ipow2(p[0].xy);

	iafloat x4 = ipow2(x2);
	iafloat x5 = imul(p[2].xy,x4);

	return iadd(iadd(isub(imul(-0.5,x5), imul(0.5,x4)), y2), z2);	
}

iafloat cube(iavec3 p, float r) {
    // max( |x|, max( |y|, |z|) ) - r
    return isub( imax( iabs(p[0].xy) , imax(iabs(p[1].xy), iabs(p[2].xy)) ) , iafloat(r));
}

iafloat dcos(iavec3 p) {
    // cos(x) - y
    return isub(icos(p[0].xy), p[1].xy);
}

iafloat dmod(iavec3 p, float a) {
    // mod(a, x) - y
    return isub(imod(a, p[0].xy), p[1].xy);
}

iafloat oct(iavec3 p, float r) {
    // |x| + |y| + |z| - r
    return isub( iadd( iabs(p[0].xy) , iadd(iabs(p[1].xy), iabs(p[2].xy)) ) , iafloat(r));    
}

iafloat steiner(iavec3 p) {
     // x^2.y^2 + y^2.z^2 + x^2.z^2 + xyz     
	iafloat x2 = ipow2(p[2].xy);
	iafloat y2 = ipow2(p[1].xy);
	iafloat z2 = ipow2(p[0].xy);    
    return iadd( iadd(imul(x2, y2), imul(x2, y2)), iadd(imul(x2, y2), imul(p[0].xy, imul(p[1].xy, p[2].xy))));
}

iafloat dtest1(iavec3 p) {
    // x^2 - y^2 + |z|
    return iadd(isub(ipow2(p[0].xy), ipow2(p[1].xy)), iabs(p[2].xy));
}

iafloat dtest2(iavec3 p) {
    // x^2 - |y| + |z|
    return iadd(isub(ipow2(p[0].xy), iabs(p[1].xy)), iabs(p[2].xy));
}

iafloat dtest3(iavec3 p) {
    // (x^2)^2 - 2xy + z^2  = 0
    return iadd(isub(ipow2(ipow2(p[0].xy)), imul(2., imul(p[0].xy, p[1].xy))), ipow2(p[2].xy));
}

iafloat dtest4(iavec3 p) {
    // x^5-5x - y  = 0
    return isub(isub(imul(imul(imul(imul(p[0].xy, p[0].xy), p[0].xy), p[0].xy), p[0].xy), imul(5., p[0].xy)), p[1].xy);
}

