// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
// https://www.shadertoy.com/view/wdXGDr
vec3 calcNormal( in vec3 pos, int o )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.0005;
    return normalize( e.xyy * map(iavec3_new(pos + e.xyy*eps), o).x + 
					  e.yyx * map(iavec3_new(pos + e.yyx*eps), o).x + 
					  e.yxy * map(iavec3_new(pos + e.yxy*eps), o).x + 
					  e.xxx * map(iavec3_new(pos + e.xxx*eps), o).x );
}


#define LIGHTING 0
#define BACKGROUND 1
#define OBJECTS 2
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 m = iMouse.xy;

    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;  

     // camera movement	
	vec2 an = vec2(10. * m.x / iResolution.x, 5. * (m.y / iResolution.y - 0.5));

    vec3 ro0 = vec3(5., 0., 0.);
    vec3 ta = vec3( 0., 0., 0. );


    vec3 ro = rotZ(ro0, min(abs(an.y), PI / 2.0001) * sign(an.y));
    ro = rotY(ro, an.x);

    // camera matrix

    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));


    vec3 col = vec3(.7);
    #if BACKGROUND==0
        col = vec3((p.x + 1.) / 15., (p.y + 1.) / 15., 0.4);
    #elif BACKGROUND==1
        col = vec3((p.x + 1.) / 15., (p.y + 1.) / 15., 0.4);
        float greyscale = (col.r + col.g + col.b) / 3.; 
        col = (col + vec3(greyscale)) * 0.5;
    #elif BACKGROUND==2
        float gradient =  2. * ww.y + p.y;
        col = vec3(0.3 * gradient, 0.5 * gradient, 0.3 * gradient + .5);  
    #endif    
    const vec3 lightCol = vec3(0.8, 0.8, 0.7);
    const vec3 ambCol = vec3(0.2);
    bool hasObj = false; // if there is a non background colour on this pixel


    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );

    const float maxt = 30.1;
	const float mine = 1e-3;    


    for (int o = 0; o < OBJECTS; o++) {	
        iafloat t = iafloat(0.0, maxt); // t is the range to look at

        bool hit = false;	
        bool shouldBreak = false;
        const int maxI = 256;
        float thMin = maxt;
        for (int i = 0; i < maxI; i++) {	
            float th = (t.x + t.y) * 0.5;
            thMin = min(thMin, th);

            iafloat t0 = iafloat(t.x, th); // first half of segment
            iafloat t1 = iafloat(th, t.y); // second half

            iafloat d0 = map(iadd(ro, imul(rd, t0)), o); // map(ro + rd*t0)
            iafloat d1 = map(iadd(ro, imul(rd, t1)), o);
            if (icontains(d0, 0.0)) {
                t.y = th;
                if (max(abs(d0.y),abs(d0.x)) < mine) {
                    hit = true;
                    break;
                }
            } else if (icontains(d1, 0.0)) {
                t.x = th;
                if (max(abs(d1.y),abs(d1.x)) < mine) {
                    hit = true;
                    break;
                }
            } else {
                // nudge to back, double interval
                float d = (t.y-t.x);
                t.x += d;
                t.y += d*2.0;
                if (t.x >= maxt)
                    break;
            }

        }
        if (hit) {    
            vec3 nor = calcNormal(ro + rd * (t.x + t.y) * 0.5, o);               
            vec3 thisCol = nor / 2. + .5;
            if (hasObj)
                col *= thisCol * 3.;
            else {
                col = thisCol;
                hasObj = true;
            }
        }
    }


    col = sqrt( col );


    fragColor = vec4(col, 1.);

}

vec3 iColor = vec3(1., 0.5, 0.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
