#define EPSILON 0.01
#define MIN_DISTANCE 0.1 
#define MAX_DISTANCE 100.0
#define MAX_MARCHING_STEP 200

#define CAM_ROTATION 1
#define MOUSE_CAM_ROT 0
#define HEAD_ROTATION 0                 
#define SHADOWS 1                       //change it to 0 to disable shadows

#define DARK_ID 0.
#define CLOTH_ID 1.
#define SKIN_ID 2.
#define WHITE_ID 3.
#define GROUND_ID 4.

#define time iTime*4.
#define PI 3.14159265

struct Ray {
    vec3 o; // origin
    vec3 d; // direction, always normalized
};

struct Camera {
    vec3 pos;
    vec3 lookAt;
    float focal;
    vec2 sensorSize;
};

struct Light {
    vec3 pos;
    vec3 color;
};

vec3 xe = vec3(EPSILON, 0., 0.);
vec3 ye = vec3(0., EPSILON, 0.);
vec3 ze = vec3(0., 0., EPSILON);

float max3 (vec3 v) {
  return max (max (v.x, v.y), v.z);
}

mat2 rotMat(float a){
    float s = sin(a);
    float c = cos(a);
    return mat2 (c,-s,s,c);
}


// get corresponding ray from uv (0.0 -> 1.0)
Ray getRay( Camera cam, vec2 uv ) {
    vec3 camDir = normalize(cam.lookAt - cam.pos); // the camera direction
    vec3 center = cam.pos + cam.focal * camDir; // get the coordinate of the screen center
    vec2 coord = (uv - 0.5) * cam.sensorSize; // convert from 0.0 -> 1.0 to -sensorSize / 2. -> sensorSize / 2.

    // axis relative to camera
    vec3 zpos = -camDir; // k hat, outward the screen
    vec3 xpos = cross(vec3(0., 1., 0.), zpos); // i hat, cross from ypos global and zpos local
    vec3 ypos = cross(zpos, xpos); // j hat, cross from zpos local and xpos local

    // target in screen
    vec3 target = center + coord.x * xpos + coord.y * ypos; // get pixel target coordinate from the screen

    Ray ray;
    ray.o = cam.pos; // the origin of the ray is from the camera duh
    ray.d = normalize(target - cam.pos); // the direction is from the camera to the target

    return ray;
}

// project point to ray
vec3 projPoint2Ray( vec3 point, Ray ray ) {
    vec3 rel = point - ray.o; // get relative vector from the point to the ray origin
    float len = dot(rel, ray.d); // project that vector to vector direction of the ray, we get the length
    return len * ray.d + ray.o; // multiply the length with unit vector direction and add to ray origin
}

// get the distance from point to ray (or line)
float distPoint2Ray( vec3 point, Ray ray ) {
    vec3 proj = projPoint2Ray(point, ray); // project it first to the ray
    return distance(proj, point); // get the distance from the projected position to the original position
}

// just linear interpolation, map value v between fromA and fromB to between toA and toB
float lerp( float v, float fromA, float fromB, float toA, float toB ) {
    return (v - fromA) / (fromB - fromA) * (toB - toA) + toA;
}


// box function
float sdBox( vec3 p, vec3 s ) {
    vec3 d = abs(p) - s;
    return max(d.x, max(d.y, d.z));
}


float sdPlane( vec3 p, vec3 n, float h )
{
  // n must be normalized
  return dot(p,n) + h;
}

vec3 opRep( in vec3 p, in vec3 c )
{
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    return q ;
}

float add (inout vec2 a, vec2 b){
    a = mix(a,b,step(b.x,a.x));
    return 0.;
}

vec2 sdScene( vec3 p ) {
    vec3 armSize = vec3 (.2,.4,.2);                           //Arms Size
    vec3 handSize = vec3 (.2,.2,.13);          
    vec3 legSize = vec3 (.22,.8,.2);
    vec3 bodySize = vec3 (.6,1.,.3);
    vec3 headSize = vec3(.3,.35,.3);
    vec3 footSize = vec3(.2,.1,.43);
    vec3 capSize = vec3(.36,.1,.45);

    vec3 clothColor = vec3(0.027,0.27,0.05);
    vec3 skinColor = vec3(0.764,0.6,0.552);

    vec3 pG = p;
    pG.z += mod(time,4.);                                                //Global Position
    pG = opRep(pG,vec3(3,0,4));
    vec3 pB = pG;                                               //Body Position
    vec3 pH = pG -vec3(0,bodySize.y+headSize.y+0.05,0);         //Head Position
#if HEAD_ROTATION 
    pH.xz *= rotMat(-sin(time)*.5);                               //Head Rotation
#endif
    vec3 pCap = pH - vec3(0,headSize.y+capSize.y-.05,-capSize.z/3.3);                                               
    vec3 pShouldL = pG - vec3 (-(bodySize.x+armSize.x+0.05),bodySize.y,0);   //Shoulder Position
    pShouldL.yz *= rotMat(sin(time));                          //Shoulder Rotation    
    vec3 pArmL = pShouldL + vec3 (0,armSize.y,0);               //Arm Position
    vec3 pForeL = pShouldL-vec3(0,-2.*armSize.y-0.05,0);                       //Forearm Position
    pForeL.xz *= rotMat(radians(90.));                           //Forearm Rotation
    pForeL.yz *= rotMat(clamp(-sin(time)*PI/2.,0.,PI/2.));
    pForeL += vec3(0,armSize.y,0);                             //Forearm Anchor P reposition
    vec3 pHandL = pForeL-vec3(0,-armSize.y-handSize.y-0.05,0);
    pHandL.xz *= rotMat(-1.*clamp(-sin(time)*PI,0.,PI/2.)+PI);    
    vec3 pLegL = pB - vec3(-.4,-bodySize.y-.05,0);
    pLegL.yz *= rotMat(-sin(time));
    pLegL += vec3(0,legSize.y,0);
    vec3 pFootL = pLegL - vec3(0,-legSize.y-footSize.y-0.05,-footSize.z/2.);

    vec3 pShouldR = pG - vec3(bodySize.x+armSize.x+0.05,bodySize.y,0);
    pShouldR.yz *= rotMat(-sin(time));
    vec3 pArmR = pShouldR + vec3 (0,armSize.y,0);;
    vec3 pForeR = pShouldR-vec3(0,-2.*armSize.y-0.05,0);
    pForeR.xz *= rotMat(radians(-90.));
    pForeR.yz *= rotMat(clamp(sin(time)*PI/2.,0.,PI/2.));
    pForeR += vec3(0,armSize.y,0);
    vec3 pHandR = pForeR-vec3(0,-armSize.y-handSize.y-0.05,0);
    pHandR.xz *= rotMat(-1.*clamp(-sin(time)*PI,-PI/2.,0.)+PI);
    vec3 pLegR = pB - vec3(.4,-bodySize.y-.05,0);
    pLegR.yz *= rotMat(sin(time));
    pLegR += vec3(0,legSize.y,0);
    vec3 pFootR = pLegR - vec3(0,-legSize.y-footSize.y-0.05,-footSize.z/2.);


    vec2 body = vec2(sdBox(pB,bodySize),CLOTH_ID);
    vec2 head = vec2(sdBox(pH,headSize),SKIN_ID);
    vec2 cap = vec2(sdBox(pCap,capSize)*.5,CLOTH_ID);                      

    vec2 armL = vec2(sdBox (pArmL,armSize),CLOTH_ID); 
    vec2 foreL = vec2(sdBox(pForeL,armSize),CLOTH_ID);
    vec2 handL = vec2(sdBox(pHandL,handSize),WHITE_ID);
    vec2 legL = vec2(sdBox(pLegL,legSize),CLOTH_ID);
    vec2 footL = vec2(sdBox(pFootL,footSize)*.5,DARK_ID);

    vec2 armR = vec2(sdBox(pArmR,armSize),CLOTH_ID);
    vec2 foreR = vec2(sdBox(pForeR,armSize),CLOTH_ID);
    vec2 handR = vec2(sdBox(pHandR,handSize),WHITE_ID);
    vec2 legR = vec2(sdBox(pLegR,legSize),CLOTH_ID);
    vec2 footR = vec2(sdBox(pFootR,footSize)*.5,DARK_ID);

    vec2 floor = vec2(sdPlane(p,vec3(0,1.,0),3.),GROUND_ID);
    // vec2 floor = vec2(p.y+3,GROUND_ID);

    vec2 result = body;
    // result = mix(result,head,step(head.x,result.x));
    // result = mix(result,cap,step(cap))
    add (result,head);
    add (result,cap);
    add (result,armL);
    add (result,foreL);
    add (result,handL);
    add (result,legL);
    add (result,footL);
    add (result,armR);
    add (result,foreR);
    add (result,handR);
    add (result,legR);
    add (result,footR);
    add (result,floor);

    return result;
}

// estimate normal with nudging
vec3 estimateNormal( vec3 p ) {
    return normalize(vec3(
        sdScene(p+xe).x - sdScene(p-xe).x,
        sdScene(p+ye).x - sdScene(p-ye).x,
        sdScene(p+ze).x - sdScene(p-ze).x
    ));
}

// ray marching algorithm
vec2 getSDF( Ray r ) {
    float depth = MIN_DISTANCE;
    float matID;
    for (int i=0; i<MAX_MARCHING_STEP; i++) {
        float dist = sdScene( r.o + depth * r.d ).x;
        matID = sdScene (r.o + depth * r.d).y;

        if (dist < EPSILON) { // inside a scene
            return vec2(depth,matID);
        }

        depth += dist; // circle marching!

        if (depth >= MAX_DISTANCE) {
            return vec2(MAX_DISTANCE,matID);
        }
    }

    return vec2(MAX_DISTANCE,matID);
}

vec3 ambientLight( vec3 ambientColor ) {
    return ambientColor;
}

vec3 phongLight( vec3 diffuseColor, vec3 specularColor, float shininess, Ray ray, float dist, Light light ) {
    vec3 p = ray.o + dist * ray.d;

    vec3 N = estimateNormal(p); // surface normal
    vec3 L = normalize(light.pos - p); // surface to light direction
    vec3 V = -ray.d; // surface to camera direction
    vec3 R = reflect(-L, N); // mirror of L by the axis N

    float dotLN = dot(L, N); // project light direction to surface normal
    float dotRV = dot(R, V); // project light reflection direction to direction to camera

    vec3 color = vec3(0.);
    if (dotLN > 0.) { // we can see the diffuse
        color += diffuseColor * dotLN;
    } 
    if (dotRV > 0.) { // we can see the specular
        color += specularColor * pow(dotRV, shininess);
    }
    color *= light.color; // influence of color of the light
#if SHADOWS
    float dif = clamp(dotLN*.5+0.5,0.,1.);
    vec3 n = estimateNormal(p);
    Ray shadow;
    shadow.o = p+n*EPSILON;
    shadow.d = L; 
    float d = getSDF(shadow).x;
    if (d<length(light.pos-p)) color *=.1;
#endif

    return color;
}


vec3 calcMaterial (float id) {
    if (id == DARK_ID)      return vec3(.1, .1, .1);
    if (id == CLOTH_ID)     return vec3(61., 74., 37.)/256.;
    if (id == SKIN_ID)     return vec3(0.764,0.6,0.552);
    if (id == WHITE_ID)     return vec3(1.);
    if (id == GROUND_ID)     return vec3(.4);
    return vec3(.2);
}




