    cam.sensorSize = iResolution.xy; // set sensor size to the screenSize
    cam.focal = 400.; // set the focal length (distance between sensor (screen) to the aperture
    cam.lookAt = vec3(0., 1., 0.); // where to look at (this camera use two point system)
    cam.pos = vec3(0,4,-4.2);//circleMovement; // the position of the camera#if CAM_ROTATION
#if CAM_ROTATION
    cam.pos = vec3 (sin(time*.1)*8.,7.+sin(time*.05)*4.,cos(time*.1)*8.);
#endif
