const float levels = 4.; // number of quantisation levels, must be at least 2

uint hash(uint x)
{
    x ^= x >> 16; x *= 0x7feb352du; 
    x ^= x >> 15; x *= 0x846ca68bu; 
    x ^= x >> 16;
    return x;
}

float hash2tf(uint x, uint y)
{
    // take a hash, split it into components
    uint h = hash(uint(x)+(uint(y)<<16));

    return float((h>>16)+(h&0xffffu)) / float(0xffffu);
}

vec3 dither(vec3 c, uvec2 xy)
{
    uint t = uint(iFrame);
    // compute noise
    float h = hash2tf(xy.x + t*12345u, xy.y) - 1.;

    // shift the rows of the ordered dither every other frame
    // this gives a more even checkerboard pattern over time
    h += float( ((xy.x^xy.y)&1u)*2u + ((t^xy.y)&1u)) - 1.75;

    // normalizing to 1 step allows some banding in the ordered dither
    // dividing by 2 instead should mostly avoid this at the cost of more noise
    h *= 1./4.;

    float g = levels-1.;
    return clamp(round(c*g + h) / g, 0.,1.);
}

