vec2 h22(vec2 v) {
	vec3 p = fract(v.xyx * vec3(.1031, .103, .0973));
	p += dot(p, p.yzx + 33.33);
	return fract((p.xx + p.yz) * p.zy);
}

vec2 map(vec3 p);
float shadow(vec3 p, vec3 lp) {
	float d,
	      s = 1.,
	      t = .1,
	      mxt = length(p - lp);
	vec3 ld = (lp - p) / mxt;
	for (float i = Z0; i < 2e2; i++) {
		d = map(t * ld + p).x;
		s = min(s, 15. * d / t);
		t += max(.1, d);
		if (mxt - t < .5 || s < .001) break;
	}
	return S01(s);
}

float ao(vec3 p, vec3 n, float h) { return map(h * n + p).x / h; }
