vec2 plant(vec3 p,vec2 center) {
    const float sh=.3; //shift
    vec2 a =h22(center*1.1); 
    float
     s =sin(a.y*20.+iTime/8.),
     h = 5.5- s*s*s*s*3. +a.y*1.5, //height
     v = .51 -.5*s,  //color
     tk = max(+.05,+.85*smoothstep(.1,.4,v)* (1.-.1*fract(p.z*3.)-.1*fract(p.z*3.+.1))* smoothstep(h+.7,h-4.,p.z) *smoothstep(.1,0.2,p.z-3.7));  //thickness
    vec3 s1= vec3(sin(a.x*25.),cos(a.x*25.),0)*sh; //bottom
    vec3 s2= s1 + vec3(0,0,h); //top

    return vec2(sdCapsule(p- vec3(center, 0),s1,s2,tk),2.*v-v*v);
}

#define mmin(a,b) (a.x<b.x?a:b)


vec2 edge(vec2 p) { //modified
    return vec2((p.x < 0.) ? -1. : 1., (p.y < 0.) ? -1. : 1.);
}

vec2 map(vec3 p) {
    vec2  d= vec2(p.z-3.7,0.);
    vec2 center = floor(p.xy) + .5;
    vec2 dir=edge(p.xy - center);
    for(float i = -0.;i<2.;i++)for(float j = -0.;j<2.;j++)
    {
        vec2 pos = center + dir*vec2(i,j);    
         //d = smin(d, plant(p,pos),0.2); 
         d = mmin(d, plant(p,pos)); 
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fc )
{
    vec2 uv = (fc-.5*R.xy)/R.y;
    vec2 mouse = (iMouse.xy-0.5*R.xy)/R.y;
    vec3 cam = normalize(vec3(1.5,uv));
    vec3 ro = vec3(-25,0,10);    
    float yrot = .45, zrot = iTime*.05;
    if (iMouse.z > 0.) {
        yrot = clamp(1.-2.*mouse.y,-0.,3.14/2.);
        zrot = 4.*mouse.x;
    }
    cam = erot(erot(cam,vec3(0,1,0),yrot),vec3(0,0,1),zrot);

    vec3 p =ro;
    bool hit = false;
    vec2 d;
    for (int i = 0; i < 100 && !hit; i++) {
       d = map(p);

        //if inside a box, don't march after border
        vec3 center = vec3(floor(p.xy) + .5,10.);
        vec2 tb= boxIntersection(  p-center,  cam, vec3(.501,.501,10.) ) ;
        if( tb.x<0. &&tb.y>0. && tb.y<d.x) d.x=tb.y;       

        hit = d.x*d.x < 1e-6;
        p += d.x*cam;
        if(distance(p,ro)>30.)break;
    }
    vec3 n = norm(p);


    vec3 lp=vec3(100, 200, 200), ld = normalize(lp - p);
    vec3 col= mix(vec3(0.3,0.,0.),vec3(0.,.1,0.02), smoothstep(.1,.4,d.y)) ;
    if(d.y<=0.){
        n = doBumpMap( iChannel2, (p)*.25, n, 0.01);
        col=min(vec3(0.5,0.8,0.0),tex3D( iChannel2, (p)*.25, n));
    } else 
    {
       n = doBumpMap( iChannel1, (p.zyx)*.25, n, 0.03*d.y);
    }


    float ao = sat(mix(ao(p, n, .1), ao(p, n, .5), .7)),
	      l1 = sat(.1 + .9 * dot(ld, n)) * (.05 + .95 * shadow(p, lp)) * (.3 + .7 * ao),
	      l2 = sat(.1 + .9 * dot(ld * vec3(-1, 0, -1), n)) * .2 + pow(sat(dot(cam, reflect(ld, n))), 10.) * .05;          
    col*=(l1 + l2 * ao);

    col = mix(col,vec3(0.835,0.851,0.549),exp(min(0.,-5.+distance(ro,p)/5.))); //fog  
    fragColor =  vec4(sqrt(col),0) ;

}

