        float cosTi = max(dot(n, l[i]), 0.0);
        float cosTh = max(dot(n, h), 0.0);
        Lo += (Kd.xyz + Ks.xyz * pow(cosTh, m)) * El * cosTi; // Outgoing radiance
    }

    return Lo; 

}

vec3 RimLight(vec3 Lo,float r,vec2 center,vec2 pos)
{
    float det = r * r - pos.x * pos.x - pos.y * pos.y;
    if(det < 0.0){
        return Lo;
    }
    float z = sqrt(det);

    vec3 n = normalize(vec3(pos.x, pos.y, z)); 		// Surface normal

    float param = 1.0 - max(0.0, dot(n, normalize(vec3(center.xy, 1.0))));

    return Lo + vec3(pow(param, 3.0) * 1.5);

}

