}

vec3 camPos = vec3(0, 0, 0);
vec3 camDir = vec3(0, 0, -1);
vec3 camUp = vec3(0, 450. / 800., 0);
float camF = 0.5;
float rtStep = 0.4;
float rtlStep = 0.025;
float rtglStep = 0.3;
const int maxSteps = 32;
const int maxlSteps = 32;
const int maxglSteps = 32;

float time;

vec3 lightPos = vec3(4, 3, -1);

vec3 INVALID = vec3(1e10, 1e20, -1e30);

float smin(float a, float b)
{
    return (a + b - log(exp(a - b) + exp(b - a))) / 2.;
}

float rand(vec2 co)
{
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 rayDir(vec2 uv)
{
    vec3 camRight = normalize(cross(camDir, camUp));
    return normalize(camDir * camF + (uv.y - 0.5) * 2. * camUp + (uv.x - 0.5) * 2. * camRight);
}

vec3 orbit(vec3 normal, float dist, float speed, float offset)
{
    vec3 v1 = normalize(cross(normal, vec3(0.42, 0.1, -0.1)));
    vec3 v2 = normalize(cross(normal, v1));
    return dist * (v1 * cos(time * speed + offset) + v2 * sin(time * speed + offset));
}

float scene(vec3 pos)
{
    vec3 ballCenter = vec3(0, 0, -5);
    float ballRadius = 2.;
    float result =  distance(pos, ballCenter) - ballRadius + sin(pos.y * 5. + time) / 3. * cos(time * 0.2);
    vec3 ballCenter2 = ballCenter + orbit(vec3(0, 1, 0), 3., 1., 0.);
    result = smin(result, distance(pos, ballCenter2) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(-4, 12, 0), 2.7, 1.7, 0.2)) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(-4, 2, 5), 2.7, 1.8, 0.2)) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(8, 7, -8), 2.7, 1.9, 0.2)) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(-14, -5, -1), 2.7, 2.0, 0.2)) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(1, 12, 0), 2.7, 0.8, 0.8)) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(-7, -5, 8), 2.6, 1.7, 0.1)) - 0.3);
    result = smin(result, distance(pos, ballCenter + orbit(vec3(-1, 1, 22), 2.5, 1.2, 0.2)) - 0.3);
    result = smin(result, distance(pos, ballCenter2 + orbit(vec3(0.1, -0.5, -1), 0.8, -0.41, -0.2)) - 0.15);
    /*
    result -= (dot(pos - ballCenter, normalize(vec3(1, 3, 7)))) * 0.1;
    result -= (dot(pos - ballCenter, normalize(vec3(-7, 2, 1)))) * 0.1;
    result -= (dot(pos - ballCenter, normalize(vec3(2, -1, 0.3)))) * 0.1;
    result -= (dot(pos - ballCenter, normalize(vec3(4, 3, -1)))) * 0.1;
    */
    // result += 1./distance(pos, vec3(-2, -2, -7));
    result = 2. - abs(result - 2.);
    result = smin(result, length((pos - ballCenter).xz) - 0.3);
    return result;
}

vec3 sceneNormal(vec3 pos)
{
    float d = 0.001;
    float base = scene(pos);
    return normalize(vec3(
        scene(pos + vec3(d, 0, 0)) - base,
        scene(pos + vec3(0, d, 0)) - base,
        scene(pos + vec3(0, 0, d)) - base
    ));
}

vec4 rt(vec3 rayPos, vec3 rayDir)
{
    float lastVal = 0.;
    float retVal = 0.;
    float stp = rtStep;
    for(int i = 0; i < maxSteps; i++)
    {
        float val = scene(rayPos);
        if(val < 0.001)
        {
            retVal = 1.;
        }
        if(sign(val * lastVal) == -1.)
            stp /= 2.;
        lastVal = val;
        rayPos += rayDir * stp * sign(val);
    }
    return vec4(rayPos, 1.);
}

vec4 rtl(vec3 rayPos, vec3 rayDir, float maxDist)
{
    float lastVal = 0.;
    float tot = 0.;
    for(int i = 0; i < maxlSteps; i++)
    {
        float val = scene(rayPos);
        if(maxDist <= 0.1 || val > 40.)
        {
            float a = -val / (lastVal - val);
            rayPos -= rtStep * rayDir * a;
            return vec4(rayPos, tot);
        }
        if(val <= 0.001)
            tot += rtlStep * (-val);
        lastVal = val;
        float step = rtlStep;
        maxDist -= length(rayDir) * step;
        rayPos += rayDir * step;
    }
    return vec4(rayPos, tot);
}

float rtgl(vec3 rayPos, vec3 rayDir)
{
    float lastVal = 0.;
    float result = 0.;
    for(int i = 0; i < maxglSteps; i++)
    {
        float val = scene(rayPos);
        if(val < 0. || val > 40.)
        {
            float a = -val / (lastVal - val);
            // val = (lastVal + val) / 2.;
            //result += rtStep * smoothstep(0.1, 0.2, val) * (1. - smoothstep(0.2, 0.3, val)) * val * a;
            return result;
        }
        //if(lastVal > 0.1 && val < 0.09)
        //    result += rtStep * val * 3.;
        result += rtglStep * smoothstep(0.1, 0.2, val) * (1. - smoothstep(0.2, 0.3, val)) * val * val;
        // result += min(10., 1. / pow(val, 2.2) * smoothstep(0., 1., (val - 0.05) / 0.1)) * rtStep;
        rayPos += rayDir * rtglStep * val;
        lastVal = val;
    }
    return result;
}

