const float period = 48.0;
const float dX = 4.8;
const float offsetMoonY = 0.5;
const float dY = 0.2;

const vec3 offset=vec3(1.5,2.,-2.5);

#define PI 3.14159265358979

// Origin (https://www.shadertoy.com/view/ldXXDj) created by inigo quilez - iq/2014
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

float fbm( vec2 p )
{
    return 0.5000*texture( iChannel1, p*1.00 ).x + 
           0.2500*texture( iChannel1, p*2.02 ).x + 
           0.1250*texture( iChannel1, p*4.03 ).x + 
           0.0625*texture( iChannel1, p*8.04 ).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )

{	float generalTime = iTime / 54.35;
	vec2 p = (-iResolution.xy+2.0*gl_FragCoord.xy) / iResolution.y;
    vec2 i = p;

    // camera
    p += vec2(1.0,3.0)*0.001*2.0*cos( generalTime*5.0 + vec2(0.0,1.5) );    
    p += vec2(1.0,3.0)*0.001*1.0*cos( generalTime*9.0 + vec2(1.0,4.5) );    
    p *= 0.85 + 0.05*length(p);

    // water
    vec2 q = vec2(p.x,1.0)/p.y;
    q.y -= 0.9*generalTime;    
        vec2 off = texture( iChannel0, 0.1*q*vec2(1.0,2.0) - vec2(0.0,0.007*iTime) ).xy;
    q += 0.4*(-1.0 + 2.0*off);
    vec3 col = 0.2*sqrt(texture( iChannel0, 0.05*q *vec2(1.0,4.0) + vec2(0.0,0.01*iTime) ).zyx);
    col *= 0.4;

    float dLight = dX - 0.6;
    float lightReflect = (dLight/period)*(mod(iTime,period))-(dLight/2.0);

    float re = 1.0-smoothstep( 0.0, 0.7, abs(p.x-lightReflect) - abs(p.y)*0.5+0.2 );
    col += 1.0*vec3(1.0,0.9,0.73)*re*0.2*off.y*5.0*(1.0-col.x);
    float re2 = 1.0-smoothstep( 0.0, 2.0, abs(p.x-0.6) - abs(p.y)*0.85 );
    col += 0.7*re2*smoothstep(0.35,1.0,texture( iChannel1, 0.075*q *vec2(1.0,4.0) ).x);

    // sky
    vec3 sky = vec3(0.01, 0.01, 0.1) * 1.4;

    // stars    
    sky += 0.5*smoothstep( 0.95,1.00,texture( iChannel1, 0.25*p ).x);
    sky += 0.5*smoothstep( 0.85,1.0,texture( iChannel1, 0.25*p ).x);
    sky += 0.2*pow(1.0-max(0.0,p.y),2.0);

    // clouds    
    float f = fbm( 0.002*vec2(p.x,1.0)/p.y );
    vec3 cloud = vec3(0.3,0.4,0.5)*0.7*(1.0-0.85*sqrt(smoothstep(0.4,1.0,f)));
    sky = mix( sky, cloud, 0.95*smoothstep( 0.4, 0.6, f ) );
    sky = mix( sky, vec3(0.33,0.34,0.35), pow(1.0-max(0.0,p.y),2.0) );
    col = mix( col, sky, smoothstep(0.0,0.1,p.y) );

    // horizon
    col += 0.1*pow(clamp(1.0-abs(p.y),0.0,1.0),9.0);

    // moon

    float moonPositionX = (dX / period) * mod(iTime, period) - ( dX / 2.);
    float moonPositionY = dY * cos( iTime / ( period / (2.0*PI))  + PI ) + offsetMoonY;

    float d = length(p-vec2(moonPositionX,moonPositionY));

    float g = 1.0 - smoothstep( 0.2, 0.22, d );

    float moontex = 0.82+0.1*smoothstep(0.25,0.7,fbm(0.06*p));

    vec3 moon = vec3(1.0,0.97,0.9)*(1.0-0.1*smoothstep(0.2,0.5,f));

    	moon = abs(fract((moon+offset/1.15)*.17)-.5);  // tile folding from Hot Shower by Kali

	for (int i=0; i<11; i++) {  moon = abs(moon)/dot(moon,moon)-.319; } // the magic formula for the hot flow

    col += 0.8*moon*exp(-4.0*d)*vec3(1.1,1.0,0.8);
    col += 0.2*moon*exp(-2.0*d);
    col = mix( col, moon*moontex, g );

    // postprocess
    col *= 1.4;
    col = pow( col, vec3(1.5,1.2,1.0) );    
    col *= clamp(1.0-0.3*length(i), 0.0, 1.0 );

    fragColor = vec4( col, 1.0 );
}

// Constants
#define PI 3.14159265359
#define TAU 6.28318530718

// Compiler trick to avoid loop unrolling (the compiler doesn't know about iFrame
// so it can't make this a constant which would allow it to unroll loops depending on it)
#define ZERO min(iFrame, 0)

// SDFs
float sdLine(in vec3 p, in vec3 a, in vec3 b) {
    vec3 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

float sdDisc(in vec3 p, in float r) {
    p.x = length(p.xz);
    return length(p - vec3(min(p.x, r), 0.0, p.z));
}

float sdCylinder(in vec3 p, in float h, in float r) {
    vec2 q = vec2(length(p.xz) - r, abs(p.y) - h);
    return length(max(q, 0.0)) + min(0.0, max(q.x, q.y));
}

float sdSphere(in vec3 p, in vec3 o, in float r) {
    return length(p - o) - r;
}

float sdBox(in vec3 p, in vec3 o, in vec3 b) {
    vec3 q = abs(p - o) - b;
    return length(max(q, 0.0)) + min(0.0, max(q.x, max(q.y, q.z)));
}

// 3D version of Inigo Quilez's horseshoe SDF
// I haven't put much thought into optimizing :P
// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdHorseshoe(in vec3 p, in float a, in vec3 w, in float r) {
    p.x = abs(p.x);

    float t = min(atan(p.y, p.x), a);
    vec2 aq = abs(vec2(length(p.xy - vec2(cos(t), sin(t)) * r), p.z)) - w.xz;
    float arc = length(max(aq, 0.0)) + min(0.0, max(aq.x, aq.y));

    float c = cos(a), s = sin(a);
    vec3 tq = abs(vec3(p.x * c + p.y * s - r, p.x * s - p.y * c + w.y, p.z)) - w;
    float prong = length(max(tq, 0.0)) + min(0.0, max(tq.x, max(tq.y, tq.z)));

    return min(arc, prong);
}

float sdBezier(in vec3 p, in vec3 v1, in vec3 v2, in vec3 v3) {
    vec3 c1 = p - v1;
    vec3 c2 = 2.0 * v2 - v3 - v1;
    vec3 c3 = v1 - v2;

    float t3 = dot(c2, c2);
    float t2 = dot(c3, c2) * 3.0 / t3;
    float t1 = (dot(c1, c2) + 2.0 * dot(c3, c3)) / t3;
    float t0 = dot(c1, c3) / t3;

    float t22 = t2 * t2;
    vec2 pq = vec2(t1 - t22 / 3.0, t22 * t2 / 13.5 - t2 * t1 / 3.0 + t0);
    float ppp = pq.x * pq.x * pq.x, qq = pq.y * pq.y;

    float p2 = abs(pq.x);
    float r1 = 1.5 / pq.x * pq.y;

    if (qq * 0.25 + ppp / 27.0 > 0.0) {
        float r2 = r1 * sqrt(3.0 / p2), root;
        if (pq.x < 0.0) root = sign(pq.y) * cosh(acosh(r2 * -sign(pq.y)) / 3.0);
        else root = sinh(asinh(r2) / 3.0);
        root = clamp(-2.0 * sqrt(p2 / 3.0) * root - t2 / 3.0, 0.0, 1.0);
        return length(p - mix(mix(v1, v2, root), mix(v2, v3, root), root));
    }

    else {
        float ac = acos(r1 * sqrt(-3.0 / pq.x)) / 3.0;
        vec2 roots = clamp(2.0 * sqrt(-pq.x / 3.0) * cos(vec2(ac, ac - 4.18879020479)) - t2 / 3.0, 0.0, 1.0);
        vec3 p1 = p - mix(mix(v1, v2, roots.x), mix(v2, v3, roots.x), roots.x);
        vec3 p2 = p - mix(mix(v1, v2, roots.y), mix(v2, v3, roots.y), roots.y);
        return sqrt(min(dot(p1, p1), dot(p2, p2)));
    }
}

// Hash from "Hash without Sine" by Dav_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash11(in vec2 p) {
	//vec3 p3  = fract(vec3(p.xyx) * .1031);
    //p3 += dot(p3, p3.yzx + 33.33);
    //return fract((p3.x + p3.y) * p3.z);
    return texture(iChannel0, p * 0.1).r; // Attempt to lower complexity
}

vec2 Hash22(in vec2 p) {
	vec3 p3 = fract(p.xyx * vec3(0.1031, 0.103, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

float snoise(in vec2 p) {
    vec2 cell = floor(p);
    vec2 local = fract(p);
    local *= local * (3.0 - 2.0 * local);

    float bl = Hash11(cell);
    float br = Hash11(cell + vec2(1.0, 0.0));
    float tl = Hash11(cell + vec2(0.0, 1.0));
    float tr = Hash11(cell + 1.0);

    return mix(mix(bl, br, local.x), mix(tl, tr, local.x), local.y);
}

float getTerrain(in vec2 p, in float t) {
    p *= 0.25;

    float height = 0.0;
    float noiseScale = 1.0;
    float totalScale = 0.0;

    for (int oct=ZERO; oct < 7; oct++) {
        height += snoise(p) * noiseScale;
        totalScale += noiseScale;
        noiseScale *= 0.5;
        p *= 2.0;
    }

    height /= totalScale;
    float height2 = height * height; // To reduce multiplications
    return height2 * height2 * height * 5.0;
}

// SDF Operators
vec2 pModPolar2(in vec2 p, in float n) { // 2D polar repetition
    float rep = 6.28 / n, hRep = 0.5 * rep;
    p = sin(mod(atan(p.y, p.x) + hRep, rep) - hRep + vec2(1.57, 0.0)) * length(p);
    return p;
}

// 2 link IK solver (positions only)
// https://www.shadertoy.com/view/NsfGRf
// config sets the handedness, -1 for left, 1 for right
vec2 solveJoint2D(in vec2 a, in vec2 b, in float ra, in float rb, in float config) {
    vec2 ba = b - a;
    float d = dot(ba, ba), l = sqrt(d);
    float q = (d + ra * ra - rb * rb) / (2.0 * ra * l);
    return a + (ba * q + vec2(-ba.y, ba.x) * sqrt(1.0 - q * q) * config) * ra / l;
}

vec3 solveJoint3D(in vec3 a, in vec3 b, in float ra, in float rb, in float config) {
    b -= a;
    float u = length(b.xz);
    vec3 j = vec3(solveJoint2D(vec2(0.0), vec2(u, b.y), ra, rb, config), 0.0);
    return a + vec3(b.xz / u * j.x, j.y).xzy;
}

// Quadratic bezier
vec3 bezierArc(in vec3 a, in vec3 b, in vec3 c, in float t) {
    float tInv = 1.0 - t;
    return a * tInv * tInv + b * 2.0 * t * tInv + c * t * t;
}

// Computes a coordinate system "looking" at a target along the z axis
// I think it is actually inverted
// https://www.shadertoy.com/view/WlKBDw
mat3 lookAt(in vec3 pos, in vec3 target) {
    vec3 f = normalize(target - pos);         // Forward
    vec3 r = normalize(vec3(-f.z, 0.0, f.x)); // Right
    vec3 u = cross(r, f);                     // Up
    return mat3(r, u, f);
}

void addShape(in float dist, in int id, inout vec2 scene) {
    if (dist < scene.x) scene = vec2(dist, id);
}

void subShape(in float dist, in int id, inout vec2 scene) {
    dist = -dist;
    if (dist > scene.x) scene = vec2(dist, id);
}

