    //addShape(sdSphere(p, arcV1, 0.3), 7, scene);
    //addShape(sdSphere(p, arcV2, 0.3), 7, scene);
    //addShape(sdSphere(p, arcV3, 0.3), 7, scene);
    //addShape(sdBezier(p, arcV1, arcV2, arcV3) - 0.1, 7, scene);

    vec3 base = vec3(0.0, 0.0, 1.5);
    vec3 end = bezierArc(arcV1, arcV2, arcV3, 0.5 + 0.5 * cos(iTime));//vec3(cos(iTime), 2.0 + sin(iTime), 3.0);
    vec3 middle = solveJoint3D(base, end, 3.0, 2.0, 1.0);

    float c = cos(bodyAngle), s = sin(bodyAngle);
    p.xz *= mat2(c, s, -s, c);

    // Body
    addShape(sdBox(p, vec3(0.0, -0.5, 0.0), vec3(1.5, 0.5, 2.5)), 2, scene);

    // Bolts
    vec3 wp = vec3(abs(p.xz) - vec2(1.9, 1.6), p.y + 1.1).xzy;
    addShape(sdBox(wp, vec3(-1.25, -0.1, 0.05), vec3(0.2, 0.1, 0.15)) - 0.1, 3, scene);

    // Wheels
    wheelAngle *= -sign(p.z);
    c = cos(wheelAngle), s = sin(wheelAngle);
    wp.yz *= mat2(c, -s, s, c);

    vec2 wheels = vec2(sdDisc(wp.yxz, 0.6) - 0.35, 4);
    addShape(sdCylinder(vec3(p.y + 1.15, p.x, abs(p.z) - 1.65), 2.0, 0.15), 5, wheels);

    // Hubcaps
    addShape(sdDisc(wp.yxz - vec3(0.0, 0.35, 0.0), 0.25) - 0.1, 5, scene);

    // Treads
    float turn = wp.x * sign(p.x * p.z);
    c = cos(turn), s = sin(turn);
    wp.yz *= mat2(c, -s, s, c);
    wp.yz = pModPolar2(wp.yz, 24.0);
    subShape(sdLine(wp, vec3(-0.25, 1.0, 0.0), vec3(1.0, 1.0, 0.0)) - 0.1, 4, wheels);
    addShape(wheels.x, int(wheels.y), scene);

    // Links
    addShape(min(sdLine(p, base, middle), sdLine(p, middle, end)) - 0.25, 6, scene);

    // Joints
    addShape(sdSphere(p, base, 0.35), 7, scene);
    addShape(sdSphere(p, middle, 0.35), 7, scene);

    // Gripper
    vec3 gp = (p - end) * lookAt(end, middle);
    gp = vec3(gp.x, -gp.z - 0.5, gp.y);
    addShape(sdHorseshoe(gp, mix(-0.2, 0.5, 0.5 + 0.5 * cos(iTime * 2.0)), vec3(0.1, 0.175, 0.2), 0.35), 8, scene);

    return scene;
}

// Tetrahedral central differences method (2 less mapScene() evaluations) plus a
// compiler trick: https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 getNormal(in vec3 p, in float t) {
    float h = 0.005 * t;
    vec3 n = vec3(0.0);
    for (int i=ZERO; i < 4; i++) {
        vec3 e = 0.5773 * (2.0 * vec3((((i + 3) >> 1) & 1), ((i >> 1) & 1), (i & 1)) - 1.0);
        n += e * mapScene(p + e * h, t).x;
    }

    return normalize(n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 8.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    // Rotate with mouse
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 3.14;
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(0.6, -0.1) * 3.14;

    float cy = cos(mouse.x), sy = sin(mouse.x);
    float cp = cos(mouse.y), sp = sin(mouse.y);

    ro.yz *= mat2(cp, -sp, sp, cp);
    ro.xz *= mat2(cy, -sy, sy, cy);
    rd.yz *= mat2(cp, -sp, sp, cp);
    rd.xz *= mat2(cy, -sy, sy, cy);

    // Raymarch
    bool hit = false;
    float t = 0.0;

    vec3 p;
    int id;

    //float iters = 0.0;
    for (int i=ZERO; i < 100; i++) {
        p = ro + rd * t;
        vec2 d = mapScene(p, t);
        if (d.x < 0.001 * t) {
            id = int(d.y);
            hit = true;
            break;
        }

        if (t > 100.0) {
            break;
        }

        t += d.x;
        //iters++;
    }

    if (hit) {
        vec3 n = getNormal(p, t);
        float diff = sqrt(max(0.0, dot(-rd, n)));

        vec3 mat = vec3(1.0);
        if (id == 1) mat = vec3(0.8, 0.6, 0.4); // Terrain
        if (id == 2) mat = vec3(0.5); // Body
        if (id == 3) mat = vec3(0.5); // Bolts
        if (id == 4) mat = vec3(0.25); // Wheels
        if (id == 5) mat = vec3(0.75); // Shaft and hubcaps
        if (id == 6) mat = vec3(0.75); // Arm

        fragColor.rgb = mat * diff;
    }

    // Iteration heatmap
    //fragColor.rgb = mix(vec3(0.0, 1.0, 0.0), vec3(1.0, 0.0, 0.0), iters / 100.0);
}

#define pi acos(-1.)
#define ss(a,b,c) smoothstep(a-b,a+b,c)
