vec2 frame(vec2 v){
    v=2.*v.xy/res.xy-1.;
    v.x*=res.x/res.y;return v;
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec2 i = vec2(0.0,1.0);

vec2 header(vec2 z) {
    z.y -= z.x;
    return z;
}

vec2 footer(vec2 r) {
    r.y += r.x;
    return r;
}

vec2 cmul(vec2 z, vec2 w) {
    vec2 r = vec2(
        z.x*w.x - z.y*w.y, 
        z.x*w.y + z.y*w.x
    );
    return r;
}

vec2 pol(vec2 z) {
    float size = sign(z.x) * length(z);
    float angle = angle(z);
    return vec2(length(z), angle);
}

vec2 cart(vec2 pol) {
    return pol.x * vec2(cos(pol.y), sin(pol.y));
}

vec2 cpow(vec2 z, vec2 w) {
    vec2 p1 = pol(z);
    vec2 p = vec2(
        pow(p1.x, w.x) * exp(-w.y*p1.y), 
        p1.y * w.x + log(p1.x) * w.y);
    vec2 r = cart(p);
    return r;
}

vec2 cpow(vec2 z, float w) {
    return cpow(z, vec2(w,0.0));
}

vec2 cpow(vec2 z, int w) {
    return cpow(z, float(w));
}

vec2 cmix(vec2 z, vec2 w, float t) {
    //z = cart(z);
    //w = cart(w);

    vec2 r = mix(z,w,t);
    //r = pol(r);
    return r;
}

vec2 cln(vec2 z) {
    vec2 p = pol(z);
    vec2 r = vec2(log(p.x), p.y);
    return r;
}

vec2 cexp(vec2 z) {
    vec2 r = cart(vec2(exp(z.x), z.y));
    return r;
}

float reduce(float x) {
    return 1.0 - pow(1.01, -x);
}

vec3 hsv_from_complex(vec2 p) {
    float size = length(p);
    float p_grid = length(fract(p));
    float radial = fract(log2(length(p)));
    float angle = angle(p)/pi;
    float angle_grid = 0.0 * fract(8.0*angle*floor(size));
    float hue = 0.91 + 0.5 * angle;
    float saturation = 0.4 + 0.2 * reduce(length(p));
    float value = 0.4 + 0.1 * p_grid + 0.1 * angle_grid + 0.1 * radial;
    vec3 hsv = vec3(hue, saturation, value);
    return hsv;
}

vec4 projection(vec2 p) {
    float othogonal = length(fract(p));
    vec3 hsv = hsv_from_complex(p);
    float transparency=0.2*othogonal;
    return vec4(hsv2rgb(hsv), transparency);
}

vec2 mapping(vec2 z) {
    vec2 r = cexp(cln(z) + cln(z));
    return r; //z + cpow(z,5) + vec2(1.0,0.0);
}

void mainImage(out vec4 r,in vec2 i){
    i-=iMouse.xy - res.xy * .5;
    vec2 z=frame(i)*zoom;
    float orthogonal = length(fract(z));
    vec2 p = mapping(z);
    p = cmix(z, p, st);
    vec3 p_hsv = hsv_from_complex(p);
    vec3 z_hsv = hsv_from_complex(z);
    vec3 hsv = p_hsv;
    hsv.x = p_hsv.x + 0.005 * fract(z_hsv.x * pow(2.0, 2.0 + floor(length(z))));
    hsv.z = pow(2.0, mix(log2(p_hsv.z), log2(z_hsv.z), 0.4));

    r = vec4(hsv2rgb(hsv), 1.0);
}

