const int idWhl = 1, idSpk = 2, idCrnk = 3, idAx = 4, idPis = 5, idCrod = 6, idCyl = 7,
   idCylEnt = 8, idValv = 9, idPipes = 10, idSup = 11, idBase = 12, idPen = 13, idFrm = 14;
