#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }
#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float CrnkDf (vec3 p, float dMin)
{
  vec3 q;
  float d, dz;
  for (int k = 0; k < 3; k ++) {
    dz = float (1 - k) * 4.;
    q = p;
    q.xy = Rot2Cs (q.xy, aCs[k]);
    q.z -= dz;
    d = min (PrRoundBoxDf (vec3 (q.x + 0.5 * crRad, q.y, abs (q.z) - 0.5),
       vec3 (0.5 * crRad, 0.2, 0.1), 0.02), PrRoundCylDf (vec3 (abs (q.x + 0.5 * crRad) -
       0.5 * crRad, q.y, abs (q.z) - 0.5), 0.6, 0.02, 0.1));
    DMIN (idCrnk);
    d = PrRoundCylDf (vec3 (q.x + crRad, q.yz), 0.3, 0.02, 0.65);
    DMIN (idAx);
    q = p;
    q.z -= dz;
    q.xy = Rot2Cs (q.xy + crMid[k], crCs[k]);
    d = min (PrCylDf (vec3 (abs (q.y) - 0.12, q.zx), 0.15, crLen - 0.5),
       PrCylDf (vec3 (abs (q.x) - crLen, q.yz), 0.6, 0.15));
    DMIN (idCrod);
    q = p;
    q.xz -= vec2 (- (4.5 + crMid[k].x + crLen * crCs[k].x), dz);
    d = PrCylDf (q.yzx, 0.25, 3.7);
    DMIN (idPis);
    d = PrCylDf ((q - vec3 (0.7, 1.7, 0.)).yzx, 0.07, 3.);
    DMIN (idPis);
    q.x -= 4.5;
    d = PrRoundCylDf (q, 0.3, 0.02, 0.5);
    DMIN (idAx);
    d = min (min (PrCylDf ((q - vec3 (-0.8, 0., 0.)).yzx, 0.6, 0.13),
       PrCylDf ((q - vec3 (-0.8, 0.8, 0.)).xzy, 0.08, 0.95)),
       PrRoundCylDf (vec3 (q.xy, abs (q.z) - 0.35), 0.7, 0.02, 0.1));
    DMIN (idCrnk);
  }
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, dz;
  dMin = dstFar / szFac;
  p /= szFac;
  p.x -= 12.;
  q = p;
  if (! isSh) d = PrCapsDf (q.yzx, 10., 7.);
  if (isSh || d < 0.2) {
    p.x -= 8.;
    q = p;
    d = max (PrCylDf (q, 0.3, 8.2), min (0.35 - abs (mod (q.z + 2., 4.) - 2.), 6. - abs (q.z)));
    DMIN (idAx);
    q.xy = Rot2Cs (q.xy, aCs[0]);
    q.z = abs (q.z) - 7.3;
    d = min (SmoothMax (abs (length (q.xy) - 4.) - 0.2, abs (q.z) - 0.8, 0.04),
       PrRoundCylDf (q, 0.6, 0.02, 0.8));
    DMINQ (idWhl);
    q.xy = Rot2D (q.xy, 2. * pi * (floor (6. * atan (q.y, - q.x) / (2. * pi) + 0.5)) / 6.);
    d = PrRoundBoxDf (vec3 (q.x + 2.2, q.y, abs (q.z) - 0.5), vec3 (1.7, 0.15, 0.25), 0.04);
    DMIN (idSpk);
    dMin = CrnkDf (p, dMin);
    q = p ;
    q.z = mod (q.z + 2., 4.);
    q.xz -= vec2 (-16.9, 2.);
    d = max (PrCylDf ((q + vec3 (-3.5, 0., 0.)).yzx, 0.7, 0.2), abs (p.z) - 6.);
    DMINQ (idCylEnt);
    d = max (PrRoundCylDf (q.yzx, 1.5, 0.02, 3.5), abs (p.z) - 6.);
    DMINQ (idCyl);
    q = p;
    q.z = mod (q.z + 2., 4.);
    q -= vec3 (-16.9, 1.7, 2.);
    d = max (PrRoundCylDf (q.yzx, 0.5, 0.02, 2.5), abs (p.z) - 6.);
    DMINQ (idValv);
    q = p;
    q.x = abs (q.x + 16.9);
    q.xy -= vec2 (1.5, 2.7);
    d = min (min (max (PrCylDf ((vec3 (q.x, q.y, mod (q.z + 2., 4.) - 2.)).xzy, 0.35, 0.75),
       abs (p.z) - 6.), PrCylDf (vec3 (q.x, q.y - 0.75, q.z), 0.35, 4.)),
       PrSphDf (vec3 (q.x, q.y - 0.75, abs (q.z) - 4.), 0.35));
    q = p;
    q.xy -= vec2 (-16.9, 1.);
    d = min (d, min (PrCylDf ((q + vec3 (-1.5, 0., 2.)).xzy, 0.35, 2.5),
       PrCylDf ((q + vec3 (1.5, 0., -2.)).xzy, 0.35, 2.5)));
    DMINQ (idPipes);
    q = p;
    q.z = abs (abs (q.z) - 4.) - 1.7;
    d = min (PrRoundBoxDf (q + vec3 (0., 1.6, 0.), vec3 (0.48, 1.5, 0.2), 0.02),
       PrRoundCylDf (q, 0.5, 0.02, 0.25));
    DMINQ (idSup);
    q = p;
    q.yz -= vec2 (1.8, -2.3);
    if (! isSh) d = PrSphDf (q, 1.);
    if (isSh || d < 0.1) {
      d = PrCylDf ((q + vec3 (0., 0.6, 0.)).xzy, 0.12, 0.8);
      q.xz = Rot2D (q.xz, 4. * aRot);
      q.xz = Rot2D (q.xz, 2. * pi * (floor (4. * atan (q.z, - q.x) / (2. * pi) + 0.5)) / 4.);
      q.xy = Rot2D (q.xy, -0.25 * pi);
      d = min (d, PrCylDf ((q + vec3 (0.4, -0.1, 0.)).yzx, 0.05, 0.4));
      DMIN (idAx);
      d = PrSphDf (q + vec3 (0.7, -0.1, 0.), 0.15);
      DMIN (idPis);
    } else dMin = min (dMin, d);
    q = p;
    q.xy -= vec2 (-8., -4.);
    d = min (PrRoundBoxDf (q, vec3 (13., 1., 6.), 0.04),
       PrRoundBoxDf (vec3 (abs (q.x + 9.) - 2., q.y - 1.5, q.z), vec3 (1., 1.4, 5.), 0.04));
    DMINQ (idBase);
    dMin *= szFac;
    dMiss = min (dMiss, dMin);
  } else dMin = min (dMin, d) * szFac;
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 ObjNfL (vec3 p)
{
  vec4 v;
  vec2 e;
  float s;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  s = dot (v, vec4 (1.));
  return vec4 (normalize (2. * v.yzw - s), (s - 2. * v.x - 4. * ObjDf (p)) / (e.x * e.x));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ExObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  q -= vec3 (2.5, -2.2, -6.8);
  q.xz = Rot2D (q.xz, 0.2 * pi);
  q.yz = Rot2D (vec2 (q.y, q.z + 4.5), 0.035 * pi + aPen) - vec2 (0., 4.5);
  d = PrRoundCylDf (q, 0.33, 0.02, 5.);
  d = max (d, - dot (vec2 (q.z, dot (q.xy, cos (2. * pi * floor (16. * (atan (q.y, - q.x) / (2. * pi) +
     0.5) + 0.5) / 16. + vec2 (0., 0.5 * pi)))), sin (-0.4 * pi + vec2 (0.5 * pi, 0.))) - 1.6);
  DMINQ (idPen);
  q = p;
  q.yz -= vec2 (-2.6, -4.);
  d = max (PrRoundBoxDf (q, vec3 (pgSize + vec3 (1., 0.1, 1.)), 0.03), - PrBox2Df (q.xz, pgSize.xz));
  DMINQ (idFrm);
  return dMin;
}

