vec3 ShGrid (vec2 p, vec2 g, int stag)
{
  vec2 q, sq, ss;
  q = p * g;
  if (stag > 0 && 2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.xy, 1. - 0.1 * sq.x * sq.y).xzy;
}

vec2 ExBlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, fcBlk;
  vec2 qBlk;
  float dn, df, bSize;
  bSize = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk)) / bSize;
  } else qBlk = vec2 (0.);
  return qBlk;
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fcBlk;
  vec2 qBlk;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return vec4 (dMin, fcBlk);
}

vec4 ObjCol (inout vec3 vn)
{
  vec4 col4;
  vec3 rg;
  float a, s;
  bool fxz;
  if (idObj == idBase) {
    col4 = vec4 (0.7, 0.8, 0.4, 0.1);
    if (abs (vn.y) < 0.01) {
      rg = qHit;
      rg.y += 0.5;
      fxz = (abs (vn.x) > 0.99);
      rg = ShGrid ((fxz ? rg.zy : rg.xy), vec2 (1., 2.), 1);
      col4 *= rg.y;
      rg.xz *= sign (fxz ? vn.x : vn.z);
      if (fxz) {
        if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
        else vn.xz = Rot2D (vn.xz, rg.x);
      } else {
        if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
        else vn.zx = Rot2D (vn.zx, rg.x);
      }
    } else {
      rg = ShGrid (qHit.xz, vec2 (1.), 0);
      col4 *= rg.y;
      if (vn.y > 0.99) {
        if (rg.x == 0.) vn.yz = Rot2D (vn.yz, rg.z);
        else vn.yx = Rot2D (vn.yx, rg.x);
      }
    }
  } else if (idObj == idCyl) {
    col4 = vec4 (0.8, 0.5, 0.2, 0.2);
    a = atan (qHit.z, - qHit.y) / (2. * pi);
    if (abs (vn.x) > 0.99) {
      col4.rgb *= (1. - 0.5 * SmoothBump (0.2, 0.4, 0.01, mod (16. * a + 0.5, 1.)) *
         SmoothBump (0.05, 0.13, 0.01, 1. - length (qHit.yz) / 1.5));
    } else {
      col4.rgb *= (1. - 0.5 * SmoothBump (0.03, 0.06, 0.01, 1. - abs (qHit.x) / 3.5));
      a = mod (32. * a, 1.);
      if (abs (qHit.x) < 3.3) vn.yz = Rot2D (vn.yz, 0.4 * SmoothBump (0.25, 0.75, 0.2, a) *
         sign (a - 0.5));
    }
  } else if (idObj == idWhl) {
    col4 = vec4 (0.8, 0.8, 0.85, 0.1);
    if (abs (vn.z) < 0.01) {
      s = length (qHit.xy);
      qHit.xy = vec2 (8. * atan (qHit.x, - qHit.y) / pi, qHit.z);
      if (s > 4.1) {
        s = mod (4. * qHit.z, 1.);
        vn.z = -0.2 * SmoothBump (0.25, 0.75, 0.15, s) * sign (s - 0.5) * sign (qHit.z);
        vn = normalize (vn);
      }
    }
  } else if (idObj == idSpk) {
    col4 = 1.1 * vec4 (0.8, 0.8, 0.85, 0.1);
  } else if (idObj == idCrnk) {
    col4 = vec4 (0.7, 0.7, 0.8, 0.2);
  } else if (idObj == idAx) {
    col4 = vec4 (0.8, 0.6, 0.2, 0.3);
  } else if (idObj == idPis) {
    col4 = vec4 (0.7, 0.7, 0.4, 0.3);
  } else if (idObj == idCrod) {
    col4 = vec4 (0.7, 0.75, 0.7, 0.3);
  } else if (idObj == idCylEnt) {
    col4 = vec4 (0.85, 0.5, 0.3, 0.2) * (0.5 + 0.5 * step (0.33, length (qHit.yz)));
  } else if (idObj == idValv) {
    col4 = vec4 (0.8, 0.5, 0.2, 0.2) * mix (1., 0.5 + 0.5 * step (0.13, length (qHit.yz)),
       step (0., vn.x));
  } else if (idObj == idPipes) {
    col4 = vec4 (0.8, 0.5, 0.2, 0.2);
  } else if (idObj == idSup) {
    col4 = vec4 (0.8, 0.9, 0.3, 0.05);
  }
  return col4;
}

void SetConf ()
{
  aRot = -0.4 * 2. * pi * tCur;
  aCs[0] = vec2 (cos (aRot), sin (aRot));
  aCs[1] = vec2 (cos (aRot + pi * 2./3.), sin (aRot + pi * 2./3.));
  aCs[2] = vec2 (cos (aRot + pi * 4./3.), sin (aRot + pi * 4./3.));
  crRad = 2.;
  crLen = 5.;
  for (int k = 0; k < 3; k ++) {
    crMid[k].y = -0.5 * crRad * aCs[k].y;
    crCs[k] = vec2 (cos (asin (crMid[k].y / crLen)), crMid[k].y / crLen);
    crMid[k].x = crLen * crCs[k].x + crRad * aCs[k].x;
  }
  aPen = 0.015 * pi * max (sin (pi * tCur), 0.);
}

vec3 SkShowScene (vec3 ro, vec3 rd, vec2 uv)
{
  vec4 col4, vn4;
  vec3 col, vn;
  float dstObj;
  dMiss = dstFar;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn4 = ObjNfL (ro);
    vn = vn4.xyz;
    col4 = ObjCol (vn);
    col = vec3 (1., 0.98, 0.96) * pow (Maxv3 (col4.rgb), 0.7);
    col *= (1. - 0.5 * smoothstep (0.5, 1., abs (vn4.w) / 64.)) *
       (0.9 + 0.2 * Noisefv2 (Rot2D (uv, 0.2 * pi) * vec2 (16., 256.))) *
       (0.5 + 0.5 * max (dot (vn, ltDirS), 0.));
    dMiss = dstFar;
  } else {
    col = vec3 (1.);
  }
  col = mix (col, vec3 (0.3), exp (- 64. * dMiss));
  return clamp (col, 0., 1.);
}

vec3 SkMain (vec2 fCoord, float vp)
{
  mat3 vuMat;
  vec3 ro, rd, col;
  vec2 uv;
  float el, az, szFacT;
  szFacT = szFac;
  uv = fCoord / vec2 (5., 5.5);
  if (vp == 0.) {
    szFac = 0.38;
    uv -= vec2 (-0.9, -1.1);
    el = -0.3 * pi;
    az = 0.1 * pi;
  } else if (vp == 1.) {
    szFac = 0.32;
    uv -= vec2 (-0.4, -0.7);
    el = -0.13 * pi;
    az = -0.4 * pi;
  } else if (vp == 2.) {
    szFac = 0.4;
    uv -= vec2 (0.3, -1.4);
    el = -0.25 * pi;
    az = 0.7 * pi;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -5., -20.);
  rd = vuMat * normalize (vec3 (uv, 4.2));
  ltDirS = vuMat * normalize (vec3 (0.7, 1., -0.7));
  col = SkShowScene (ro, rd, uv);
  szFac = szFacT;
  return clamp (col, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, db4;
  vec3 col, vn, qHitEx;
  vec2 qBlk, q, sq;
  float dstObj, dstExObj, sh, r;
  int idObjEx;
  bool isBg;
  SetConf ();
  szFac = 0.5;
  pgSize = vec3 (15., 0.1, 10.);
  isBg = false;
  isSh = false;
  dstExObj = ExObjRay (ro, rd);
  idObjEx = idObj;
  qHitEx = qHit;
  dstObj = ObjRay (ro, rd);
  db4 = BlkHit (ro - vec3 (0., -2.6, -4.), rd, pgSize);
  if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
    if (dstObj < dstExObj) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      qBlk = mod (2. * ExBlkHit (ro, reflect (rd, vn)), 1.);
      col4 = ObjCol (vn);
    } else {
      ro += dstExObj * rd;
      vn = ExObjNf (ro);
      if (idObjEx == idPen) {
        r = length (qHit.xy);
        col4 = mix (vec4 (0.9, 0.7, 0.1, 0.), vec4 (1., 0.9, 0.1, 0.01), step (0.34, r));
        col4 = mix (vec4 (0.3, 0.3, 0.3, 0.), col4, step (0.18, r));
        col4 = mix (vec4 (0.8, 0.4, 0.1, 0.2), col4, step (qHitEx.z, 4.5));
      } else if (idObjEx == idFrm) {
        col4 = vec4 (0.4, 0.5, 0.6, 0.) * (0.9 + 0.1 * Noisefv2 (32. * qHit.xz));
      }
    }
  } else if (db4.x < dstFar) {
    ro += db4.x * rd;
    vn = db4.yzw;
    q = ro.xz - vec2 (0., -4.);
    if (vn.y > 0.5 && PrBox2Df (q, pgSize.xz - 0.96) < 0.) {
      sq = sign (q);
      col = (sq.x < 0. || sq.y < 0.) ? SkMain (q - 0.5 * sq * pgSize.xz,
         sq.y + 1. + 0.5 * (sq.x + 1.)) : vec3 (1.);
      if (Minv3 (col) > 0.95) {
        q = smoothstep (0.02, 0.05, abs (mod (q + 0.5, 1.) - 0.5));
        col = mix (vec3 (0.6, 0.8, 0.9), vec3 (1.), min (q.x, q.y));
      }
    } else {
      col = vec3 (1.) * (0.8 + 0.2 * step (0.5, vn.y));
      sh = 1.;
    }
  } else {
    isBg = true;
  }
  if (! isBg) {
    isSh = true;
    sh = min (ObjSShadow (ro + 0.01 * vn, ltDir), ExObjSShadow (ro + 0.01 * vn, ltDir));
    if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
      col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
      col += col4.a * vec3 (0.5) * (0.5 + 0.5 * SmoothBump (0.25, 0.75, 0.05, qBlk.x) *
         SmoothBump (0.25, 0.75, 0.05, qBlk.y));
    } else {
      col *= 0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.);
    }
  } else {
    qBlk = mod (8. * ExBlkHit (ro, rd), 1.);
    col = vec3 (0.4, 0.5, 0.4) * (0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.)) +
       vec3 (0.2) * (0.9 + 0.1 * SmoothBump (0.25, 0.75, 0.1, qBlk.x) *
       SmoothBump (0.25, 0.75, 0.1, qBlk.y));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.28 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.8 * pi * mPtr.y;
  }
  el = clamp (el, -0.5 * pi, -0.1 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -4.8, -40.);
  rd = vuMat * normalize (vec3 (uv, 4.));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (0.7, 1., -0.7));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

#define MAX_STEPS 32
#define MAX_DIST 100.
#define SURF_DIST .001
#define Light_pos vec3(5.,5.,9.)
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}
vec3 spectrum(float n) {
    return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.1,0.5,0.9) );
}
mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );
float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p )
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p );
    return f;
}
float smin(float a,float b,float h){
    float c=clamp((b-a)/h*0.5+0.5,0.,1.);
    //return c;
    return mix(b,a,c)-h*c*(1.0-c);
}

mat2 rot(float a){
   float c=sin(a);
   float s=cos(a);
    return mat2(c,s,-s,c);

}
float GetDist(vec3 p){
   return 1.9*(max(0.,fbm(p*.3)-.35+.05*abs(sin(iTime))));
}
float rayMarch(vec3 ro,vec3 rd){
    float d0=0.;
    float T=1.;
    for(int i=0;i<MAX_STEPS;i++){
        vec3 p=ro+rd*d0;
        float ds=GetDist(p)/32.;
        T*=(1.-ds);
        d0+=50.*ds*T;
        if(T<.001)break;
        //if(d0>MAX_DIST||ds<SURF_DIST)break;
    }
    return d0;
}
vec3 getNormal(vec3 p){
    float l= GetDist(p);
    vec2 cau=vec2(.01,0.);
    vec3 n=normalize(vec3(l-GetDist(p-cau.xyy),
                          l- GetDist(p-cau.yxy),
                          l- GetDist(p-cau.yyx)
                           ));
    return n;

}
float  getLight(vec3 p){
    vec3 ld=Light_pos;
    vec3 li=normalize(ld-p);
    vec3 sn=getNormal(p);
    float sh=rayMarch(p+sn*SURF_DIST*2.,li);
    float dif=clamp(dot(sn,li),0.,1.) ;
    return dif;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 r0=vec3(0.,0.,5.);
    vec3 rd=vec3(uv.x,uv.y-.2,1.);
    vec3 col = vec3(1.);
    col.xyz=vec3(1.)*rayMarch(r0,rd)*.09;
    float d= rayMarch(r0,rd);
    vec3 p=r0+rd*d;
    vec3 n=getNormal(p);
    float difu=getLight(p);
     float ndotv=max(0.,dot(normalize(rd),n));
    vec3 halfh=normalize(normalize(-rd)+normalize(Light_pos-p));
    float ndothalfh=max(0.,dot(halfh,n));
     col =mix(vec3(difu),spectrum(.4+ndothalfh),0.9);
    col+=vec3(pow(ndothalfh,25.));
    fragColor = vec4(col*difu,1.0);
}

