vec3 v[16];
ivec4 faces[6] = ivec4[](
    ivec4(0, 1, 2, 3), 
    ivec4(0, 1, 5, 4), 
    ivec4(1, 2, 6, 5), 
    ivec4(2, 3, 7, 6), 
    ivec4(3, 0, 4, 7), 
    ivec4(4, 5, 6, 7)
    );
struct Indices{
    int v[8];

};
Indices cube_indices[8];

vec3 cubecols[8] = vec3[](
    vec3(1.,1.,1.),
    vec3(1.,1.,0.),
    vec3(1.,0.,1.),
    vec3(0.,1.,1.),
    vec3(0.,0.,1.),
    vec3(0.,1.,0.),
    vec3(1.,0.,0.),
    vec3(0.,0.,0.)
);

float cross2d( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }



const int lut[4] = int[](1,2,0,1); //"left-upper triangle"??

// 0--b--3
// |\
// a c
// |  \
// 1    2
//
vec3 quadIntersect( in vec3 ro, in vec3 rd, in vec3 v0, in vec3 v1, in vec3 v2, in vec3 v3 )
{
    // lets make v0 the origin
    vec3 a = v1 - v0;
    vec3 b = v3 - v0;
    vec3 c = v2 - v0;
    vec3 p = ro - v0;//change basis

    // intersect plane
    vec3 nor = cross(a,b);//normal of plane (kinda. might not have the right magnitude, but we care about direction)
    float t = -dot(p,nor)/dot(rd,nor);
    if( t<0.0 ) return vec3(-1.0);

    // intersection point
    vec3 pos = p + t*rd;

    // bilinear interpolation: https://www.shadertoy.com/view/lsBSDm

    // select projection plane
    vec3 mor = abs(nor);
    int id = (mor.x>nor.y && mor.x>mor.z ) ? 0 : 
             ((mor.y>mor.z)                ? 1 : 
                                             2);
    int idu = lut[id  ];
    int idv = lut[id+1];

    // project to 2D
    vec2 kp = vec2( pos[idu], pos[idv] );
    vec2 ka = vec2( a[idu], a[idv] );
    vec2 kb = vec2( b[idu], b[idv] );
    vec2 kc = vec2( c[idu], c[idv] );

    // find barycentric coords of the quadrilateral
    vec2 kg = kc-kb-ka;

    float k0 = cross2d( kp, kb );
    float k2 = cross2d( kc-kb, ka );        // float k2 = cross2d( kg, ka );
    float k1 = cross2d( kp, kg ) - nor[id]; // float k1 = cross2d( kb, ka ) + cross2d( kp, kg );

    // if edges are parallel, this is a linear equation
	float u, v;
    if( abs(k2)<0.001 )
    {
		v = -k0/k1;
	    //u  = (kp.x*k1+ka.x*k0) / (kb.x*k1-kg.x*k0);
    }
	else
    {
        // otherwise, it's a quadratic
        float w = k1*k1 - 4.0*k0*k2;
        if( w<0.0 ) return vec3(-1.0);
        w = sqrt( w );

        float ik2 = 1.0/(2.0*k2);

        v = (-k1 - w)*ik2; 
        if( v<0.0 || v>1.0 ) 
            v = (-k1 + w)*ik2;
    }
    u = (kp.x - ka.x*v)/(kb.x + kg.x*v);
    if( u<0.0 || u>1.0 || v<0.0 || v>1.0) return vec3(-1.0);

    return vec3( t, u, v );
}




float iCapsule( in vec3 ro, in vec3 rd, in vec3 pa, in vec3 pb, in float r )
{
    vec3  ba = pb - pa;
    vec3  oa = ro - pa;

    float baba = dot(ba,ba);
    float bard = dot(ba,rd);
    float baoa = dot(ba,oa);
    float rdoa = dot(rd,oa);
    float oaoa = dot(oa,oa);

    float a = baba      - bard*bard;
    float b = baba*rdoa - baoa*bard;
    float c = baba*oaoa - baoa*baoa - r*r*baba;
    float h = b*b - a*c;
    if( h>=0.0 )
    {
        float t = (-b-sqrt(h))/a;

        float y = baoa + t*bard;

        // body
        if( y>0.0 && y<baba ) return t;

        // caps
        vec3 oc = (y<=0.0) ? oa : ro - pb;
        b = dot(rd,oc);
        c = dot(oc,oc) - r*r;
        h = b*b - c;
        if( h>0.0 )
        {
            return -b - sqrt(h);
        }
    }
    return -1.0;
}

vec3 nCapsule( in vec3 p, in vec3 a, in vec3 b, in float r )
{
    vec3 ba = b-a, pa = p-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return (pa - h*ba)/r;
}

vec3 dCapsule( in vec3 ro, in vec3 rd, vec3 pa, vec3 pb, float rad )
{
	vec3 ba = pb - pa;
	vec3 oa = ro - pa;

	float oad  = dot( oa, rd );
	float dba  = dot( rd, ba );
	float baba = dot( ba, ba );
	float oaba = dot( oa, ba );

	vec2 th = vec2( -oad*baba + dba*oaba, oaba - oad*dba ) / (baba - dba*dba);

	th.x = max(   th.x, 0.0 );
	th.y = clamp( th.y, 0.0, 1.0 );

	vec3 p = pa + ba*th.y;
	vec3 q = ro + rd*th.x;

	return vec3( length( p-q )-rad, th );
}



int objA, objB;
bool cubeHit[8];
bool poleHit;
const float rad = 0.07;

float intersect( in vec3 ro, in vec3 rd, in vec3 v[16] )
{
    cubeHit = bool[](false,false,false,false,false,false,false,false);
    poleHit=false;
    float tmp;

    float res = 1e10;



    for( int i=0; i<16; i++ ) // for each vertex
    for( int j=0; j< 4; j++ ) // connect it to its 4 neighbors
    {
        int a = i;
        int b = a ^ (1<<j); // change one bit/dimension
        if( a<b )          // skip edge if already visited
        {
            tmp = iCapsule( ro, rd, v[a], v[b], rad );
            if( tmp>0.0 && tmp<res )
            {
                poleHit=true;
                res = tmp; 
                objA = a; 
                objB = b;
            }
        }
    }


    for(int ci=0; ci<8; ci++){
        for(int fi; fi<6; fi++){
            ivec4 fvoffsets = faces[fi];
            Indices cube_verts = cube_indices[ci];
            vec3 v0 = v[cube_verts.v[fvoffsets.x]];
            vec3 v1 = v[cube_verts.v[fvoffsets.y]];
            vec3 v2 = v[cube_verts.v[fvoffsets.z]];
            vec3 v3 = v[cube_verts.v[fvoffsets.w]];
            tmp = quadIntersect(ro, rd, v0, v1, v2, v3).x;
            if(tmp > 0. && tmp<res){
                cubeHit[ci] = true;
                res=tmp;
            }

        //     int a = ci;
        //     int b = a^(1<<fi);
        //     if( a<b )          // skip edge if already visited
        // {
        //     tmp = iCapsule( ro, rd, v[a], v[b], rad );
        //     if( tmp>0.0 && tmp<res )
        //     {
        //         res = tmp; 
        //         objA = a; 
        //         objB = b;
        //     }
        // }

        }
    }


    return (res<1e9)?res:-1.0;
}

vec3 calcNormal( in vec3 pos, in vec3 v[16] )
{
    return nCapsule( pos, v[objA], v[objB], rad );
}

float softShadowCapsule( in vec3 ro, in vec3 rd, in vec3 a, in vec3 b, in float r )
{
    const float k = 16.0;
    vec3 t = dCapsule( ro, rd, a, b, r );
    return clamp( k*t.x/max(t.z,0.0001), 0.0, 1.0 );
}


// soft shadows
float calcShadow( in vec3 ro, in vec3 rd, in vec3 v[16] )
{
    float t = 1.0;

    for( int i=0; i<16; i++ ) // for each vertex
    for( int j=0; j< 4; j++ ) // connect it to its 4 neighbors
    {
        int a = i;
        int b = a ^ (1<<j); // change one bit/dimension
        if( a<b )           // skip edge if already visited
        {
            t = min( t, softShadowCapsule( ro, rd, v[a], v[b], rad ) );
        }
    }    

    return t;
}

float calcAO( in vec3 pos, in vec3 nor, in float seed, in vec3 v[16] )
{
    return 1.0;
}


vec3 render( in vec3 ro, in vec3 rd, in float seed, in vec3 v[16] )
{ 
    vec3 col = vec3(0.04) + 0.03*rd.y;

    float t = intersect(ro,rd,v);
    if( t>0.0 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, v );
        // material        
		col = vec3(0.4);

        // lighting
        if(poleHit){
        float occ = calcAO( pos, nor, seed, v )*(0.7+0.3*nor.y);
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
        vec3  hal = normalize( lig-rd );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );

        float sha = (dif>0.001) ? calcShadow( pos+0.02*nor, lig, v ) : 0.0;

		float spe = pow( clamp( dot(nor,hal), 0.0, 1.0 ),16.0)*dif*sha*
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.20,0.80,0.65)*vec3(sha,0.5*sha+0.5*sha*sha,sha*sha);
        lin += 0.70*amb*vec3(0.70,0.80,1.00)*occ;
        lin += 1.00*fre*vec3(1.20,1.10,1.00)*occ;
		col = col*lin;
		col += 15.00*spe*vec3(1.00,0.90,0.70);
        }
        for(int i = 0; i<8; i++){
            if(cubeHit[i]){
                col += cubecols[i]*(1./8.);
            }
        }
    }

	return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

mat2 rot(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c,-s,s,c);
}

vec3 transform( in vec4 p )
{
    p.xw *= rot(iTime*0.41);
    p.yw *= rot(iTime*0.23);
    p.xy *= rot(iTime*0.73);
    p.wz *= rot(iTime*0.37);

    // orthogonal projection
    #if PROJECTION==0
    return p.xyz;
    #else
    // perspective projection
	return 2.5*p.xyz/(3.0+p.w);
    #endif
}


