    v[13] = transform( vec4( 1, 1,-1, 1));
    v[14] = transform( vec4( 1, 1, 1,-1));
    v[15] = transform( vec4( 1, 1, 1, 1));


    // camera (static)
    vec3 ro = vec3( 4.5, 1.5, -10.0 );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
