    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 tot = vec3(0.0);
#if AA>1
	#define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        float seed =  gl_FragCoord.x + gl_FragCoord.y*131.1 + iTime + 17.1*float(m) + 37.4*float(n);
#else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        float seed =  gl_FragCoord.x + gl_FragCoord.y*131.1 + iTime;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd, seed, v );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif

    // cheap dither to remove banding from background
    tot += 0.5*sin(fragCoord.x)*sin(fragCoord.y)/256.0;

    fragColor = vec4( tot, 1.0 );
}

vec2 tile(vec2 st, float scale, float t){
    st *= scale;

    // Move direction
    float a = mod(st.y, 2.0) > 1.0 ? 1.0 : -1.0;
    // Offset
    st.x += a * t;

    return fract(st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv = tile(uv, 15.0, iTime);

    // Block dot
    float d = length(uv - vec2(0.5, 0.5));
    float pix = 22./iResolution.y;
    vec3 col = vec3(smoothstep(0.3, 0.3+pix, d));
    col += vec3(1.0, 0.0, 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

