  vec3 rp = ro;

  for(float i=0.;i<=128.;i++){
      vec2 d = sdf(rp);
      if(length(rp) > 100.)break;
      if(d.x <=.001){
          vec3 n= norm(rp);

          float diff = max(0.,dot(normalize(light-rp),n));
          float spec = pow(max(0.,dot(normalize(ro-rp),reflect(-normalize(light),n))),32.);
        float  fr=pow(1.+dot(n,rd),8.);
          col = vec3(.1)*diff+spec+2.*vec3(1.,.7,0.5)*fr;
          col = mix(vec3(.1),col,fr+AO(rp,n)/3.);

