float Tree(vec2 uv, int branches){
// to polar
vec2 p=vec2((atan(uv.x,uv.y)/TAU)*float(branches),length(uv));

//f
float Y,y,X;
 // the magic number hoha
  Y= exp2( floor(p.y));
  y=  .5+( pow(fract(p.y),.85 ))*.5+.5;
  X=abs(p.x* Y);
  p.x=        abs(fract(X-y*.5)-.5)*2.;
  p.x= max(p.x,abs(fract(X+ y*.5)-.5)*2. );
 // 
  float branchwidth=.1/(.25+p.y);
  float outerlimit =p.y-4.6;
  float scaleAdjusted= 1.5*(((1.-p.x)/Y)*p.y);
  return  smax(outerlimit, scaleAdjusted-branchwidth ,.2)* .5 ;
}


